import React from 'react';

const Button = ({ text = 'Click Me', variant = 'yellow', onClick }) => {
    // Map variant to fixed Tailwind classes
    const variants = {
        yellow: 'bg-yellow-400 text-black border-4 border-black hover:bg-yellow-600',
        green: 'bg-green-400 text-black border-4 border-black hover:bg-green-600',
        red: 'bg-red-400 text-black border-4 border-black hover:bg-red-600',
    };

    const classes = `neo-brutalist text-lg font-bold py-3 px-6 transition-none ${variants[variant] || variants.yellow}`;

    return (
        <button className={classes} onClick={onClick}>
            {text}
        </button>
    );
};

export default Button;
