import React, { useEffect, useRef } from "react";
import { useNavigate } from "react-router-dom";
import NeoBrutalistButton from "../component/NeoBrutalist/NeoBrutalistButton.js";
import "../styles/MainPage.css";

const MainPage = () => {
    const navigate = useNavigate();
    const unicornRef = useRef(null);

    useEffect(() => {
        // Load UnicornStudio SDK
        const script = document.createElement('script');
        script.src = 'https://cdn.unicornstudio.com/latest/unicornstudio.min.js';
        script.async = true;

        script.onload = () => {
            // Initialize UnicornStudio with custom scaling
            if (window.UnicornStudio && unicornRef.current) {
                window.UnicornStudio.addScene({
                    elementId: unicornRef.current.id,
                    projectId: '9LavQqV4nq9siFdVEwmk',
                    // Add scaling options
                    options: {
                        scale: 0.5, // Scale down to 50%
                        fit: 'cover', // or 'contain', 'fill'
                        position: 'center',
                        // Custom viewport settings
                        viewport: {
                            width: window.innerWidth,
                            height: window.innerHeight,
                            scale: 0.5
                        }
                    }
                }).then(scene => {
                    console.log('UnicornStudio scene loaded with scaling');

                    // Apply additional scaling if needed
                    if (scene.setScale) {
                        scene.setScale(0.5);
                    }

                    // Set custom camera position/zoom if available
                    if (scene.camera) {
                        scene.camera.setZoom(0.5);
                    }
                }).catch(error => {
                    console.error('Error loading UnicornStudio scene:', error);
                });
            }
        };

        document.head.appendChild(script);

        return () => {
            document.head.removeChild(script);
        };
    }, []);

    return (
        <div className="page-wrapper">
            {/* Background Unicorn scene using vanilla JS SDK */}
            <div
                id="unicorn-scene"
                ref={unicornRef}
                style={{
                    position: "absolute",
                    top: 0,
                    left: 0,
                    width: "100vw",
                    height: "100vh",
                    zIndex: 0,
                }}
            />

            {/* Foreground content */}
            <div className="main-container">
                <div className="main-box">
                    <h2 className="main-title">سامانه ثبت سفارش</h2>
                    <div className="button-group">
                        <NeoBrutalistButton
                            text="ورود"
                            className="login-btn"
                            color="yellow-400"
                            textColor="black"
                            onClick={() => navigate("/login")}
                        />
                        <NeoBrutalistButton
                            text="ثبت نام"
                            className="register-btn"
                            color="yellow-400"
                            textColor="black"
                            onClick={() => navigate("/register")}
                        />
                    </div>
                </div>
            </div>
        </div>
    );
};

export default MainPage;
