import React, { useState, useEffect } from "react";
import API from "../component/api";
import NeoBrutalistButton from "../component/NeoBrutalist/NeoBrutalistButton";
import NeoBrutalistInput from "../component/NeoBrutalist/NeoBrutalistInput";
import NeoBrutalistDropdown from "../component/NeoBrutalist/NeoBrutalistDropdown";
import NeoBrutalistCard from "../component/NeoBrutalist/NeoBrutalistCard";
import '../styles/NeoBrutalistCreateOrder.css';

const NeoBrutalistCreateOrderPage = ({ onOrderCreated }) => {
    const [products, setProducts] = useState([]);
    const [customerComment, setCustomerComment] = useState("");
    const [items, setItems] = useState([
        { product: "", requested_quantity: 1, customer_notes: "" },
    ]);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState("");

    useEffect(() => {
        API.get("products/")
            .then((res) => {
                setProducts(res.data);
            })
            .catch(() => setError("Failed to load products."));
    }, []);

    const handleItemChange = (idx, field, value) => {
        setItems((prev) =>
            prev.map((item, i) =>
                i === idx ? { ...item, [field]: value } : item
            )
        );
    };

    const addItem = () => {
        setItems((prev) => [
            ...prev,
            { product: "", requested_quantity: 1, customer_notes: "" },
        ]);
    };

    const removeItem = (idx) => {
        setItems((prev) => prev.filter((_, i) => i !== idx));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setLoading(true);
        setError("");

        if (!items.length) {
            setError("Order must contain at least one item.");
            setLoading(false);
            return;
        }

        // Validate that all items have products selected
        const invalidItems = items.filter(item => !item.product);
        if (invalidItems.length > 0) {
            setError("Please select a product for all items.");
            setLoading(false);
            return;
        }

        try {
            const payload = {
                customer_comment: customerComment,
                items: items.map((item) => ({
                    product: item.product,
                    requested_quantity: Number(item.requested_quantity),
                    customer_notes: item.customer_notes,
                })),
            };
            const res = await API.post("orders/", payload);

            setLoading(false);
            if (onOrderCreated) onOrderCreated(res.data);
            setCustomerComment("");
            setItems([{ product: "", requested_quantity: 1, customer_notes: "" }]);
        } catch (err) {
            setLoading(false);
            if (err.response) {
                setError(`Failed to create order: ${JSON.stringify(err.response.data)}`);
            } else {
                setError("Failed to create order. Please try again.");
            }
        }
    };

    const getUserInfo = () => {
        const userDataString = localStorage.getItem('userData');
        return userDataString ? JSON.parse(userDataString) : null;
    };

    // Transform products for dropdown
    const productOptions = products.map(product => ({
        value: product.id,
        label: product.name
    }));

    return (
        <div className="neo-create-order">
            <div className="neo-create-order-header">
                <h1 className="neo-create-order-title">Create New Order</h1>
                <div className="neo-customer-info">
                    <span className="neo-customer-label">Customer:</span>
                    <span className="neo-customer-name">{getUserInfo()?.name}</span>
                </div>
            </div>

            {error && (
                <NeoBrutalistCard className="neo-error-card">
                    <div className="neo-error-content">
                        <span className="neo-error-icon">⚠</span>
                        <span className="neo-error-text">{error}</span>
                    </div>
                </NeoBrutalistCard>
            )}

            <form onSubmit={handleSubmit} className="neo-order-form">
                <NeoBrutalistCard className="neo-comment-section">
                    <NeoBrutalistInput
                        type="textarea"
                        label="Customer Comment"
                        value={customerComment}
                        onChange={(e) => setCustomerComment(e.target.value)}
                        placeholder="Initial request/comments..."
                        rows={3}
                    />
                </NeoBrutalistCard>

                <div className="neo-items-section">
                    <div className="neo-section-header">
                        <h2 className="neo-section-title">Order Items</h2>
                        <NeoBrutalistButton
                            text="+ Add Item"
                            color="blue-400"
                            textColor="white"
                            onClick={addItem}
                            className="neo-add-item-btn"
                            type="button"
                        />
                    </div>

                    <div className="neo-items-list">
                        {items.map((item, idx) => (
                            <NeoBrutalistCard key={idx} className="neo-item-card">
                                <div className="neo-item-header">
                                    <h3 className="neo-item-number">Item #{idx + 1}</h3>
                                    {items.length > 1 && (
                                        <NeoBrutalistButton
                                            text="Remove"
                                            color="red-400"
                                            textColor="white"
                                            onClick={() => removeItem(idx)}
                                            className="neo-remove-btn"
                                            type="button"
                                        />
                                    )}
                                </div>

                                <div className="neo-item-fields">
                                    <div className="neo-field-group">
                                        <NeoBrutalistDropdown
                                            label="Product"
                                            options={productOptions}
                                            value={item.product}
                                            onChange={(value) => handleItemChange(idx, "product", value)}
                                            placeholder="Select a product..."
                                            required
                                        />
                                    </div>

                                    <div className="neo-field-group">
                                        <NeoBrutalistInput
                                            type="number"
                                            label="Quantity"
                                            value={item.requested_quantity}
                                            onChange={(e) => handleItemChange(idx, "requested_quantity", e.target.value)}
                                            min={1}
                                            required
                                        />
                                    </div>

                                    <div className="neo-field-group">
                                        <NeoBrutalistInput
                                            type="textarea"
                                            label="Item Notes"
                                            value={item.customer_notes}
                                            onChange={(e) => handleItemChange(idx, "customer_notes", e.target.value)}
                                            placeholder="Special requirements or notes for this item..."
                                            rows={2}
                                        />
                                    </div>
                                </div>
                            </NeoBrutalistCard>
                        ))}
                    </div>
                </div>

                <div className="neo-form-actions">
                    <NeoBrutalistButton
                        text={loading ? "Creating Order..." : "Create Order"}
                        color="yellow-400"
                        textColor="black"
                        type="submit"
                        disabled={loading}
                        className="neo-submit-btn"
                    />
                </div>
            </form>
        </div>
    );
};

export default NeoBrutalistCreateOrderPage;
