import React, { useState, useEffect, useRef } from 'react';
import API from './api';
import NeoBrutalistCard from './NeoBrutalist/NeoBrutalistCard';
import NeoBrutalistButton from './NeoBrutalist/NeoBrutalistButton';
import NeoBrutalistInput from './NeoBrutalist/NeoBrutalistInput';
import PaymentReceiptUploadModal from './PaymentReceiptUploadModal';
import '../styles/component/CustomerComponent/OrderDetail.css';

const OrderDetailPage = ({ orderId, onOrderUpdated }) => {
    const [order, setOrder] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');
    const [isEditing, setIsEditing] = useState(false);
    const [editedComment, setEditedComment] = useState('');
    const [approvalDecision, setApprovalDecision] = useState('');
    const [rejectionReason, setRejectionReason] = useState('');
    const [submitting, setSubmitting] = useState(false);
    const [generatingInvoice, setGeneratingInvoice] = useState(false);

    // Payment upload modal state
    const [isPaymentModalOpen, setIsPaymentModalOpen] = useState(false);

    // NEW: Payment receipts management
    const [paymentReceipts, setPaymentReceipts] = useState([]);
    const [loadingReceipts, setLoadingReceipts] = useState(false);
    const [receiptsError, setReceiptsError] = useState('');

    const tableRef = useRef(null);

    useEffect(() => {
        if (orderId) {
            fetchOrder();
        }
    }, [orderId]);

    useEffect(() => {
        if (order?.items && tableRef.current) {
            applySmartTextSizing();
        }
    }, [order]);

    // NEW: Fetch payment receipts when order loads
    useEffect(() => {
        if (order && (order.status === 'payment_uploaded' || order.has_payment_receipts || order.payment_receipts?.length > 0)) {
            fetchPaymentReceipts();
        }
    }, [order]);

    const fetchOrder = async () => {
        setLoading(true);
        setError('');
        try {
            const response = await API.get(`/orders/${orderId}/`);
            setOrder(response.data);
            setEditedComment(response.data.customer_comment || '');
        } catch (err) {
            console.error('‚ùå Error fetching order:', err);
            setError('ÿÆÿ∑ÿß ÿØÿ± ÿ®ÿßÿ±⁄Ø€åÿ±€å ÿ¨ÿ≤ÿ¶€åÿßÿ™ ÿ≥ŸÅÿßÿ±ÿ¥');
        } finally {
            setLoading(false);
        }
    };

    // NEW: Fetch payment receipts
    const fetchPaymentReceipts = async () => {
        setLoadingReceipts(true);
        setReceiptsError('');
        try {
            const response = await API.get(`/orders/${orderId}/payment-receipts/`);
            setPaymentReceipts(response.data.receipts || []);
        } catch (err) {
            console.error('‚ùå Error fetching payment receipts:', err);
            setReceiptsError('ÿÆÿ∑ÿß ÿØÿ± ÿ®ÿßÿ±⁄Ø€åÿ±€å ÿ±ÿ≥€åÿØŸáÿß€å Ÿæÿ±ÿØÿßÿÆÿ™');
        } finally {
            setLoadingReceipts(false);
        }
    };

    // NEW: Delete payment receipt
    const deletePaymentReceipt = async (receiptId) => {
        if (!window.confirm('ÿ¢€åÿß ÿßÿ≤ ÿ≠ÿ∞ŸÅ ÿß€åŸÜ ÿ±ÿ≥€åÿØ ÿßÿ∑ŸÖ€åŸÜÿßŸÜ ÿØÿßÿ±€åÿØÿü')) {
            return;
        }

        try {
            await API.delete(`/orders/${orderId}/delete-payment-receipt/${receiptId}/`);

            // Remove from local state
            setPaymentReceipts(prev => prev.filter(receipt => receipt.id !== receiptId));

            // Refresh order data
            fetchOrder();

            alert('ÿ±ÿ≥€åÿØ ÿ®ÿß ŸÖŸàŸÅŸÇ€åÿ™ ÿ≠ÿ∞ŸÅ ÿ¥ÿØ');
        } catch (err) {
            console.error('‚ùå Error deleting payment receipt:', err);
            alert('ÿÆÿ∑ÿß ÿØÿ± ÿ≠ÿ∞ŸÅ ÿ±ÿ≥€åÿØ Ÿæÿ±ÿØÿßÿÆÿ™');
        }
    };

    const applySmartTextSizing = () => {
        const productCells = tableRef.current?.querySelectorAll('.neo-table-cell:nth-child(1)');
        const notesCells = tableRef.current?.querySelectorAll('.neo-table-cell:nth-child(3)');

        productCells?.forEach(cell => {
            const textLength = cell.textContent.length;
            if (textLength > 30) {
                cell.style.fontSize = '0.7rem';
                cell.classList.add('text-overflow');
            } else if (textLength > 20) {
                cell.style.fontSize = '0.75rem';
            } else if (textLength > 15) {
                cell.style.fontSize = '0.8rem';
            }
        });

        notesCells?.forEach(cell => {
            const textLength = cell.textContent.length;
            if (textLength > 25) {
                cell.style.fontSize = '0.75rem';
                cell.classList.add('text-overflow');
            }
        });
    };

    const handleApprovalSubmit = async () => {
        if (!approvalDecision) {
            setError('ŸÑÿ∑ŸÅÿß ÿ™ÿß€å€åÿØ €åÿß ÿ±ÿØ ÿ±ÿß ÿßŸÜÿ™ÿÆÿßÿ® ⁄©ŸÜ€åÿØ');
            return;
        }

        if (approvalDecision === 'reject' && !rejectionReason.trim()) {
            setError('ŸÑÿ∑ŸÅÿß ÿØŸÑ€åŸÑ ÿ±ÿØ ÿ±ÿß ÿ∞⁄©ÿ± ⁄©ŸÜ€åÿØ');
            return;
        }

        setSubmitting(true);
        setError('');

        try {
            if (approvalDecision === 'approve') {
                await API.post(`/orders/${orderId}/approve/`);
            } else {
                await API.post(`/orders/${orderId}/reject/`, {
                    rejection_reason: rejectionReason
                });
            }

            if (onOrderUpdated) {
                onOrderUpdated();
            } else {
                fetchOrder();
            }
        } catch (err) {
            console.error('‚ùå Error submitting approval:', err);
            setError(err.response?.data?.error || 'ÿÆÿ∑ÿß ÿØÿ± ÿßÿ±ÿ≥ÿßŸÑ ÿ™ÿµŸÖ€åŸÖ');
            setGeneratingInvoice(false);
        } finally {
            setSubmitting(false);
        }
    };

    const downloadInvoice = async () => {
        try {
            if (!order.invoice_id) {
                setError('ŸÅÿß⁄©ÿ™Ÿàÿ± ŸáŸÜŸàÿ≤ ÿß€åÿ¨ÿßÿØ ŸÜÿ¥ÿØŸá ÿßÿ≥ÿ™');
                return;
            }

            const response = await API.get(`/invoices/${order.invoice_id}/download-pdf/`, {
                responseType: 'blob'
            });

            const blob = new Blob([response.data], { type: 'application/pdf' });
            const url = window.URL.createObjectURL(blob);
            const link = document.createElement('a');
            link.href = url;
            link.download = `invoice_${order.id}.pdf`;
            document.body.appendChild(link);
            link.click();
            link.remove();
            window.URL.revokeObjectURL(url);
        } catch (err) {
            console.error('‚ùå Error downloading invoice:', err);
            setError('ÿÆÿ∑ÿß ÿØÿ± ÿØÿßŸÜŸÑŸàÿØ ŸÅÿß⁄©ÿ™Ÿàÿ±');
        }
    };

    // UPDATED: Payment upload success handler
    const handlePaymentUploadSuccess = (response) => {
        console.log('‚úÖ Payment upload successful:', response);

        // Refresh order data to show updated payment status
        fetchOrder();

        // Refresh payment receipts
        fetchPaymentReceipts();

        // Close modal
        setIsPaymentModalOpen(false);

        // Clear any previous errors
        setError('');
    };

    const getStatusColor = (status) => {
        switch (status) {
            case 'pending_pricing':
                return 'yellow-400';
            case 'waiting_customer_approval':
                return 'blue-400';
            case 'confirmed':
                return 'green-400';
            case 'payment_uploaded':
                return 'purple-400';
            case 'completed':
                return 'green-600';
            case 'rejected':
                return 'red-400';
            case 'cancelled':
                return 'gray-400';
            default:
                return 'gray-400';
        }
    };

    const formatStatus = (status) => {
        const statusMap = {
            'pending_pricing': 'ÿØÿ± ÿßŸÜÿ™ÿ∏ÿßÿ± ŸÇ€åŸÖÿ™‚Äå⁄Øÿ∞ÿßÿ±€å',
            'waiting_customer_approval': 'ÿØÿ± ÿßŸÜÿ™ÿ∏ÿßÿ± ÿ™ÿ£€å€åÿØ',
            'confirmed': 'ÿ™ÿ£€å€åÿØ ÿ¥ÿØŸá Ÿà ŸÅÿß⁄©ÿ™Ÿàÿ± ÿµÿßÿØÿ± ÿ¥ÿØ',
            'payment_uploaded': 'ÿ±ÿ≥€åÿØ Ÿæÿ±ÿØÿßÿÆÿ™ ÿ¢ŸæŸÑŸàÿØ ÿ¥ÿØŸá',
            'completed': 'ÿ™⁄©ŸÖ€åŸÑ ÿ¥ÿØŸá',
            'rejected': 'ÿ±ÿØ ÿ¥ÿØŸá',
            'cancelled': 'ŸÑÿ∫Ÿà ÿ¥ÿØŸá'
        };
        return statusMap[status] || status;
    };

    const formatPrice = (price) => {
        if (!price || price === 0) return 'ÿØÿ± ÿßŸÜÿ™ÿ∏ÿßÿ±';
        const formattedNumber = new Intl.NumberFormat('fa-IR').format(price);
        return `${formattedNumber} ÿ±€åÿßŸÑ`;
    };

    const formatQuantity = (quantity) => {
        if (!quantity || quantity === 0) return 'ÿØÿ± ÿßŸÜÿ™ÿ∏ÿßÿ±';
        return new Intl.NumberFormat('fa-IR').format(quantity);
    };

    const calculateTotal = (unitPrice, quantity) => {
        if (!unitPrice || !quantity || unitPrice === 0 || quantity === 0) {
            return 'ÿØÿ± ÿßŸÜÿ™ÿ∏ÿßÿ±';
        }
        const total = unitPrice * quantity;
        const formattedTotal = new Intl.NumberFormat('fa-IR').format(total);
        return `${formattedTotal} ÿ±€åÿßŸÑ`;
    };

    const truncateText = (text, maxLength = 30) => {
        if (!text) return '-';
        if (text.length <= maxLength) return text;
        return text.substring(0, maxLength) + '...';
    };

    const formatFileSize = (bytes) => {
        if (bytes === 0) return '0 Bytes';
        const k = 1024;
        const sizes = ['Bytes', 'KB', 'MB', 'GB'];
        const i = Math.floor(Math.log(bytes) / Math.log(k));
        return parseFloat((bytes / Math.pow(k, i)).toFixed(2)) + ' ' + sizes[i];
    };

    if (loading) {
        return (
            <div className="neo-order-detail">
                <NeoBrutalistCard className="neo-loading-card">
                    <div className="neo-loading-content">
                        <span>üîÑ</span>
                        <span className="neo-loading-text">ÿØÿ± ÿ≠ÿßŸÑ ÿ®ÿßÿ±⁄Øÿ∞ÿßÿ±€å ÿ¨ÿ≤ÿ¶€åÿßÿ™ ÿ≥ŸÅÿßÿ±ÿ¥...</span>
                    </div>
                </NeoBrutalistCard>
            </div>
        );
    }

    if (error && !order) {
        return (
            <div className="neo-order-detail">
                <NeoBrutalistCard className="neo-error-card">
                    <div className="neo-error-content">
                        <span className="neo-error-icon">‚ö†Ô∏è</span>
                        <span className="neo-error-text">{error}</span>
                    </div>
                </NeoBrutalistCard>
            </div>
        );
    }

    if (!order) {
        return (
            <div className="neo-order-detail">
                <NeoBrutalistCard className="neo-error-card">
                    <div className="neo-error-content">
                        <span className="neo-error-icon">‚ùå</span>
                        <span className="neo-error-text">ÿ≥ŸÅÿßÿ±ÿ¥ Ÿæ€åÿØÿß ŸÜÿ¥ÿØ</span>
                    </div>
                </NeoBrutalistCard>
            </div>
        );
    }

    return (
        <div className="neo-order-detail" dir="rtl">
            {/* Header */}
            <div className="neo-order-header">
                <h1 className="neo-order-title">ÿ≥ŸÅÿßÿ±ÿ¥ #{order.id}</h1>
                <NeoBrutalistButton
                    text={formatStatus(order.status)}
                    color={getStatusColor(order.status)}
                    textColor="black"
                    className="neo-status-badge"
                />
            </div>

            {error && (
                <div className="neo-status-message neo-error">
                    <span className="neo-status-icon">‚ö†Ô∏è</span>
                    <span>{error}</span>
                </div>
            )}

            {/* Order Information */}
            <NeoBrutalistCard className="neo-order-info-card">
                <div className="neo-card-header">
                    <h2 className="neo-card-title">ÿßÿ∑ŸÑÿßÿπÿßÿ™ ÿ≥ŸÅÿßÿ±ÿ¥</h2>
                </div>
                <div className="neo-order-info-grid">
                    <div className="neo-info-item">
                        <span className="neo-info-label">ŸÖÿ¥ÿ™ÿ±€å</span>
                        <span className="neo-info-value">{order.customer_name}</span>
                    </div>
                    <div className="neo-info-item">
                        <span className="neo-info-label">ÿ™ÿßÿ±€åÿÆ ÿß€åÿ¨ÿßÿØ</span>
                        <span className="neo-info-value">
                            {new Date(order.created_at).toLocaleDateString('fa-IR')}
                        </span>
                    </div>
                    <div className="neo-info-item">
                        <span className="neo-info-label">Ÿàÿ∂ÿπ€åÿ™</span>
                        <span className="neo-info-value">{formatStatus(order.status)}</span>
                    </div>
                    {order.quoted_total > 0 && (
                        <div className="neo-info-item">
                            <span className="neo-info-label">ŸÖÿ®ŸÑÿ∫ ⁄©ŸÑ</span>
                            <span className="neo-info-value neo-payable-amount">
                                {formatPrice(order.quoted_total)}
                            </span>
                        </div>
                    )}
                    {order.invoice_number && (
                        <div className="neo-info-item">
                            <span className="neo-info-label">ÿ¥ŸÖÿßÿ±Ÿá ŸÅÿß⁄©ÿ™Ÿàÿ±</span>
                            <span className="neo-info-value">{order.invoice_number}</span>
                        </div>
                    )}
                    {order.invoice_date && (
                        <div className="neo-info-item">
                            <span className="neo-info-label">ÿ™ÿßÿ±€åÿÆ ŸÅÿß⁄©ÿ™Ÿàÿ±</span>
                            <span className="neo-info-value">
                                {new Date(order.invoice_date).toLocaleDateString('fa-IR')}
                            </span>
                        </div>
                    )}
                </div>

                {/* Customer Comment Section */}
                {(order.customer_comment || isEditing) && (
                    <div className="neo-comment-section">
                        <h3 className="neo-comment-title">ÿ™Ÿàÿ∂€åÿ≠ÿßÿ™ ŸÖÿ¥ÿ™ÿ±€å</h3>
                        {!isEditing ? (
                            <div className="neo-comment-display">
                                <p className="neo-comment-text">
                                    {order.customer_comment || 'Ÿá€å⁄Ü ÿ™Ÿàÿ∂€åÿ≠€å ÿßÿ±ÿßÿ¶Ÿá ŸÜÿ¥ÿØŸá'}
                                </p>
                                {order.status === 'pending_pricing' && (
                                    <NeoBrutalistButton
                                        text="Ÿà€åÿ±ÿß€åÿ¥ ŸÜÿ∏ÿ±"
                                        color="blue-400"
                                        textColor="white"
                                        onClick={() => setIsEditing(true)}
                                        className="neo-edit-btn"
                                    />
                                )}
                            </div>
                        ) : (
                            <div className="neo-edit-form">
                                <NeoBrutalistInput
                                    type="text"
                                    value={editedComment}
                                    onChange={(e) => setEditedComment(e.target.value)}
                                    placeholder="ŸÜÿ∏ÿ±ÿßÿ™ ÿÆŸàÿØ ÿ±ÿß Ÿàÿßÿ±ÿØ ⁄©ŸÜ€åÿØ..."
                                />
                                <div className="neo-edit-actions">
                                    <NeoBrutalistButton
                                        text="ÿ∞ÿÆ€åÿ±Ÿá"
                                        color="green-400"
                                        textColor="black"
                                        onClick={() => setIsEditing(false)}
                                        className="neo-edit-btn"
                                    />
                                    <NeoBrutalistButton
                                        text="ŸÑÿ∫Ÿà"
                                        color="gray-400"
                                        textColor="black"
                                        onClick={() => {
                                            setIsEditing(false);
                                            setEditedComment(order.customer_comment || '');
                                        }}
                                        className="neo-edit-btn"
                                    />
                                </div>
                            </div>
                        )}
                    </div>
                )}
            </NeoBrutalistCard>

            {/* Order Items */}
            <NeoBrutalistCard className="neo-items-card">
                <div className="neo-card-header">
                    <h2 className="neo-card-title">ÿßŸÇŸÑÿßŸÖ ÿ≥ŸÅÿßÿ±ÿ¥</h2>
                </div>
                <div className="neo-items-table" ref={tableRef}>
                    <div className="neo-table-header">
                        <div className="neo-header-cell">ŸÜÿßŸÖ ŸÖÿ≠ÿµŸàŸÑ</div>
                        <div className="neo-header-cell">ÿ™ÿπÿØÿßÿØ ÿØÿ±ÿÆŸàÿßÿ≥ÿ™€å</div>
                        <div className="neo-header-cell">€åÿßÿØÿØÿßÿ¥ÿ™ ŸÖÿ¥ÿ™ÿ±€å</div>
                        <div className="neo-header-cell">ŸÇ€åŸÖÿ™ Ÿàÿßÿ≠ÿØ</div>
                        <div className="neo-header-cell">ÿ™ÿπÿØÿßÿØ ŸÜŸáÿß€å€å</div>
                        <div className="neo-header-cell">ŸÖÿ®ŸÑÿ∫ ⁄©ŸÑ</div>
                    </div>
                    {order.items?.map((item, index) => (
                        <div key={index} className="neo-table-row">
                            <div
                                className="neo-table-cell"
                                title={item.product_name}
                                data-pending={!item.product_name}
                            >
                                {item.product_name || 'ŸÜÿßŸÖÿ¥ÿÆÿµ'}
                            </div>
                            <div className="neo-table-cell">
                                {formatQuantity(item.requested_quantity)}
                            </div>
                            <div
                                className="neo-table-cell"
                                title={item.customer_notes}
                                data-pending={!item.customer_notes}
                            >
                                {truncateText(item.customer_notes) || '-'}
                            </div>
                            <div
                                className="neo-table-cell"
                                data-pending={!item.quoted_unit_price}
                            >
                                {formatPrice(item.quoted_unit_price)}
                            </div>
                            <div
                                className="neo-table-cell"
                                data-pending={!item.final_quantity}
                            >
                                {formatQuantity(item.final_quantity)}
                            </div>
                            <div
                                className="neo-table-cell"
                                data-pending={!item.quoted_unit_price || !item.final_quantity}
                            >
                                {calculateTotal(item.quoted_unit_price, item.final_quantity)}
                            </div>
                        </div>
                    ))}
                </div>
            </NeoBrutalistCard>

            {/* Admin Reply */}
            {order.admin_comment && (
                <NeoBrutalistCard className="neo-admin-reply-card">
                    <div className="neo-card-header">
                        <h2 className="neo-card-title">Ÿæÿßÿ≥ÿÆ ŸÖÿØ€åÿ±</h2>
                    </div>
                    <div className="neo-admin-info-grid">
                        <div className="neo-info-item">
                            <span className="neo-info-label">ÿ™ÿßÿ±€åÿÆ ŸÇ€åŸÖÿ™‚Äå⁄Øÿ∞ÿßÿ±€å</span>
                            <span className="neo-info-value">
                                {order.pricing_date
                                    ? new Date(order.pricing_date).toLocaleDateString('fa-IR')
                                    : 'ŸáŸÜŸàÿ≤ ŸÇ€åŸÖÿ™‚Äå⁄Øÿ∞ÿßÿ±€å ŸÜÿ¥ÿØŸá'
                                }
                            </span>
                        </div>
                        <div className="neo-info-item">
                            <span className="neo-info-label">€åÿßÿØÿØÿßÿ¥ÿ™‚ÄåŸáÿß€å ŸÖÿØ€åÿ±</span>
                            <span className="neo-info-value">{order.admin_comment}</span>
                        </div>
                    </div>
                </NeoBrutalistCard>
            )}

            {/* Customer Approval Section */}
