// frontend/src/pages/NewArrivalsPage.js - COMPLETE UPDATED Customer View with Enhanced Image Gallery
import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import API from '../component/api';
import NeoBrutalistCard from '../component/NeoBrutalist/NeoBrutalistCard';
import NeoBrutalistButton from '../component/NeoBrutalist/NeoBrutalistButton';
import NeoBrutalistModal from '../component/NeoBrutalist/NeoBrutalistModal';
import '../styles/component/NewArrivalsPage.css';

const NewArrivalsPage = () => {
    const [shipmentAnnouncements, setShipmentAnnouncements] = useState([]);
    const [featuredProducts, setFeaturedProducts] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');
    const [selectedAnnouncement, setSelectedAnnouncement] = useState(null);
    const [currentImageIndex, setCurrentImageIndex] = useState(0);
    const [isImageGalleryOpen, setIsImageGalleryOpen] = useState(false);
    const [selectedImages, setSelectedImages] = useState([]);
    const navigate = useNavigate();

    // Category mapping for Persian display
    const getCategoryDisplayName = (categoryData) => {
        if (!categoryData) return '';

        // If it's a string (old format), use mapping
        if (typeof categoryData === 'string') {
            const categoryMap = {
                'Coffee Related': 'محصولات قهوه',
                'Seeds': 'دانه‌ها',
                'Spices': 'ادویه‌جات',
                'Nuts': 'آجیل',
                'Confectionery products': 'محصولات قنادی'
            };
            return categoryMap[categoryData] || categoryData;
        }

        // If it's an object with display_name
        if (typeof categoryData === 'object') {
            return categoryData.display_name || categoryData.name || '';
        }

        return categoryData;
    };

    useEffect(() => {
        fetchShipmentAnnouncements();
        fetchFeaturedProducts();
    }, []);

    const fetchShipmentAnnouncements = async () => {
        try {
            console.log('🚢 Fetching shipment announcements...');
            const response = await API.get('/shipment-announcements/');
            console.log('✅ Shipment announcements fetched:', response.data);
            setShipmentAnnouncements(response.data);
        } catch (err) {
            console.error('❌ Error fetching shipment announcements:', err);
            if (err.response?.status === 401) {
                setError('نشست شما منقضی شده است. در حال انتقال به صفحه ورود...');
                setTimeout(() => handleLogout(), 2000);
            } else {
                setError('خطا در بارگیری اطلاعیه‌های محموله');
            }
        }
    };

    const fetchFeaturedProducts = async () => {
        try {
            // Get recent products that might be featured
            const response = await API.get('/products/new-arrivals/');
            console.log('🆕 Featured products fetched:', response.data);
            setFeaturedProducts(response.data.slice(0, 6)); // Limit to 6 featured items
        } catch (err) {
            console.error('❌ Error fetching featured products:', err);
            // Don't set error for featured products - they're optional
        } finally {
            setLoading(false);
        }
    };

    const handleLogout = () => {
        localStorage.removeItem('access_token');
        localStorage.removeItem('refresh_token');
        localStorage.removeItem('userData');
        delete API.defaults.headers.common['Authorization'];
        navigate('/');
    };

    const getDaysAgo = (dateString) => {
        const date = new Date(dateString);
        const now = new Date();
        const diffTime = Math.abs(now - date);
        const diffDays = Math.ceil(diffTime / (1000 * 60 * 60 * 24));

        if (diffDays === 1) return 'دیروز';
        if (diffDays < 7) return `${diffDays} روز پیش`;
        if (diffDays < 30) return `${Math.floor(diffDays / 7)} هفته پیش`;
        return 'بیش از یک ماه پیش';
    };

    const handleOrderInquiry = (productName, announcementTitle) => {
        // Navigate to create order with inquiry about this product/announcement
        navigate('/orders/create', {
            state: {
                inquiryMode: true,
                productInquiry: productName,
                announcementReference: announcementTitle
            }
        });
    };

    const openAnnouncementModal = (announcement) => {
        setSelectedAnnouncement(announcement);
        setCurrentImageIndex(0);
    };

    const openImageGallery = (images, startIndex = 0) => {
        setSelectedImages(images);
        setCurrentImageIndex(startIndex);
        setIsImageGalleryOpen(true);
    };

    const nextImage = () => {
        if (selectedImages && selectedImages.length > 1) {
            setCurrentImageIndex((prev) =>
                prev === selectedImages.length - 1 ? 0 : prev + 1
            );
        }
    };

    const prevImage = () => {
        if (selectedImages && selectedImages.length > 1) {
            setCurrentImageIndex((prev) =>
                prev === 0 ? selectedImages.length - 1 : prev - 1
            );
        }
    };

    const formatPrice = (price) => {
        if (!price || price === 0) return 'تماس بگیرید';
        return `${parseFloat(price).toLocaleString('fa-IR')} ریال`;
    };

    if (loading) {
        return (
            <div className="new-arrivals-page">
                <div className="page-header">
                    <div className="loading-container">
                        <div className="loading-spinner"></div>
                        <h1>در حال بارگیری اطلاعیه‌های جدید...</h1>
                    </div>
                </div>
            </div>
        );
    }

    return (
        <div className="new-arrivals-page" dir="rtl">
            {/* Header */}
            <div className="page-header">
                <div className="header-content">
                    <div className="title-section">
                        <h1 className="page-title">🚢 محموله‌های جدید</h1>
                        <p className="page-subtitle">
                            آخرین اطلاعیه‌ها و محموله‌های وارد شده
                            {shipmentAnnouncements.length > 0 && ` - ${shipmentAnnouncements.length} محموله`}
                        </p>
                    </div>
                    <div className="header-actions">
                        <NeoBrutalistButton
                            text="کاتالوگ محصولات"
                            color="blue-400"
                            textColor="white"
                            onClick={() => navigate('/product')}
                            className="catalog-btn"
                        />
                        <NeoBrutalistButton
                            text="ثبت سفارش"
                            color="green-400"
                            textColor="black"
                            onClick={() => navigate('/orders/create')}
                            className="order-btn"
                        />
                        <NeoBrutalistButton
                            text="داشبورد"
                            color="yellow-400"
                            textColor="black"
                            onClick={() => navigate('/dashboard')}
                            className="dashboard-btn"
                        />
                        <NeoBrutalistButton
                            text="خروج"
                            color="red-400"
                            textColor="white"
                            onClick={handleLogout}
                            className="logout-btn"
                        />
                    </div>
                </div>
            </div>

            {error && (
                <div className="error-banner">
                    <span>⚠️ {error}</span>
                    <NeoBrutalistButton
                        text="تلاش مجدد"
                        color="blue-400"
                        textColor="white"
                        onClick={() => {
                            setError('');
                            fetchShipmentAnnouncements();
                            fetchFeaturedProducts();
                        }}
                        className="retry-btn"
                    />
                </div>
            )}

            {/* Latest Shipment Announcements */}
            {shipmentAnnouncements.length > 0 && (
                <div className="shipments-section">
                    <h2 className="section-title">📢 آخرین محموله‌ها</h2>
                    <div className="shipments-grid">
                        {shipmentAnnouncements.map((announcement) => (
                            <NeoBrutalistCard
                                key={announcement.id}
                                className={`shipment-card ${announcement.is_featured ? 'featured' : ''}`}
                                onClick={() => openAnnouncementModal(announcement)}
                            >
                                <div className="shipment-header">
                                    <h3 className="shipment-title">{announcement.title}</h3>
                                    <div className="shipment-meta">
                                        <span className="shipment-date">
                                            {getDaysAgo(announcement.created_at)}
                                        </span>
                                        {announcement.is_featured && (
                                            <span className="featured-badge">ویژه</span>
                                        )}
                                        {announcement.created_by_name && (
                                            <span className="author-badge">
                                                توسط {announcement.created_by_name}
                                            </span>
                                        )}
                                    </div>
                                </div>

                                {/* Enhanced Multiple Images Support with Gallery */}
                                {announcement.images && announcement.images.length > 0 && (
                                    <div className="shipment-images">
                                        {announcement.images.length === 1 ? (
                                            <div className="single-image" onClick={(e) => {
                                                e.stopPropagation();
                                                openImageGallery(announcement.images, 0);
                                            }}>
                                                <img
                                                    src={announcement.images[0].image}
                                                    alt={announcement.title}
                                                    className="shipment-image"
                                                    onError={(e) => {
                                                        e.target.style.display = 'none';
                                                    }}
                                                />
                                                <div className="image-overlay">
                                                    <span>🔍 کلیک برای بزرگنمایی</span>
                                                </div>
                                            </div>
                                        ) : (
                                            <div className="multiple-images">
                                                <div className="main-image" onClick={(e) => {
                                                    e.stopPropagation();
                                                    openImageGallery(announcement.images, 0);
                                                }}>
                                                    <img
                                                        src={announcement.images[0].image}
                                                        alt={announcement.title}
                                                        className="shipment-image"
                                                        onError={(e) => {
                                                            e.target.style.display = 'none';
                                                        }}
                                                    />
                                                    <div className="image-overlay">
                                                        <span>🔍 {announcement.images.length} تصویر</span>
                                                    </div>
                                                </div>
                                                <div className="thumbnail-grid">
                                                    {announcement.images.slice(1, 4).map((img, idx) => (
                                                        <div key={idx} className="thumbnail" onClick={(e) => {
                                                            e.stopPropagation();
                                                            openImageGallery(announcement.images, idx + 1);
                                                        }}>
                                                            <img
                                                                src={img.image}
                                                                alt={`${announcement.title} ${idx + 2}`}
                                                                className="thumbnail-image"
                                                                onError={(e) => {
                                                                    e.target.style.display = 'none';
                                                                }}
                                                            />
                                                        </div>
                                                    ))}
                                                    {announcement.images.length > 4 && (
                                                        <div className="more-images" onClick={(e) => {
                                                            e.stopPropagation();
                                                            openImageGallery(announcement.images, 4);
                                                        }}>
                                                            <span>+{announcement.images.length - 4}</span>
                                                        </div>
                                                    )}
                                                </div>
                                            </div>
                                        )}
                                    </div>
                                )}

                                <div className="shipment-content">
                                    <p className="shipment-description">
                                        {announcement.description.length > 150
                                            ? `${announcement.description.substring(0, 150)}...`
                                            : announcement.description
                                        }
                                    </p>

                                    {/* Enhanced Shipment Details */}
                                    <div className="shipment-details">
                                        {announcement.origin_country && (
                                            <div className="detail-item">
                                                <span className="detail-label">🌍 مبدأ:</span>
                                                <span className="detail-value">{announcement.origin_country}</span>
                                            </div>
                                        )}
                                        {announcement.shipment_date && (
                                            <div className="detail-item">
                                                <span className="detail-label">📅 تاریخ ارسال:</span>
                                                <span className="detail-value">
                                                    {new Date(announcement.shipment_date).toLocaleDateString('fa-IR')}
                                                </span>
                                            </div>
                                        )}
                                        {announcement.estimated_arrival && (
                                            <div className="detail-item">
                                                <span className="detail-label">🚛 تاریخ رسیدن:</span>
                                                <span className="detail-value">
                                                    {new Date(announcement.estimated_arrival).toLocaleDateString('fa-IR')}
                                                </span>
                                            </div>
                                        )}
                                        {announcement.product_categories && (
                                            <div className="detail-item">
                                                <span className="detail-label">📦 دسته‌بندی:</span>
                                                <span className="detail-value">{announcement.product_categories}</span>
                                            </div>
                                        )}
                                        {announcement.products_count > 0 && (
                                            <div className="detail-item">
                                                <span className="detail-label">📊 تعداد محصول:</span>
                                                <span className="detail-value">
                                                    {announcement.products_count} قلم
                                                </span>
                                            </div>
                                        )}
                                    </div>

                                    {/* Related Products Preview - NO STOCK NUMBERS */}
                                    {announcement.related_products_info && announcement.related_products_info.length > 0 && (
                                        <div className="related-products-preview">
                                            <h4 className="related-title">محصولات این محموله:</h4>
                                            <div className="related-list">
                                                {announcement.related_products_info.slice(0, 3).map((product, idx) => (
                                                    <div key={idx} className="related-item">
                                                        <span className="item-name">{product.name}</span>
                                                        {product.stock_status && (
                                                            <span className={`item-status ${product.stock_status}`}>
                                                                {product.stock_status === 'in_stock' ? 'موجود' :
                                                                    product.stock_status === 'out_of_stock' ? 'ناموجود' : 'نامشخص'}
                                                            </span>
                                                        )}
                                                    </div>
                                                ))}
                                                {announcement.related_products_info.length > 3 && (
                                                    <div className="more-products">
                                                        و {announcement.related_products_info.length - 3} محصول دیگر...
                                                    </div>
                                                )}
                                            </div>
                                        </div>
                                    )}
                                </div>

                                <div className="shipment-actions">
                                    <NeoBrutalistButton
                                        text="مشاهده جزئیات"
                                        color="blue-400"
                                        textColor="white"
                                        onClick={(e) => {
                                            e.stopPropagation();
                                            openAnnouncementModal(announcement);
                                        }}
                                        className="details-btn"
                                    />
                                    <NeoBrutalistButton
                                        text="استعلام قیمت"
                                        color="green-400"
                                        textColor="black"
                                        onClick={(e) => {
                                            e.stopPropagation();
                                            handleOrderInquiry('محصولات محموله', announcement.title);
                                        }}
                                        className="inquiry-btn"
                                    />
                                </div>
                            </NeoBrutalistCard>
                        ))}
                    </div>
                </div>
            )}

            {/* Featured Products Section */}
            {featuredProducts.length > 0 && (
                <div className="featured-products-section">
                    <div className="section-header" style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '1rem' }}>
                        <h2 className="section-title">⭐ محصولات ویژه</h2>
                        <NeoBrutalistButton
                            text="مشاهده کاتالوگ کامل"
                            color="green-400"
                            textColor="black"
                            onClick={() => navigate('/product')}
                            className="view-catalog-btn"
                        />
                    </div>
                    <div className="products-grid" style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(280px, 1fr))', gap: '1.5rem' }}>
                        {featuredProducts.map(product => (
                            <NeoBrutalistCard
                                key={product.id}
                                className="product-preview-card"
                                onClick={() => navigate('/product')}
                                style={{ cursor: 'pointer' }}
                            >
                                <div className="product-preview-image" style={{ marginBottom: '1rem', position: 'relative' }}>
                                    <img
                                        src={product.image_url || 'https://placehold.co/280x200/e2e8f0/a0aec0?text=محصول'}
                                        alt={product.name}
                                        style={{
                                            width: '100%',
                                            height: '200px',
                                            objectFit: 'cover',
                                            borderRadius: '8px'
                                        }}
                                        onClick={(e) => {
                                            if (product.image_url) {
                                                e.stopPropagation();
                                                openImageGallery([{image: product.image_url}], 0);
                                            }
                                        }}
                                    />
                                    {product.image_url && (
                                        <div className="image-overlay" style={{
                                            position: 'absolute',
                                            top: 0,
                                            left: 0,
                                            right: 0,
                                            bottom: 0,
                                            backgroundColor: 'rgba(0,0,0,0.5)',
                                            display: 'flex',
                                            alignItems: 'center',
                                            justifyContent: 'center',
                                            opacity: 0,
                                            transition: 'opacity 0.3s',
                                            borderRadius: '8px',
                                            color: 'white',
                                            fontSize: '0.9rem'
                                        }}>
                                            <span>🔍 کلیک برای بزرگنمایی</span>
                                        </div>
                                    )}
                                </div>
                                <h4 style={{ fontSize: '1.1rem', margin: '0 0 0.5rem 0', fontWeight: 'bold' }}>{product.name}</h4>
                                <p style={{ fontSize: '0.9rem', color: '#666', margin: '0 0 1rem 0', lineHeight: '1.4' }}>
                                    {product.description?.substring(0, 100)}...
                                </p>
                                <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '1rem' }}>
                                    <span style={{ fontWeight: 'bold', color: '#059669', fontSize: '1.1rem' }}>
                                        {formatPrice(product.base_price)}
                                    </span>
                                    {getCategoryDisplayName(product.category_name) && (
                                        <span style={{
                                            backgroundColor: '#e0e7ff',
                                            color: '#3730a3',
                                            padding: '0.25rem 0.75rem',
                                            borderRadius: '12px',
                                            fontSize: '0.8rem',
                                            fontWeight: '500'
                                        }}>
                                            {getCategoryDisplayName(product.category_name)}
                                        </span>
                                    )}
                                </div>
                                <NeoBrutalistButton
                                    text="مشاهده در کاتالوگ"
                                    color="blue-400"
                                    textColor="white"
                                    onClick={(e) => {
                                        e.stopPropagation();
                                        navigate('/product');
                                    }}
                                    style={{ width: '100%' }}
                                />
                            </NeoBrutalistCard>
                        ))}
                    </div>
                </div>
            )}

            {/* Empty State */}
            {shipmentAnnouncements.length === 0 && featuredProducts.length === 0 && !loading && (
                <div className="empty-state">
                    <NeoBrutalistCard className="empty-card">
                        <div className="empty-content">
                            <div className="empty-icon">🚢</div>
                            <h3>در حال حاضر محموله جدیدی نداریم</h3>
                            <p>به محض ورود محموله‌های جدید، اطلاعیه‌ها در اینجا نمایش داده می‌شود.</p>

                            <div className="empty-actions">
                                <NeoBrutalistButton
                                    text="مشاهده کاتالوگ فعلی"
                                    color="blue-400"
                                    textColor="white"
                                    onClick={() => navigate('/product')}
                                    className="view-catalog-btn"
                                />
                                <NeoBrutalistButton
                                    text="ثبت درخواست ویژه"
                                    color="yellow-400"
                                    textColor="black"
                                    onClick={() => navigate('/orders/create')}
                                    className="special-request-btn"
                                />
                            </div>
                        </div>
                    </NeoBrutalistCard>
                </div>
            )}

            {/* Quick Actions */}
            <div className="quick-actions-section">
                <NeoBrutalistCard className="quick-actions-card">
                    <h3 className="actions-title">خدمات سریع</h3>
                    <div className="actions-grid">
                        <NeoBrutalistButton
                            text="📋 ثبت سفارش جدید"
                            color="green-400"
                            textColor="black"
                            onClick={() => navigate('/orders/create')}
                            className="quick-action-btn"
                        />

                        <NeoBrutalistButton
                            text="📦 کاتالوگ کامل"
                            color="blue-400"
                            textColor="white"
                            onClick={() => navigate('/product')}
                            className="quick-action-btn"
                        />

                        <NeoBrutalistButton
                            text="📞 تماس برای استعلام"
                            color="yellow-400"
                            textColor="black"
                            onClick={() => {
                                window.open('tel:+989123456789', '_self');
                            }}
                            className="quick-action-btn"
                        />

                        <NeoBrutalistButton
                            text="📨 سفارشات من"
                            color="purple-400"
                            textColor="white"
                            onClick={() => navigate('/dashboard')}
                            className="quick-action-btn"
                        />
                    </div>
                </NeoBrutalistCard>
            </div>

            {/* Enhanced Shipment Detail Modal */}
            <NeoBrutalistModal
                isOpen={!!selectedAnnouncement}
                onClose={() => setSelectedAnnouncement(null)}
                title={selectedAnnouncement ? selectedAnnouncement.title : ""}
                size="large"
            >
                {selectedAnnouncement && (
                    <div className="announcement-detail-modal" dir="rtl">
                        {/* Image Gallery in Modal */}
                        {selectedAnnouncement.images && selectedAnnouncement.images.length > 0 && (
                            <div className="modal-image-gallery">
                                <div className="modal-main-image-container">
                                    <img
                                        src={selectedAnnouncement.images[0].image}
                                        alt={`${selectedAnnouncement.title} - تصویر اصلی`}
                                        className="modal-main-image"
                                        onClick={() => openImageGallery(selectedAnnouncement.images, 0)}
                                        style={{ cursor: 'pointer' }}
                                        onError={(e) => {
                                            e.target.style.display = 'none';
                                        }}
                                    />
                                    <div className="modal-image-overlay">
                                        <span>🔍 کلیک برای گالری کامل</span>
                                    </div>
                                </div>

                                {/* Thumbnail Grid */}
                                {selectedAnnouncement.images.length > 1 && (
                                    <div className="modal-thumbnail-grid">
                                        {selectedAnnouncement.images.slice(1).map((img, index) => (
                                            <div
                                                key={index}
                                                className="modal-thumbnail"
                                                onClick={() => openImageGallery(selectedAnnouncement.images, index + 1)}
                                            >
                                                <img
                                                    src={img.image}
                                                    alt={`تصویر ${index + 2}`}
                                                    className="modal-thumbnail-image"
                                                />
                                            </div>
                                        ))}
                                    </div>
                                )}
                            </div>
                        )}

                        {/* Announcement Details */}
                        <div className="modal-announcement-info">
                            <div className="modal-header-info">
                                <h2 className="modal-announcement-title">{selectedAnnouncement.title}</h2>
                                <div className="modal-meta">
                                    <span className="modal-date">
                                        {getDaysAgo(selectedAnnouncement.created_at)}
                                    </span>
                                    {selectedAnnouncement.is_featured && (
                                        <span className="modal-featured-badge">ویژه</span>
                                    )}
                                </div>
                            </div>

                            <div className="modal-description">
                                <h4>توضیحات کامل:</h4>
                                <p>{selectedAnnouncement.description}</p>
                            </div>

                            {/* Complete Details */}
                            <div className="modal-complete-details">
                                {selectedAnnouncement.origin_country && (
                                    <div className="modal-detail-row">
                                        <span className="modal-detail-label">🌍 کشور مبدأ:</span>
                                        <span className="modal-detail-value">{selectedAnnouncement.origin_country}</span>
                                    </div>
                                )}

                                {selectedAnnouncement.shipment_date && (
                                    <div className="modal-detail-row">
                                        <span className="modal-detail-label">📅 تاریخ ارسال:</span>
                                        <span className="modal-detail-value">
                                            {new Date(selectedAnnouncement.shipment_date).toLocaleDateString('fa-IR')}
                                        </span>
                                    </div>
                                )}

                                {selectedAnnouncement.estimated_arrival && (
                                    <div className="modal-detail-row">
                                        <span className="modal-detail-label">🚛 تاریخ تخمینی رسیدن:</span>
                                        <span className="modal-detail-value">
                                            {new Date(selectedAnnouncement.estimated_arrival).toLocaleDateString('fa-IR')}
                                        </span>
                                    </div>
                                )}

                                {selectedAnnouncement.product_categories && (
                                    <div className="modal-detail-row">
                                        <span className="modal-detail-label">📦 دسته‌بندی محصولات:</span>
                                        <span className="modal-detail-value">{selectedAnnouncement.product_categories}</span>
                                    </div>
                                )}

                                <div className="modal-detail-row">
                                    <span className="modal-detail-label">📊 تعداد بازدید:</span>
                                    <span className="modal-detail-value">
                                        {selectedAnnouncement.view_count || 0} بار
                                    </span>
                                </div>

                                <div className="modal-detail-row">
                                    <span className="modal-detail-label">📅 تاریخ انتشار:</span>
                                    <span className="modal-detail-value">
                                        {new Date(selectedAnnouncement.created_at).toLocaleDateString('fa-IR')}
                                    </span>
                                </div>
                            </div>

                            {/* Related Products Full List - NO STOCK NUMBERS */}
                            {selectedAnnouncement.related_products_info && selectedAnnouncement.related_products_info.length > 0 && (
                                <div className="modal-related-products">
                                    <h4>محصولات این محموله:</h4>
                                    <div className="modal-products-list">
                                        {selectedAnnouncement.related_products_info.map((product, idx) => (
                                            <div key={idx} className="modal-product-item">
                                                <div className="product-item-info">
                                                    <span className="product-item-name">{product.name}</span>
                                                    {product.stock_status && (
                                                        <span className={`product-item-status ${product.stock_status}`}>
                                                            {product.stock_status === 'in_stock' ? 'موجود' :
                                                                product.stock_status === 'out_of_stock' ? 'ناموجود' : 'نامشخص'}
                                                        </span>
                                                    )}
                                                </div>
                                                <NeoBrutalistButton
                                                    text="استعلام"
                                                    color="blue-400"
                                                    textColor="white"
                                                    onClick={() => {
                                                        setSelectedAnnouncement(null);
                                                        handleOrderInquiry(product.name, selectedAnnouncement.title);
                                                    }}
                                                    className="product-inquiry-btn"
                                                />
                                            </div>
                                        ))}
                                    </div>
                                </div>
                            )}
                        </div>
                    </div>
                )}
            </NeoBrutalistModal>

            {/* Full-Screen Image Gallery Modal */}
            <NeoBrutalistModal
                isOpen={isImageGalleryOpen}
                onClose={() => setIsImageGalleryOpen(false)}
                title="گالری تصاویر"
                size="large"
            >
                <div className="fullscreen-image-gallery">
                    {selectedImages.length > 0 && (
                        <>
                            <div className="gallery-main-container">
                                <img
                                    src={selectedImages[currentImageIndex]?.image}
                                    alt={`تصویر ${currentImageIndex + 1}`}
                                    className="gallery-main-image"
                                    style={{
                                        maxWidth: '100%',
                                        maxHeight: '70vh',
                                        objectFit: 'contain',
                                        margin: '0 auto',
                                        display: 'block'
                                    }}
                                />
                                {selectedImages.length > 1 && (
                                    <>
                                        <button
                                            className="gallery-nav-btn gallery-prev"
                                            onClick={prevImage}
                                            style={{
                                                position: 'absolute',
                                                left: '10px',
                                                top: '50%',
                                                transform: 'translateY(-50%)',
                                                backgroundColor: 'rgba(0,0,0,0.7)',
                                                color: 'white',
                                                border: 'none',
                                                borderRadius: '50%',
                                                width: '50px',
                                                height: '50px',
                                                fontSize: '24px',
                                                cursor: 'pointer',
                                                zIndex: 10
                                            }}
                                        >
                                            ‹
                                        </button>
                                        <button
                                            className="gallery-nav-btn gallery-next"
                                            onClick={nextImage}
                                            style={{
                                                position: 'absolute',
                                                right: '10px',
                                                top: '50%',
                                                transform: 'translateY(-50%)',
                                                backgroundColor: 'rgba(0,0,0,0.7)',
                                                color: 'white',
                                                border: 'none',
                                                borderRadius: '50%',
                                                width: '50px',
                                                height: '50px',
                                                fontSize: '24px',
                                                cursor: 'pointer',
                                                zIndex: 10
                                            }}
                                        >
                                            ›
                                        </button>
                                    </>
                                )}
                            </div>
                            <div className="gallery-counter" style={{
                                textAlign: 'center',
                                marginTop: '1rem',
                                fontSize: '1.1rem',
                                fontWeight: 'bold'
                            }}>
                                {currentImageIndex + 1} از {selectedImages.length}
                            </div>
                            {selectedImages.length > 1 && (
                                <div className="gallery-thumbnails" style={{
                                    display: 'flex',
                                    justifyContent: 'center',
                                    gap: '0.5rem',
                                    marginTop: '1rem',
                                    flexWrap: 'wrap'
                                }}>
                                    {selectedImages.map((image, index) => (
                                        <img
                                            key={index}
                                            src={image.image}
                                            alt={`تصویر کوچک ${index + 1}`}
                                            className={`gallery-thumbnail ${index === currentImageIndex ? 'active' : ''}`}
                                            onClick={() => setCurrentImageIndex(index)}
                                            style={{
                                                width: '60px',
                                                height: '60px',
                                                objectFit: 'cover',
                                                borderRadius: '4px',
                                                cursor: 'pointer',
                                                border: index === currentImageIndex ? '3px solid #3b82f6' : '2px solid #e5e7eb',
                                                transition: 'border-color 0.2s'
                                            }}
                                        />
                                    ))}
                                </div>
                            )}

                            {/* Image Actions */}
                            <div className="gallery-actions" style={{
                                display: 'flex',
                                justifyContent: 'center',
                                gap: '1rem',
                                marginTop: '1.5rem'
                            }}>
                                <NeoBrutalistButton
                                    text="دانلود تصویر"
                                    color="green-400"
                                    textColor="black"
                                    onClick={() => {
                                        const link = document.createElement('a');
                                        link.href = selectedImages[currentImageIndex]?.image;
                                        link.download = `image-${currentImageIndex + 1}.jpg`;
                                        link.click();
                                    }}
                                />
                                <NeoBrutalistButton
                                    text="بستن گالری"
                                    color="gray-400"
                                    textColor="black"
                                    onClick={() => setIsImageGalleryOpen(false)}
                                />
                            </div>
                        </>
                    )}
                </div>
            </NeoBrutalistModal>

            {/* Enhanced CSS for image interactions */}
            <style jsx>{`
                .product-preview-image:hover .image-overlay,
                .single-image:hover .image-overlay,
                .main-image:hover .image-overlay,
                .modal-main-image-container:hover .modal-image-overlay {
                    opacity: 1;
                }

                .shipment-image,
                .product-preview-image img {
                    transition: transform 0.3s ease;
                }

                .shipment-image:hover,
                .product-preview-image img:hover {
                    transform: scale(1.05);
                }

                .thumbnail:hover,
                .modal-thumbnail:hover {
                    transform: scale(1.1);
                    transition: transform 0.2s ease;
                }

                .gallery-main-container {
                    position: relative;
                    display: flex;
                    justify-content: center;
                    align-items: center;
                    min-height: 400px;
                }

                .gallery-nav-btn:hover {
                    backgroundColor: rgba(0,0,0,0.9) !important;
                    transform: translateY(-50%) scale(1.1);
                }

                .modal-thumbnail {
                    cursor: pointer;
                    border-radius: 8px;
                    overflow: hidden;
                    transition: transform 0.2s ease;
                }

                .modal-thumbnail-image {
                    width: 80px;
                    height: 80px;
                    object-fit: cover;
                }

                .modal-thumbnail-grid {
                    display: flex;
                    gap: 0.5rem;
                    margin-top: 1rem;
                    flex-wrap: wrap;
                    justify-content: center;
                }
            `}</style>
        </div>
    );
};

export default NewArrivalsPage;
