import React from 'react';

const Button = ({ text = 'Click Me', color = 'yellow-400', textColor = 'black', onClick }) => {
    const bgColor = `bg-${color}`;
    const hoverBgColor = `hover:bg-${color}-600`;

    // Tailwind can't handle dynamic class names fully, so we build full classes
    const classes = `neo-brutalist ${bgColor} text-${textColor} text-lg font-bold py-3 px-6 ${hoverBgColor} border-4 border-black transition-none`;

    return (
        <button className={classes} onClick={onClick}>
            {text}
        </button>
    );
};

export default Button;
