import React, { useState, useEffect, useRef } from 'react';
import API from '../component/api';
import NeoBrutalistCard from '../component/NeoBrutalist/NeoBrutalistCard';
import NeoBrutalistButton from '../component/NeoBrutalist/NeoBrutalistButton';
import NeoBrutalistInput from '../component/NeoBrutalist/NeoBrutalistInput';
import '../styles/AdminOrderDetail.css';

const AdminOrderDetailPage = ({ orderId, onOrderUpdated }) => {
    const [order, setOrder] = useState(null);
    const [items, setItems] = useState([]);
    const [adminComment, setAdminComment] = useState('');
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');
    const [submitting, setSubmitting] = useState(false);
    const tableRef = useRef(null);

    useEffect(() => {
        if (orderId) {
            fetchOrder();
        }
    }, [orderId]);

    useEffect(() => {
        // Apply smart font sizing after component mounts
        if (order?.items && tableRef.current) {
            applySmartTextSizing();
        }
    }, [order]);

    const fetchOrder = async () => {
        setLoading(true);
        try {
            const res = await API.get(`/orders/${orderId}/`);
            setOrder(res.data);
            setItems(res.data.items || []);
            setAdminComment(res.data.admin_comment || '');
        } catch (err) {
            setError('ÿÆÿ∑ÿß ÿØÿ± ÿ®ÿßÿ±⁄Ø€åÿ±€å ÿ≥ŸÅÿßÿ±ÿ¥');
        } finally {
            setLoading(false);
        }
    };

    const handleCompleteOrder = async () => {
        try {
            if (onOrderUpdated) onOrderUpdated();
        } catch (err) {
            setError('Error competeing the order');
        }
    };

    // Smart text sizing function
    const applySmartTextSizing = () => {
        const productCells = tableRef.current?.querySelectorAll('.admin-table-cell:nth-child(1)');
        const notesCells = tableRef.current?.querySelectorAll('.admin-table-cell:nth-child(3)');

        productCells?.forEach(cell => {
            const textLength = cell.textContent.length;
            if (textLength > 30) {
                cell.style.fontSize = '0.7rem';
                cell.classList.add('text-overflow');
            } else if (textLength > 20) {
                cell.style.fontSize = '0.75rem';
            } else if (textLength > 15) {
                cell.style.fontSize = '0.8rem';
            }
        });

        notesCells?.forEach(cell => {
            const textLength = cell.textContent.length;
            if (textLength > 25) {
                cell.style.fontSize = '0.75rem';
                cell.classList.add('text-overflow');
            }
        });
    };

    const handlePricingSubmit = async (e) => {
        e.preventDefault();
        setSubmitting(true);
        setError('');

        try {
            // Validate items
            const hasValidItems = items.some(item =>
                item.quoted_unit_price && item.final_quantity
            );

            if (!hasValidItems) {
                setError('ŸÑÿ∑ŸÅÿßŸã ÿ≠ÿØÿßŸÇŸÑ ÿ®ÿ±ÿß€å €å⁄© ŸÖÿ≠ÿµŸàŸÑ ŸÇ€åŸÖÿ™ Ÿà ÿ™ÿπÿØÿßÿØ ŸÜŸáÿß€å€å ÿ±ÿß Ÿàÿßÿ±ÿØ ⁄©ŸÜ€åÿØ');
                setSubmitting(false);
                return;
            }

            await API.post(`/orders/${orderId}/submit_pricing/`, {
                admin_comment: adminComment,
                items: items.map(i => ({
                    id: i.id,
                    quoted_unit_price: Number(i.quoted_unit_price) || 0,
                    final_quantity: Number(i.final_quantity) || 0,
                    admin_notes: i.admin_notes || '',
                }))
            });

            if (onOrderUpdated) {
                onOrderUpdated();
            }
        } catch (err) {
            setError(err.response?.data?.error || 'ÿÆÿ∑ÿß ÿØÿ± ÿ®Ÿá‚Äåÿ±Ÿàÿ≤ÿ±ÿ≥ÿßŸÜ€å ŸÇ€åŸÖÿ™‚Äå⁄Øÿ∞ÿßÿ±€å');
        } finally {
            setSubmitting(false);
        }
    };

    const updateItem = (index, field, value) => {
        const newItems = [...items];
        newItems[index][field] = value;
        setItems(newItems);
    };

    const getStatusColor = (status) => {
        switch (status) {
            case 'pending_pricing':
                return 'yellow-400';
            case 'waiting_customer_approval':
                return 'blue-400';
            case 'confirmed':
                return 'green-400';
            case 'rejected':
                return 'red-400';
            case 'cancelled':
                return 'gray-400';
            default:
                return 'gray-400';
        }
    };

    const formatStatus = (status) => {
        const statusMap = {
            'pending_pricing': 'ÿØÿ± ÿßŸÜÿ™ÿ∏ÿßÿ± ŸÇ€åŸÖÿ™‚Äå⁄Øÿ∞ÿßÿ±€å',
            'waiting_customer_approval': 'ÿØÿ± ÿßŸÜÿ™ÿ∏ÿßÿ± ÿ™ÿ£€å€åÿØ ŸÖÿ¥ÿ™ÿ±€å',
            'confirmed': 'ÿ™ÿ£€å€åÿØ ÿ¥ÿØŸá',
            'completed':'ÿ™⁄©ŸÖ€åŸÑ ÿ¥ÿØŸá',
            'rejected': 'ÿ±ÿØ ÿ¥ÿØŸá',
            'cancelled': 'ŸÑÿ∫Ÿà ÿ¥ÿØŸá'
        };
        return statusMap[status] || status;
    };

    // Helper functions with Persian formatting
    const formatPrice = (price) => {
        if (!price || price === 0) return '';
        const formattedNumber = new Intl.NumberFormat('fa-IR').format(price);
        return formattedNumber;
    };

    const formatQuantity = (quantity) => {
        if (!quantity || quantity === 0) return '';
        return new Intl.NumberFormat('fa-IR').format(quantity);
    };

    const calculateTotal = (unitPrice, quantity) => {
        if (!unitPrice || !quantity || unitPrice === 0 || quantity === 0) {
            return 'ÿØÿ± ÿßŸÜÿ™ÿ∏ÿßÿ±';
        }
        const total = unitPrice * quantity;
        const formattedTotal = new Intl.NumberFormat('fa-IR').format(total);
        return `${formattedTotal} ÿ±€åÿßŸÑ`;
    };

    if (loading) {
        return (
            <div className="admin-order-detail">
                <NeoBrutalistCard className="admin-loading-card">
                    <div className="admin-loading-content">
                        <span>üîÑ</span>
                        <span className="admin-loading-text">ÿØÿ± ÿ≠ÿßŸÑ ÿ®ÿßÿ±⁄Øÿ∞ÿßÿ±€å ÿ≥ŸÅÿßÿ±ÿ¥...</span>
                    </div>
                </NeoBrutalistCard>
            </div>
        );
    }

    if (error && !order) {
        return (
            <div className="admin-order-detail">
                <NeoBrutalistCard className="admin-error-card">
                    <div className="admin-error-content">
                        <span className="admin-error-icon">‚ö†Ô∏è</span>
                        <span className="admin-error-text">{error}</span>
                    </div>
                </NeoBrutalistCard>
            </div>
        );
    }

    if (!order) {
        return (
            <div className="admin-order-detail">
                <NeoBrutalistCard className="admin-error-card">
                    <div className="admin-error-content">
                        <span className="admin-error-icon">‚ùå</span>
                        <span className="admin-error-text">ÿ≥ŸÅÿßÿ±ÿ¥ Ÿæ€åÿØÿß ŸÜÿ¥ÿØ</span>
                    </div>
                </NeoBrutalistCard>
            </div>
        );
    }

    return (
        <div className="admin-order-detail" dir="rtl">
            {/* Header */}
            <div className="admin-order-header">
                <h1 className="admin-order-title">Ÿà€åÿ±ÿß€åÿ¥ ÿ≥ŸÅÿßÿ±ÿ¥ #{order.id}</h1>
                <NeoBrutalistButton
                    text={formatStatus(order.status)}
                    color={getStatusColor(order.status)}
                    textColor="black"
                    className="admin-status-badge"
                />
            </div>

            {error && (
                <div className="admin-status-message admin-error">
                    <span className="admin-status-icon">‚ö†Ô∏è</span>
                    <span>{error}</span>
                </div>
            )}

            {/* Order Information */}
            <NeoBrutalistCard className="admin-order-info-card">
                <div className="admin-card-header">
                    <h2 className="admin-card-title">ÿßÿ∑ŸÑÿßÿπÿßÿ™ ÿ≥ŸÅÿßÿ±ÿ¥</h2>
                </div>
                <div className="admin-order-info-grid">
                    <div className="admin-info-item">
                        <span className="admin-info-label">ŸÖÿ¥ÿ™ÿ±€å</span>
                        <span className="admin-info-value">{order.customer_name}</span>
                    </div>
                    <div className="admin-info-item">
                        <span className="admin-info-label">ÿ™ÿßÿ±€åÿÆ ÿß€åÿ¨ÿßÿØ</span>
                        <span className="admin-info-value">
                            {new Date(order.created_at).toLocaleDateString('fa-IR')}
                        </span>
                    </div>
                    <div className="admin-info-item">
                        <span className="admin-info-label">Ÿàÿ∂ÿπ€åÿ™</span>
                        <span className="admin-info-value">{formatStatus(order.status)}</span>
                    </div>
                    <div className="admin-info-item">
                        <span className="admin-info-label">ÿ™Ÿàÿ∂€åÿ≠ÿßÿ™ ŸÖÿ¥ÿ™ÿ±€å</span>
                        <span className="admin-info-value">
                            {order.customer_comment || 'Ÿá€å⁄Ü ÿ™Ÿàÿ∂€åÿ≠€å ÿßÿ±ÿßÿ¶Ÿá ŸÜÿ¥ÿØŸá'}
                        </span>
                    </div>
                </div>
            </NeoBrutalistCard>

            {/* Pricing Form */}
            <NeoBrutalistCard className="admin-pricing-card">
                <div className="admin-card-header">
                    <h2 className="admin-card-title">ŸÇ€åŸÖÿ™‚Äå⁄Øÿ∞ÿßÿ±€å Ÿà ÿ¨ÿ≤ÿ¶€åÿßÿ™</h2>
                </div>

                <form className="admin-pricing-form" onSubmit={handlePricingSubmit}>
                    {/* Admin Comment */}
                    <div className="admin-comment-section">
                        <h3 className="admin-comment-title">ŸÜÿ∏ÿ± ŸÖÿØ€åÿ±</h3>
                        <textarea
                            className="admin-comment-textarea"
                            value={adminComment}
                            onChange={e => setAdminComment(e.target.value)}
                            placeholder="ŸÜÿ∏ÿ±ÿßÿ™ Ÿà ÿ™Ÿàÿ∂€åÿ≠ÿßÿ™ ŸÖÿØ€åÿ± ÿ®ÿ±ÿß€å ÿß€åŸÜ ÿ≥ŸÅÿßÿ±ÿ¥..."
                            rows={4}
                        />
                    </div>

                    {/* Items Table */}
                    <div className="admin-items-section">
                        <h3 className="admin-section-title">ŸÖÿ≠ÿµŸàŸÑÿßÿ™</h3>
                        <div className="admin-items-table" ref={tableRef}>
                            <div className="admin-table-header">
                                <div className="admin-header-cell">ŸÜÿßŸÖ ŸÖÿ≠ÿµŸàŸÑ</div>
                                <div className="admin-header-cell">ÿ™ÿπÿØÿßÿØ ÿØÿ±ÿÆŸàÿßÿ≥ÿ™€å</div>
                                <div className="admin-header-cell">ŸÜÿ∏ÿ± ŸÖÿ¥ÿ™ÿ±€å</div>
                                <div className="admin-header-cell">ŸÇ€åŸÖÿ™ Ÿàÿßÿ≠ÿØ (ÿ±€åÿßŸÑ)</div>
                                <div className="admin-header-cell">ÿ™ÿπÿØÿßÿØ ŸÜŸáÿß€å€å</div>
                                <div className="admin-header-cell">ŸÜÿ∏ÿ± ŸÖÿØ€åÿ±</div>
                                <div className="admin-header-cell">ÿ¨ŸÖÿπ ⁄©ŸÑ</div>
                            </div>

                            {items.map((item, idx) => (
                                <div key={item.id} className="admin-table-row">
                                    <div className="admin-table-cell admin-product-name" title={item.product_name}>
                                        {item.product_name}
                                    </div>
                                    <div className="admin-table-cell">
                                        {formatQuantity(item.requested_quantity)}
                                    </div>
                                    <div className="admin-table-cell admin-customer-notes" title={item.customer_notes}>
                                        {item.customer_notes || '-'}
                                    </div>
                                    <div className="admin-table-cell admin-input-cell">
                                        <NeoBrutalistInput
                                            type="number"
                                            value={item.quoted_unit_price || ''}
                                            onChange={e => updateItem(idx, 'quoted_unit_price', e.target.value)}
                                            placeholder="ŸÇ€åŸÖÿ™"
                                            min="0"
                                            step="1000"
                                        />
                                    </div>
                                    <div className="admin-table-cell admin-input-cell">
                                        <NeoBrutalistInput
                                            type="number"
                                            value={item.final_quantity || ''}
                                            onChange={e => updateItem(idx, 'final_quantity', e.target.value)}
                                            placeholder="ÿ™ÿπÿØÿßÿØ"
                                            min="0"
                                        />
                                    </div>
                                    <div className="admin-table-cell admin-input-cell">
                                        <NeoBrutalistInput
                                            type="text"
                                            value={item.admin_notes || ''}
                                            onChange={e => updateItem(idx, 'admin_notes', e.target.value)}
                                            placeholder="ŸÜÿ∏ÿ± ŸÖÿØ€åÿ±"
                                        />
                                    </div>
                                    <div className="admin-table-cell admin-total-cell">
                                        {calculateTotal(item.quoted_unit_price, item.final_quantity)}
                                    </div>
                                </div>
                            ))}
                        </div>
                    </div>

                    {/* Submit Section */}
                    <div className="admin-submit-section">
                        <NeoBrutalistButton
                            text={submitting ? "ÿØÿ± ÿ≠ÿßŸÑ ÿßÿ±ÿ≥ÿßŸÑ..." : "ÿ´ÿ®ÿ™ ŸÇ€åŸÖÿ™‚Äå⁄Øÿ∞ÿßÿ±€å"}
                            color="yellow-400"
                            textColor="black"
                            type="submit"
                            disabled={submitting}
                            className="admin-submit-btn"
                        />

                        {order.status === 'confirmed' && (
                            <NeoBrutalistButton
                                text="ÿ™⁄©ŸÖ€åŸÑ ÿ≥ŸÅÿßÿ±ÿ¥"
                                color="green-400"
                                textColor="black"
                                onClick={handleCompleteOrder}
                                className="complete-order-btn"
                            />
                        )}
                    </div>
                </form>
            </NeoBrutalistCard>

            {/* Success Message */}
            {!error && submitting && (
                <div className="admin-status-message admin-success">
                    <span className="admin-status-icon">‚úÖ</span>
                    <span>ŸÇ€åŸÖÿ™‚Äå⁄Øÿ∞ÿßÿ±€å ÿ®ÿß ŸÖŸàŸÅŸÇ€åÿ™ ÿ´ÿ®ÿ™ ÿ¥ÿØ!</span>
                </div>
            )}
        </div>
    );
};

export default AdminOrderDetailPage;
