// tasks/views/admin/component/PaymentVerificationComponent.js - UPDATED VERSION

import React, { useState, useEffect } from 'react';
import API from '../../../component/api';
import NeoBrutalistButton from "../../../component/NeoBrutalist/NeoBrutalistButton";
import NeoBrutalistCard from "../../../component/NeoBrutalist/NeoBrutalistCard";
import SecureImage from "../../../component/SecureImage"; // Add this import
import '../../../styles/component/AdminComponent/PaymentVerification.css'

const PaymentVerificationComponent = ({ order, onPaymentVerified }) => {
    const [verifying, setVerifying] = useState(false);
    const [paymentNotes, setPaymentNotes] = useState('');
    const [error, setError] = useState('');
    const [paymentReceipts, setPaymentReceipts] = useState([]);
    const [loadingReceipts, setLoadingReceipts] = useState(false);
    const [currentImageIndex, setCurrentImageIndex] = useState(0);
    const [selectedImages, setSelectedImages] = useState([]);
    const [isImageModalOpen, setIsImageModalOpen] = useState(false);

    useEffect(() => {
        if (order && (order.status === 'payment_uploaded' || order.has_payment_receipts)) {
            fetchPaymentReceipts();
        }
    }, [order]);

    const fetchPaymentReceipts = async () => {
        setLoadingReceipts(true);
        try {
            const response = await API.get(`/orders/${order.id}/payment-receipts/`);
            setPaymentReceipts(response.data.receipts || []);
        } catch (err) {
            console.error('‚ùå Error fetching payment receipts:', err);
            setError('ÿÆÿ∑ÿß ÿØÿ± ÿ®ÿßÿ±⁄Ø€åÿ±€å ÿ±ÿ≥€åÿØŸáÿß€å Ÿæÿ±ÿØÿßÿÆÿ™');
        } finally {
            setLoadingReceipts(false);
        }
    };

    const handleVerifyPayment = async (verified) => {
        if (!window.confirm(
            verified
                ? 'ÿ¢€åÿß ŸÖÿ∑ŸÖÿ¶ŸÜ Ÿáÿ≥ÿ™€åÿØ ⁄©Ÿá Ÿæÿ±ÿØÿßÿÆÿ™ ÿ±ÿß ÿ™ÿß€å€åÿØ Ÿà ÿ≥ŸÅÿßÿ±ÿ¥ ÿ±ÿß ÿ™⁄©ŸÖ€åŸÑ ŸÖ€å‚Äå⁄©ŸÜ€åÿØÿü'
                : 'ÿ¢€åÿß ŸÖÿ∑ŸÖÿ¶ŸÜ Ÿáÿ≥ÿ™€åÿØ ⁄©Ÿá ÿ±ÿ≥€åÿØ Ÿæÿ±ÿØÿßÿÆÿ™ ÿ±ÿß ÿ±ÿØ ŸÖ€å‚Äå⁄©ŸÜ€åÿØÿü'
        )) {
            return;
        }

        setVerifying(true);
        setError('');

        try {
            const response = await API.post(`/orders/${order.id}/verify-payment/`, {
                payment_verified: verified,
                payment_notes: paymentNotes
            });

            alert(verified ? 'Ÿæÿ±ÿØÿßÿÆÿ™ ÿ™ÿß€å€åÿØ ÿ¥ÿØ Ÿà ÿ≥ŸÅÿßÿ±ÿ¥ ÿ™⁄©ŸÖ€åŸÑ ⁄Øÿ±ÿØ€åÿØ!' : 'ÿ±ÿ≥€åÿØ Ÿæÿ±ÿØÿßÿÆÿ™ ÿ±ÿØ ÿ¥ÿØ');

            if (onPaymentVerified) {
                onPaymentVerified();
            }

        } catch (err) {
            console.error('‚ùå Payment verification failed:', err);
            const errorMessage = err.response?.data?.error || 'ÿÆÿ∑ÿß ÿØÿ± ÿ™ÿß€å€åÿØ Ÿæÿ±ÿØÿßÿÆÿ™';
            setError(errorMessage);
        } finally {
            setVerifying(false);
        }
    };

    const handleImageClick = (receipts, startIndex = 0) => {
        const images = receipts.filter(receipt => receipt.file_type === 'image');
        if (images.length > 0) {
            setSelectedImages(images);
            setCurrentImageIndex(startIndex);
            setIsImageModalOpen(true);
        }
    };

    const nextImage = () => {
        setCurrentImageIndex((prev) =>
            prev === selectedImages.length - 1 ? 0 : prev + 1
        );
    };

    const prevImage = () => {
        setCurrentImageIndex((prev) =>
            prev === 0 ? selectedImages.length - 1 : prev - 1
        );
    };

    const handleViewPDF = async (receipt) => {
        const newWindow = window.open('', '_blank');
        if (newWindow) {
            newWindow.document.write('ÿØÿ± ÿ≠ÿßŸÑ ÿ®ÿßÿ±⁄Ø€åÿ±€å PDF...');
        }

        try {
            // Use the file_url directly for PDF viewing
            if (newWindow) {
                newWindow.location.href = receipt.file_url;
            } else {
                alert('Pop-up blocker may be preventing the PDF from opening. Please allow pop-ups for this site.');
            }
        } catch (error) {
            console.error('‚ùå Error opening PDF:', error);
            if (newWindow) {
                newWindow.close();
            }
            alert('ÿÆÿ∑ÿß ÿØÿ± ŸÖÿ¥ÿßŸáÿØŸá ŸÅÿß€åŸÑ PDF. ŸÑÿ∑ŸÅÿßŸã ÿØŸàÿ®ÿßÿ±Ÿá ÿ™ŸÑÿßÿ¥ ⁄©ŸÜ€åÿØ.');
        }
    };

    const handleDownloadReceipt = async (receipt) => {
        try {
            const downloadUrl = receipt.download_url;

            if (!downloadUrl) {
                alert('ŸÑ€åŸÜ⁄© ÿØÿßŸÜŸÑŸàÿØ ÿØÿ± ÿØÿ≥ÿ™ÿ±ÿ≥ ŸÜ€åÿ≥ÿ™');
                return;
            }

            // Create a temporary link for download
            const link = document.createElement('a');
            link.href = downloadUrl;
            link.download = receipt.file_name;
            link.style.display = 'none';

            document.body.appendChild(link);
            link.click();
            document.body.removeChild(link);

        } catch (error) {
            console.error('‚ùå Error downloading file:', error);
            alert('ÿÆÿ∑ÿß ÿØÿ± ÿØÿßŸÜŸÑŸàÿØ ŸÅÿß€åŸÑ');
        }
    };

    const formatFileSize = (bytes) => {
        if (bytes === 0) return '0 Bytes';
        const k = 1024;
        const sizes = ['Bytes', 'KB', 'MB', 'GB'];
        const i = Math.floor(Math.log(bytes) / Math.log(k));
        return parseFloat((bytes / Math.pow(k, i)).toFixed(2)) + ' ' + sizes[i];
    };

    return (
        <div className="payment-verification" dir="rtl">
            {error && (
                <div className="verification-error">
                    <span className="error-icon">‚ö†Ô∏è</span>
                    <span>{error}</span>
                </div>
            )}

            {/* Multiple Payment Receipts Support */}
            {paymentReceipts.length > 0 ? (
                <div className="payment-receipts-section">
                    <h3>ÿ±ÿ≥€åÿØŸáÿß€å Ÿæÿ±ÿØÿßÿÆÿ™ ({paymentReceipts.length})</h3>

                    <div className="receipts-summary">
                        <div className="summary-item">
                            <span>ŸÖÿ®ŸÑÿ∫ ÿ≥ŸÅÿßÿ±ÿ¥: {new Intl.NumberFormat('fa-IR').format(order.quoted_total)} ÿ±€åÿßŸÑ</span>
                        </div>
                        <div className="summary-item">
                            <span>ÿ™ÿπÿØÿßÿØ ÿ±ÿ≥€åÿØŸáÿß: {paymentReceipts.length}</span>
                        </div>
                        <div className="summary-item">
                            <span>ÿ™ÿß€å€åÿØ ÿ¥ÿØŸá: {paymentReceipts.filter(r => r.is_verified).length}</span>
                        </div>
                    </div>

                    <div className="receipts-grid">
                        {paymentReceipts.map((receipt, index) => (
                            <NeoBrutalistCard key={receipt.id} className="receipt-card">
                                <div className="receipt-header">
                                    <h4>ÿ±ÿ≥€åÿØ {index + 1}</h4>
                                    <div className="receipt-meta">
                                        <span className="receipt-type">
                                            {receipt.file_type === 'pdf' ? 'üìÑ PDF' : 'üñºÔ∏è ÿ™ÿµŸà€åÿ±'}
                                        </span>
                                        <span className="receipt-size">
                                            {formatFileSize(receipt.file_size)}
                                        </span>
                                        <span className={`receipt-status ${receipt.is_verified ? 'verified' : 'pending'}`}>
                                            {receipt.is_verified ? '‚úÖ ÿ™ÿß€å€åÿØ ÿ¥ÿØŸá' : '‚è≥ ÿØÿ± ÿßŸÜÿ™ÿ∏ÿßÿ± ÿ®ÿ±ÿ±ÿ≥€å'}
                                        </span>
                                    </div>
                                </div>

                                <div className="receipt-info">
                                    <div className="receipt-details">
                                        <div className="detail-item">
                                            <span className="detail-label">ŸÜÿßŸÖ ŸÅÿß€åŸÑ:</span>
                                            <span className="detail-value">{receipt.file_name}</span>
                                        </div>
                                        <div className="detail-item">
                                            <span className="detail-label">ÿ™ÿßÿ±€åÿÆ ÿ¢ŸæŸÑŸàÿØ:</span>
                                            <span className="detail-value">
                                                {new Date(receipt.uploaded_at).toLocaleDateString('fa-IR')}
                                            </span>
                                        </div>
                                        <div className="detail-item">
                                            <span className="detail-label">ÿ¢ŸæŸÑŸàÿØ ÿ¥ÿØŸá ÿ™Ÿàÿ≥ÿ∑:</span>
                                            <span className="detail-value">{receipt.uploaded_by}</span>
                                        </div>
                                        {receipt.admin_notes && (
                                            <div className="detail-item">
                                                <span className="detail-label">ÿ™Ÿàÿ∂€åÿ≠ÿßÿ™ ŸÖÿØ€åÿ±:</span>
                                                <span className="detail-value">{receipt.admin_notes}</span>
                                            </div>
                                        )}
                                    </div>

                                    {/* FIXED: Receipt preview using SecureImage component */}
                                    <div className="receipt-preview">
                                        {receipt.file_type === 'image' ? (
                                            <SecureImage
                                                receiptId={receipt.id}
                                                alt={`ÿ±ÿ≥€åÿØ Ÿæÿ±ÿØÿßÿÆÿ™ ${index + 1}`}
                                                className="receipt-image"
                                                onClick={() => handleImageClick([receipt], 0)}
                                                style={{
                                                    maxWidth: '200px',
                                                    maxHeight: '200px',
                                                    cursor: 'pointer',
                                                    border: '2px solid #000',
                                                    borderRadius: '8px',
                                                    objectFit: 'cover'
                                                }}
                                                onError={(error) => {
                                                    console.error('‚ùå Receipt image error:', error);
                                                }}
                                            />
                                        ) : (
                                            <div className="pdf-preview">
                                                <div style={{
                                                    fontSize: '48px',
                                                    textAlign: 'center',
                                                    padding: '20px',
                                                    background: '#f3f4f6',
                                                    border: '2px solid #000',
                                                    borderRadius: '8px'
                                                }}>
                                                    üìÑ
                                                </div>
                                                <p className="pdf-name">{receipt.file_name}</p>
                                                <NeoBrutalistButton
                                                    text="üîç ŸÖÿ¥ÿßŸáÿØŸá PDF"
                                                    color="blue-400"
                                                    textColor="white"
                                                    onClick={() => handleViewPDF(receipt)}
                                                    className="pdf-view-btn"
                                                />
                                            </div>
                                        )}
                                    </div>
                                </div>

                                {/* Receipt actions */}
                                <div className="receipt-actions">
                                    <NeoBrutalistButton
                                        text="üíæ ÿØÿßŸÜŸÑŸàÿØ"
                                        color="green-400"
                                        textColor="black"
                                        onClick={() => handleDownloadReceipt(receipt)}
                                        className="download-receipt-btn"
                                    />
                                </div>
                            </NeoBrutalistCard>
                        ))}
                    </div>

                    {/* Gallery view for all images */}
                    {paymentReceipts.filter(r => r.file_type === 'image').length > 1 && (
                        <div className="gallery-section">
                            <NeoBrutalistButton
                                text="üñºÔ∏è ŸÖÿ¥ÿßŸáÿØŸá ŸáŸÖŸá ÿ™ÿµÿßŸà€åÿ± ÿØÿ± ⁄ØÿßŸÑÿ±€å"
                                color="purple-400"
                                textColor="white"
                                onClick={() => handleImageClick(paymentReceipts.filter(r => r.file_type === 'image'), 0)}
                                className="gallery-btn"
                            />
                        </div>
                    )}
                </div>
            ) : order.payment_receipt ? (
                // Legacy single payment receipt support
                <div className="payment-receipt-section">
                    <h3>ÿ±ÿ≥€åÿØ ÿ¢ŸæŸÑŸàÿØ ÿ¥ÿØŸá (ŸÇÿØ€åŸÖ€å)</h3>
                    <div className="receipt-info">
                        <div className="receipt-meta">
