import React from "react";
import { useNavigate } from "react-router-dom";
import NeoBrutalistButton from "../component/NeoBrutalist/NeoBrutalistButton.js";
import "../styles/MainPage.css";
import UnicornScene from "unicornstudio-react"; // assumed wrapper component

const MainPage = () => {
    const navigate = useNavigate();

    return (
        <div className="main-container" id="mainPageContainer">
            {/* Background or top full-screen scene */}
            <UnicornScene
                projectId="9LavQqV4nq9siFdVEwmk"
                style={{
                    position: "absolute",
                    top: 0,
                    left: 0,
                    width: "100vw",
                    height: "100vh",
                    zIndex: 0,
                }}
            />

            {/* Your UI on top */}
            <div className="main-box" style={{ position: "relative", zIndex: 1 }}>
                <h2 className="main-title" id="mainTitle">سامانه ثبت سفارش</h2>

                <div className="button-group">
                    <NeoBrutalistButton
                        text="ورود"
                        className="login-btn"
                        color="yellow-400"
                        textColor="black"
                        onClick={() => navigate("/login")}
                    />
                    <NeoBrutalistButton
                        text="ثبت نام"
                        className="register-btn"
                        color="yellow-400"
                        textColor="black"
                        onClick={() => navigate("/register")}
                    />
                </div>
            </div>
        </div>
    );
};

export default MainPage;
