import React, { useState, useEffect } from 'react';
import API from './api';
import NeoBrutalistCard from './NeoBrutalist/NeoBrutalistCard';
import NeoBrutalistButton from './NeoBrutalist/NeoBrutalistButton';
import NeoBrutalistInput from './NeoBrutalist/NeoBrutalistInput';
import '../styles/OrderDetail.css';

const OrderDetailPage = ({ orderId, onOrderUpdated }) => {
    const [order, setOrder] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');
    const [isEditing, setIsEditing] = useState(false);
    const [editedComment, setEditedComment] = useState('');
    const [approvalDecision, setApprovalDecision] = useState('');
    const [rejectionReason, setRejectionReason] = useState('');
    const [submitting, setSubmitting] = useState(false);

    useEffect(() => {
        if (orderId) {
            fetchOrder();
        }
    }, [orderId]);

    const fetchOrder = async () => {
        setLoading(true);
        try {
            const response = await API.get(`/orders/${orderId}/`);
            setOrder(response.data);
            setEditedComment(response.data.customer_comment || '');
        } catch (err) {
            setError('Failed to load order details');
        } finally {
            setLoading(false);
        }
    };

    const handleApprovalSubmit = async () => {
        if (!approvalDecision) {
            setError('Please select approve or reject');
            return;
        }

        if (approvalDecision === 'reject' && !rejectionReason.trim()) {
            setError('Please provide a reason for rejection');
            return;
        }

        setSubmitting(true);
        try {
            if (approvalDecision === 'approve') {
                await API.post(`/orders/${orderId}/approve/`);
            } else {
                await API.post(`/orders/${orderId}/reject/`, {
                    rejection_reason: rejectionReason
                });
            }

            if (onOrderUpdated) {
                onOrderUpdated();
            } else {
                fetchOrder();
            }
        } catch (err) {
            setError(err.response?.data?.error || 'Failed to submit decision');
        } finally {
            setSubmitting(false);
        }
    };

    const getStatusColor = (status) => {
        switch (status) {
            case 'pending_pricing':
                return 'yellow-400';
            case 'waiting_customer_approval':
                return 'blue-400';
            case 'confirmed':
                return 'green-400';
            case 'rejected':
                return 'red-400';
            case 'cancelled':
                return 'gray-400';
            default:
                return 'gray-400';
        }
    };

    const formatStatus = (status) => {
        const statusMap = {
            'pending_pricing': 'Ø¯Ø± Ø§Ù†ØªØ¸Ø§Ø± Ù‚ÛŒÙ…Øªâ€ŒÚ¯Ø°Ø§Ø±ÛŒ',
            'waiting_customer_approval': 'Ø¯Ø± Ø§Ù†ØªØ¸Ø§Ø± ØªØ£ÛŒÛŒØ¯',
            'confirmed': 'ØªØ£ÛŒÛŒØ¯ Ø´Ø¯Ù‡',
            'rejected': 'Ø±Ø¯ Ø´Ø¯Ù‡',
            'cancelled': 'Ù„ØºÙˆ Ø´Ø¯Ù‡'
        };
        return statusMap[status] || status;
    };

    if (loading) {
        return (
            <div className="neo-order-detail">
                <NeoBrutalistCard className="neo-loading-card">
                    <div className="neo-loading-content">
                        <span>ğŸ”„</span>
                        <span className="neo-loading-text">...Ø¯Ø± Ø­Ø§Ù„ Ø¨Ø§Ø±Ú¯Ø°Ø§Ø±ÛŒ Ø¬Ø²Ø¦ÛŒØ§Øª Ø³ÙØ§Ø±Ø´</span>
                    </div>
                </NeoBrutalistCard>
            </div>
        );
    }

    if (error && !order) {
        return (
            <div className="neo-order-detail">
                <NeoBrutalistCard className="neo-error-card">
                    <div className="neo-error-content">
                        <span className="neo-error-icon">âš ï¸</span>
                        <span className="neo-error-text">{error}</span>
                    </div>
                </NeoBrutalistCard>
            </div>
        );
    }

    if (!order) {
        return (
            <div className="neo-order-detail">
                <NeoBrutalistCard className="neo-error-card">
                    <div className="neo-error-content">
                        <span className="neo-error-icon">âŒ</span>
                        <span className="neo-error-text">Ø³ÙØ§Ø±Ø´ Ù¾ÛŒØ¯Ø§ Ù†Ø´Ø¯</span>
                    </div>
                </NeoBrutalistCard>
            </div>
        );
    }

    return (
        <div className="neo-order-detail">
            {/* Header */}
            <div className="neo-order-header">
                <h1 className="neo-order-title">Ø³ÙØ§Ø±Ø´ #{order.id}</h1>
                <NeoBrutalistButton
                    text={formatStatus(order.status)}
                    color={getStatusColor(order.status)}
                    textColor="black"
                    className="neo-status-badge"
                />
            </div>

            {error && (
                <div className="neo-status-message neo-error">
                    <span className="neo-status-icon">âš ï¸</span>
                    <span>{error}</span>
                </div>
            )}

            {/* Order Information */}
            <NeoBrutalistCard className="neo-order-info-card">
                <div className="neo-card-header">
                    <h2 className="neo-card-title">Ø§Ø·Ù„Ø§Ø¹Ø§Øª Ø³ÙØ§Ø±Ø´</h2>
                </div>
                <div className="neo-order-info-grid">
                    <div className="neo-info-item">
                        <span className="neo-info-label">Ù…Ø´ØªØ±ÛŒ</span>
                        <span className="neo-info-value">{order.customer_name}</span>
                    </div>
                    <div className="neo-info-item">
                        <span className="neo-info-label">Ø§ÛŒØ¬Ø§Ø¯ Ø´Ø¯Ù‡: </span>
                        <span className="neo-info-value">
                            {new Date(order.created_at).toLocaleDateString('fa-IR')}
                        </span>
                    </div>
                    <div className="neo-info-item">
                        <span className="neo-info-label">ÙˆØ¶Ø¹ÛŒØª: </span>
                        <span className="neo-info-value">{formatStatus(order.status)}</span>
                    </div>
                    {order.quoted_total > 0 && (
                        <div className="neo-info-item">
                            <span className="neo-info-label">Ù…Ø¨Ù„Øº Ú©Ù„: </span>
                            <span className="neo-info-value neo-payable-amount">
                                ${order.quoted_total}
                            </span>
                        </div>
                    )}
                </div>

                {/* Customer Comment Section */}
                {(order.customer_comment || isEditing) && (
                    <div className="neo-comment-section">
                        <h3 className="neo-comment-title">ØªÙˆØ¶ÛŒØ­Ø§Øª Ù…Ø´ØªØ±ÛŒØ§Ù†</h3>
                        {!isEditing ? (
                            <div className="neo-comment-display">
                                <p className="neo-comment-text">
                                    {order.customer_comment || 'No comments provided'}
                                </p>
                                {order.status === 'pending_pricing' && (
                                    <NeoBrutalistButton
                                        text="ÙˆÛŒØ±Ø§ÛŒØ´ Ù†Ø¸Ø±"
                                        color="blue-400"
                                        textColor="white"
                                        onClick={() => setIsEditing(true)}
                                        className="neo-edit-btn"
                                    />
                                )}
                            </div>
                        ) : (
                            <div className="neo-edit-form">
                                <NeoBrutalistInput
                                    type="text"
                                    value={editedComment}
                                    onChange={(e) => setEditedComment(e.target.value)}
                                    placeholder="Ù†Ø¸Ø±Ø§Øª Ø®ÙˆØ¯ Ø±Ø§ ÙˆØ§Ø±Ø¯ Ú©Ù†ÛŒØ¯..."
                                />
                                <div className="neo-edit-actions">
                                    <NeoBrutalistButton
                                        text="Ø°Ø®ÛŒØ±Ù‡"
                                        color="green-400"
                                        textColor="black"
                                        onClick={() => {
                                            // You can implement comment update API call here
                                            setIsEditing(false);
                                        }}
                                        className="neo-edit-btn"
                                    />
                                    <NeoBrutalistButton
                                        text="Ù„ØºÙˆ"
                                        color="gray-400"
                                        textColor="black"
                                        onClick={() => {
                                            setIsEditing(false);
                                            setEditedComment(order.customer_comment || '');
                                        }}
                                        className="neo-edit-btn"
                                    />
                                </div>
                            </div>
                        )}
                    </div>
                )}
            </NeoBrutalistCard>

            {/* Order Items */}
            <NeoBrutalistCard className="neo-items-card">
                <div className="neo-card-header">
                    <h2 className="neo-card-title">Ø§Ù‚Ù„Ø§Ù…</h2>
                </div>
                <div className="neo-items-table">
                    <div className="neo-table-header">
                        <div>Ù…Ø­ØµÙˆÙ„</div>
                        <div>ØªØ¹Ø¯Ø§Ø¯ Ø¯Ø±Ø®ÙˆØ§Ø³ØªÛŒ </div>
                        <div>ÛŒØ§Ø¯Ø¯Ø§Ø´Øªâ€ŒÙ‡Ø§ÛŒ Ù…Ø´ØªØ±ÛŒ</div>
                        <div>Unit Price</div>
                        <div>Final Qty</div>
                        <div>Total</div>
                    </div>
                    {order.items?.map((item, index) => (
                        <div key={index} className="neo-table-row">
                            <div className="neo-table-cell" data-label="Product">
                                {item.product_name}
                            </div>
                            <div className="neo-table-cell" data-label="Requested">
                                {item.requested_quantity}
                            </div>
                            <div className="neo-table-cell" data-label="Notes">
                                {item.customer_notes || '-'}
                            </div>
                            <div className="neo-table-cell" data-label="Price">
                                {item.quoted_unit_price ? `$${item.quoted_unit_price}` : 'Pending'}
                            </div>
                            <div className="neo-table-cell" data-label="Final Qty">
                                {item.final_quantity || 'Pending'}
                            </div>
                            <div className="neo-table-cell" data-label="Total">
                                {item.quoted_unit_price && item.final_quantity
                                    ? `$${(item.quoted_unit_price * item.final_quantity).toFixed(2)}`
                                    : 'Pending'
                                }
                            </div>
                        </div>
                    ))}
                </div>
            </NeoBrutalistCard>

            {/* Admin Reply */}
            {order.admin_comment && (
                <NeoBrutalistCard className="neo-admin-reply-card">
                    <div className="neo-card-header">
                        <h2 className="neo-card-title">Admin Response</h2>
                    </div>
                    <div className="neo-admin-info-grid">
                        <div className="neo-info-item">
                            <span className="neo-info-label">Pricing Date</span>
                            <span className="neo-info-value">
                                {order.pricing_date
                                    ? new Date(order.pricing_date).toLocaleDateString('fa-IR')
                                    : 'Not yet priced'
                                }
                            </span>
                        </div>
                        <div className="neo-info-item">
                            <span className="neo-info-label">Admin Notes</span>
                            <span className="neo-info-value">{order.admin_comment}</span>
                        </div>
                    </div>
                </NeoBrutalistCard>
            )}

            {/* Customer Approval Section */}
            {order.status === 'waiting_customer_approval' && (
                <NeoBrutalistCard className="neo-approval-section">
                    <h2 className="neo-approval-title">Order Approval Required</h2>
                    <p>Please review the pricing and decide whether to approve or reject this order.</p>

                    <div className="neo-approval-form">
                        <div className="neo-radio-group">
                            <label className="neo-radio-label">
                                <input
                                    type="radio"
                                    name="approval"
                                    value="approve"
                                    checked={approvalDecision === 'approve'}
                                    onChange={(e) => setApprovalDecision(e.target.value)}
                                    className="neo-radio-input"
                                />
                                <span className="neo-radio-text">Approve Order</span>
                            </label>
                            <label className="neo-radio-label">
                                <input
                                    type="radio"
                                    name="approval"
                                    value="reject"
                                    checked={approvalDecision === 'reject'}
                                    onChange={(e) => setApprovalDecision(e.target.value)}
                                    className="neo-radio-input"
                                />
                                <span className="neo-radio-text">Reject Order</span>
                            </label>
                        </div>

                        {approvalDecision === 'reject' && (
                            <div className="neo-rejection-reason">
                                <NeoBrutalistInput
                                    type="text"
                                    placeholder="Please provide reason for rejection..."
                                    value={rejectionReason}
                                    onChange={(e) => setRejectionReason(e.target.value)}
                                />
                            </div>
                        )}

                        <div className="neo-approval-actions">
                            <NeoBrutalistButton
                                text={submitting ? "Submitting..." : "Submit Decision"}
                                color="yellow-400"
                                textColor="black"
                                onClick={handleApprovalSubmit}
                                disabled={submitting || !approvalDecision}
                                className="neo-submit-decision-btn"
                            />
                        </div>
                    </div>
                </NeoBrutalistCard>
            )}

            {/* Order Status Messages */}
            {order.status === 'confirmed' && (
                <div className="neo-status-message neo-success">
                    <span className="neo-status-icon">âœ…</span>
                    <span>Order confirmed successfully! Your order is now being processed.</span>
                </div>
            )}

            {order.status === 'rejected' && order.customer_rejection_reason && (
                <NeoBrutalistCard className="neo-admin-reply-card">
                    <div className="neo-card-header">
                        <h2 className="neo-card-title">Rejection Reason</h2>
                    </div>
                    <p className="neo-comment-text">{order.customer_rejection_reason}</p>
                </NeoBrutalistCard>
            )}
        </div>
    );
};

export default OrderDetailPage;
