import React from 'react';
import '../../styles/NeoBrutalistCSS/toggle.css';

const NeoBrutalistToggle = ({
                                checked,
                                onChange,
                                label,
                                className = '',
                                disabled = false,
                                color = 'green',
                                ...props
                            }) => {
    const uniqueId = React.useId();

    const toggleClasses = [
        'neo-brutalist-toggle',
        color,
        disabled && 'disabled',
        className
    ].filter(Boolean).join(' ');

    const handleClick = (e) => {
        // Prevent form submission and page refresh
        e.preventDefault();
        e.stopPropagation();

        if (!disabled && onChange) {
            // Create a synthetic event that mimics checkbox behavior
            const syntheticEvent = {
                target: {
                    checked: !checked,
                    name: props.name || '',
                    type: 'checkbox'
                },
                preventDefault: () => {},
                stopPropagation: () => {}
            };
            onChange(syntheticEvent);
        }
    };

    const handleKeyDown = (e) => {
        // Handle keyboard accessibility (Space and Enter)
        if (e.key === ' ' || e.key === 'Enter') {
            e.preventDefault();
            handleClick(e);
        }
    };

    return (
        <div className={toggleClasses}>
            <input
                id={uniqueId}
                type="checkbox"
                className="toggle-input"
                checked={checked}
                onChange={() => {}} // Controlled by click handler
                disabled={disabled}
                tabIndex={-1} // Remove from tab order, label will handle focus
                {...props}
            />
            <div
                className="toggle-track"
                onClick={handleClick}
                onKeyDown={handleKeyDown}
                tabIndex={disabled ? -1 : 0}
                role="switch"
                aria-checked={checked}
                aria-labelledby={label ? `${uniqueId}-label` : undefined}
            >
                <div className="toggle-knob"></div>
            </div>
            {label && (
                <label
                    id={`${uniqueId}-label`}
                    htmlFor={uniqueId}
                    className="toggle-label"
                    onClick={handleClick}
                >
                    {label}
                </label>
            )}
        </div>
    );
};

export default NeoBrutalistToggle;
