import React, { useEffect } from "react";
import { useNavigate } from "react-router-dom";
import NeoBrutalistButton from "../component/NeoBrutalist/NeoBrutalistButton.js";
import UnicornStudio from "unicorn-studio"; // or unicornstudio-react
import "../styles/MainPage.css";

const MainPage = () => {
    const navigate = useNavigate();

    useEffect(() => {
        UnicornStudio.init().then((scenes) => {
            console.log("Unicorn scene ready:", scenes);
        });
    }, []);

    return (
        <div className="page-wrapper">
            {/* FULLSCREEN BACKGROUND SCENE */}
            <div
                id="unicorn"
                data-us-project="9LavQqV4nq9siFdVEwmk"
                className="unicorn-bg"
            ></div>

            {/* CONTENT OVERLAY */}
            <div className="main-container" id="mainPageContainer">
                <div className="main-box">
                    <h2 className="main-title" id="mainTitle">سامانه ثبت سفارش</h2>
                    <div className="button-group">
                        <NeoBrutalistButton
                            text="ورود"
                            className="login-btn"
                            color="yellow-400"
                            textColor="black"
                            onClick={() => navigate("/login")}
                        />
                        <NeoBrutalistButton
                            text="ثبت نام"
                            className="register-btn"
                            color="yellow-400"
                            textColor="black"
                            onClick={() => navigate("/register")}
                        />
                    </div>
                </div>
            </div>
        </div>
    );
};

export default MainPage;
