import React, { useState, useEffect } from 'react';
import API from './api';
import NeoBrutalistInput from './NeoBrutalist/NeoBrutalistInput';
import NeoBrutalistButton from './NeoBrutalist/NeoBrutalistButton';
import NeoBrutalistCard from './NeoBrutalist/NeoBrutalistCard';
import NeoBrutalistDropdown from './NeoBrutalist/NeoBrutalistDropdown';
import '../styles/component/CustomerComponent/CreateOrder.css';

const CreateOrderPage = ({ onOrderCreated }) => {
    const [products, setProducts] = useState([]);
    const [customerComment, setCustomerComment] = useState('');
    const [orderItems, setOrderItems] = useState([{ product: '', requested_quantity: 1, customer_notes: '' }]);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState('');

    // NEW: Business invoice type state
    const [businessInvoiceType, setBusinessInvoiceType] = useState('unofficial');

    useEffect(() => {
        fetchProducts();
    }, []);

    const fetchProducts = async () => {
        try {
            const response = await API.get('/products/');
            setProducts(response.data);
        } catch (err) {
            setError('Failed to load products');
        }
    };

    const addOrderItem = () => {
        setOrderItems([...orderItems, { product: '', requested_quantity: 1, customer_notes: '' }]);
    };

    const removeOrderItem = (index) => {
        if (orderItems.length > 1) {
            setOrderItems(orderItems.filter((_, i) => i !== index));
        }
    };

    const updateOrderItem = (index, field, value) => {
        const updatedItems = orderItems.map((item, i) =>
            i === index ? { ...item, [field]: value } : item
        );
        setOrderItems(updatedItems);
    };

    const getUserInfo = () => {
        const userDataString = localStorage.getItem('userData');
        return userDataString ? JSON.parse(userDataString) : null;
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setLoading(true);
        setError('');

        try {
            // Validate items
            const validItems = orderItems.filter(item =>
                item.product && item.requested_quantity > 0
            );

            if (validItems.length === 0) {
                throw new Error('Please add at least one item to your order');
            }

            const orderData = {
                customer_comment: customerComment,
                business_invoice_type: businessInvoiceType,
                items: validItems.map(item => ({
                    product: parseInt(item.product),
                    requested_quantity: parseInt(item.requested_quantity),
                    customer_notes: item.customer_notes || ''
                }))
            };

            console.log('üì§ Submitting order with business invoice type:', orderData);

            const response = await API.post('orders/', orderData);

            if (response.status === 201) {
                console.log('‚úÖ Order created successfully:', response.data);
                if (onOrderCreated) {
                    onOrderCreated();
                }
            }
        } catch (err) {
            console.error('‚ùå Order creation failed:', err);
            setError(err.response?.data?.error || err.message || 'Failed to create order');
        } finally {
            setLoading(false);
        }
    };

    const productOptions = products.map(product => ({
        value: product.id.toString(),
        label: `${product.name} - $${product.base_price}`
    }));

    const userInfo = getUserInfo();

    return (
        <div className="neo-create-order">
            <div className="neo-create-order-header">
                <h1 className="neo-create-order-title">ÿ´ÿ®ÿ™ ÿ≥ŸÅÿßÿ±ÿ¥ ÿ¨ÿØ€åÿØ</h1>
                {userInfo && (
                    <div className="neo-customer-info">
                        <span className="neo-customer-label">ŸÖÿ¥ÿ™ÿ±€å</span>
                        <span className="neo-customer-name">{userInfo.name}</span>
                    </div>
                )}
            </div>

            {error && (
                <NeoBrutalistCard className="neo-error-card">
                    <div className="neo-error-content">
                        <span className="neo-error-icon">‚ö†Ô∏è</span>
                        <span className="neo-error-text">{error}</span>
                    </div>
                </NeoBrutalistCard>
            )}

            <form className="neo-order-form" onSubmit={handleSubmit}>
                {/* NEW: Business Invoice Type Selection */}
                <NeoBrutalistCard className="neo-invoice-type-section">
                    <h3>ŸÜŸàÿπ ŸÅÿß⁄©ÿ™Ÿàÿ± ŸÖŸàÿ±ÿØ ŸÜ€åÿßÿ≤</h3>
                    <div className="neo-invoice-type-selection">
                        <div className="neo-radio-group">
                            <label className="neo-radio-label">
                                <input
                                    type="radio"
                                    name="business_invoice_type"
                                    value="unofficial"
                                    checked={businessInvoiceType === 'unofficial'}
                                    onChange={(e) => setBusinessInvoiceType(e.target.value)}
                                    className="neo-radio-input"
                                />
                                <span className="neo-radio-text">
                                    <strong>ŸÅÿß⁄©ÿ™Ÿàÿ± ÿ∫€åÿ±ÿ±ÿ≥ŸÖ€å</strong>
                                    <br />
                                    <small>ÿ®ÿØŸàŸÜ ŸÖÿßŸÑ€åÿßÿ™ - ÿ®ÿ±ÿß€å ÿÆÿ±€åÿØŸáÿß€å ÿ¥ÿÆÿµ€å</small>
                                </span>
                            </label>
                            <label className="neo-radio-label">
                                <input
                                    type="radio"
                                    name="business_invoice_type"
                                    value="official"
                                    checked={businessInvoiceType === 'official'}
                                    onChange={(e) => setBusinessInvoiceType(e.target.value)}
                                    className="neo-radio-input"
                                />
                                <span className="neo-radio-text">
                                    <strong>ŸÅÿß⁄©ÿ™Ÿàÿ± ÿ±ÿ≥ŸÖ€å</strong>
                                    <br />
                                    <small>ÿ®ÿß ŸÖÿßŸÑ€åÿßÿ™ - ÿ®ÿ±ÿß€å ÿ´ÿ®ÿ™ ÿØÿ± ÿ≠ÿ≥ÿßÿ®ÿØÿßÿ±€å ÿ¥ÿ±⁄©ÿ™</small>
                                </span>
                            </label>
                        </div>
                    </div>
                </NeoBrutalistCard>

                {order.is_official_invoice && (
                    <NeoBrutalistCard className="neo-customer-invoice-info-card">
                        <div className="neo-card-header">
                            <h2 className="neo-card-title">ÿßÿ∑ŸÑÿßÿπÿßÿ™ ŸÅÿß⁄©ÿ™Ÿàÿ± ÿ±ÿ≥ŸÖ€å</h2>
                        </div>
                        <div className="neo-customer-invoice-info">
                            <div className="neo-info-grid">
                                <div className="neo-info-item">
                                    <span className="neo-info-label">ÿ¥ŸÜÿßÿ≥Ÿá ŸÖŸÑ€å:</span>
                                    <span className="neo-info-value">{order.customer_national_id || 'ÿ´ÿ®ÿ™ ŸÜÿ¥ÿØŸá'}</span>
                                </div>
                                <div className="neo-info-item">
                                    <span className="neo-info-label">ÿ¥ŸÜÿßÿ≥Ÿá ÿßŸÇÿ™ÿµÿßÿØ€å:</span>
                                    <span className="neo-info-value">{order.customer_economic_id || 'ÿ´ÿ®ÿ™ ŸÜÿ¥ÿØŸá'}</span>
                                </div>
                                <div className="neo-info-item">
                                    <span className="neo-info-label">⁄©ÿØ Ÿæÿ≥ÿ™€å:</span>
                                    <span className="neo-info-value">{order.customer_postal_code || 'ÿ´ÿ®ÿ™ ŸÜÿ¥ÿØŸá'}</span>
                                </div>
                                <div className="neo-info-item full-width">
                                    <span className="neo-info-label">ÿ¢ÿØÿ±ÿ≥ ⁄©ÿßŸÖŸÑ:</span>
                                    <span className="neo-info-value">{order.customer_complete_address || 'ÿ´ÿ®ÿ™ ŸÜÿ¥ÿØŸá'}</span>
                                </div>
                            </div>
                        </div>
                    </NeoBrutalistCard>
                )}

                <NeoBrutalistCard className="neo-comment-section">
                    <h3>ÿ¨ÿ≤ÿ¶€åÿßÿ™ ÿ≥ŸÅÿßÿ±ÿ¥</h3>
                    <NeoBrutalistInput
                        className="neo-comment-input"
                        type="text"
                        placeholder="...ÿßŸÑÿ≤ÿßŸÖÿßÿ™ €åÿß €åÿßÿØÿØÿßÿ¥ÿ™‚ÄåŸáÿß€å Ÿà€å⁄òŸá ÿ®ÿ±ÿß€å ÿß€åŸÜ ÿ≥ŸÅÿßÿ±ÿ™"
                        value={customerComment}
                        onChange={(e) => setCustomerComment(e.target.value)}
                    />
                </NeoBrutalistCard>

                <div className="neo-items-section">
                    <div className="neo-section-header">
                        <h2 className="neo-section-title">Order Items</h2>
                        <NeoBrutalistButton
                            text="+ ÿßŸÅÿ≤ŸàÿØŸÜ ŸÖÿ≠ÿµŸàŸÑ"
                            color="green-400"
                            textColor="black"
                            onClick={addOrderItem}
                            type="button"
                            className="neo-add-item-btn"
                        />
                    </div>

                    <div className="neo-items-list">
                        {orderItems.map((item, index) => (
                            <NeoBrutalistCard key={index} className="neo-item-card">
                                <div className="neo-item-header">
                                    <h3 className="neo-item-number">Item #{index + 1}</h3>
                                    {orderItems.length > 1 && (
                                        <NeoBrutalistButton
                                            text="ÿ≠ÿ∞ŸÅ"
                                            color="red-400"
                                            textColor="white"
                                            onClick={() => removeOrderItem(index)}
                                            type="button"
                                            className="neo-remove-btn"
                                        />
                                    )}
                                </div>

                                <div className="neo-item-fields">
                                    <div className="neo-field-group">
                                        <NeoBrutalistDropdown
                                            label="ŸÖÿ≠ÿµŸàŸÑ"
                                            options={productOptions}
                                            value={item.product}
                                            onChange={(value) => updateOrderItem(index, 'product', value)}
                                            placeholder="...ŸÖÿ≠ÿµŸàŸÑ ŸÖŸàÿ±ÿØ ŸÜÿ∏ÿ± ÿ±ÿß ÿßŸÜÿ™ÿÆÿßÿ® ⁄©ŸÜ€åÿØ"
                                            required
                                        />
                                    </div>

                                    <div className="neo-field-group">
                                        <NeoBrutalistInput
                                            className="neo-comment-input"
                                            type="number"
                                            placeholder="ÿ™ÿπÿØÿßÿØ"
                                            value={item.requested_quantity}
                                            onChange={(e) => updateOrderItem(index, 'requested_quantity', e.target.value)}
                                            min="1"
                                            required
                                        />
                                    </div>

                                    <div className="neo-field-group">
                                        <NeoBrutalistInput
                                            className="neo-comment-input"
                                            type="text"
                                            placeholder="Ÿàÿ≤ŸÜ ÿØÿ±ÿÆŸàÿßÿ≥ÿ™€å"
                                            value={item.customer_notes}
                                            onChange={(e) => updateOrderItem(index, 'customer_notes', e.target.value)}
                                        />
                                    </div>
                                </div>
                            </NeoBrutalistCard>
                        ))}
                    </div>
                </div>

                <div className="neo-form-actions">
                    <NeoBrutalistButton
                        text={loading ? "... ÿØÿ± ÿ≠ÿßŸÑ ÿß€åÿ¨ÿßÿØ" : "ÿ´ÿ®ÿ™ ÿ≥ŸÅÿßÿ±ÿ¥"}
                        color="yellow-400"
                        textColor="black"
                        type="submit"
                        disabled={loading}
                        className="neo-submit-btn"
                    />
                </div>
            </form>
        </div>
    );
};

export default CreateOrderPage;
