import React, { useRef, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import * as THREE from "three";
import "../styles/MainPage.css";

const MainPage = () => {
    const navigate = useNavigate();
    const canvasRef = useRef(null);
    const mouseRef = useRef({ x: 0, y: 0 });

    useEffect(() => {
        const canvas = canvasRef.current;
        if (!canvas) return;

        // Scene setup
        const scene = new THREE.Scene();
        const camera = new THREE.PerspectiveCamera(
            75,
            window.innerWidth / window.innerHeight,
            1,
            1000
        );
        camera.position.z = 100;

        const renderer = new THREE.WebGLRenderer({
            canvas,
            alpha: true,
            antialias: true
        });
        renderer.setSize(window.innerWidth, window.innerHeight);
        renderer.setPixelRatio(window.devicePixelRatio);

        // Create particle system
        const particleCount = 150;
        const particles = [];
        const connections = [];

        // Particle geometry and material
        const particleGeometry = new THREE.SphereGeometry(0.3, 8, 8);
        const particleMaterial = new THREE.MeshBasicMaterial({
            color: 0x00d4ff,
            transparent: true,
            opacity: 0.8
        });

        // Create particles
        for (let i = 0; i < particleCount; i++) {
            const particle = new THREE.Mesh(particleGeometry, particleMaterial);

            particle.position.x = THREE.MathUtils.randFloatSpread(200);
            particle.position.y = THREE.MathUtils.randFloatSpread(200);
            particle.position.z = THREE.MathUtils.randFloatSpread(200);

            particle.velocity = new THREE.Vector3(
                (Math.random() - 0.5) * 0.02,
                (Math.random() - 0.5) * 0.02,
                (Math.random() - 0.5) * 0.02
            );

            particles.push(particle);
            scene.add(particle);
        }

        // Create connection lines
        const lineGeometry = new THREE.BufferGeometry();
        const lineMaterial = new THREE.LineBasicMaterial({
            color: 0x00d4ff,
            transparent: true,
            opacity: 0.3
        });

        const positions = new Float32Array(particleCount * particleCount * 6);
        lineGeometry.setAttribute('position', new THREE.BufferAttribute(positions, 3));

        const lines = new THREE.LineSegments(lineGeometry, lineMaterial);
        scene.add(lines);

        // Mouse interaction
        const handleMouseMove = (event) => {
            mouseRef.current.x = (event.clientX / window.innerWidth) * 2 - 1;
            mouseRef.current.y = -(event.clientY / window.innerHeight) * 2 + 1;
        };

        window.addEventListener('mousemove', handleMouseMove);

        // Animation loop
        let animationId;
        const animate = () => {
            animationId = requestAnimationFrame(animate);

            // Update particles
            particles.forEach((particle, i) => {
                // Move particles
                particle.position.add(particle.velocity);

                // Boundary check
                if (Math.abs(particle.position.x) > 100) particle.velocity.x *= -1;
                if (Math.abs(particle.position.y) > 100) particle.velocity.y *= -1;
                if (Math.abs(particle.position.z) > 100) particle.velocity.z *= -1;

                // Mouse interaction
                const mouseInfluence = new THREE.Vector3(
                    mouseRef.current.x * 50,
                    mouseRef.current.y * 50,
                    0
                );

                const distance = particle.position.distanceTo(mouseInfluence);
                if (distance < 30) {
                    const force = mouseInfluence.clone().sub(particle.position).normalize().multiplyScalar(0.001);
                    particle.velocity.add(force);
                }

                // Color animation
                const time = Date.now() * 0.001;
                const hue = (time + i * 0.1) % 1;
                particle.material.color.setHSL(hue * 0.1 + 0.5, 0.8, 0.6);
            });

            // Update connections
            const positions = lines.geometry.attributes.position.array;
            let connectionIndex = 0;

            for (let i = 0; i < particles.length; i++) {
                for (let j = i + 1; j < particles.length; j++) {
                    const distance = particles[i].position.distanceTo(particles[j].position);

                    if (distance < 25) {
                        positions[connectionIndex] = particles[i].position.x;
                        positions[connectionIndex + 1] = particles[i].position.y;
                        positions[connectionIndex + 2] = particles[i].position.z;

                        positions[connectionIndex + 3] = particles[j].position.x;
                        positions[connectionIndex + 4] = particles[j].position.y;
                        positions[connectionIndex + 5] = particles[j].position.z;

                        connectionIndex += 6;
                    }
                }
            }

            // Clear unused positions
            for (let i = connectionIndex; i < positions.length; i++) {
                positions[i] = 0;
            }

            lines.geometry.attributes.position.needsUpdate = true;
            lines.geometry.setDrawRange(0, connectionIndex / 3);

            // Rotate the entire system slowly
            scene.rotation.y += 0.0005;
            scene.rotation.x += 0.0002;

            renderer.render(scene, camera);
        };

        animate();

        // Handle resize
        const handleResize = () => {
            camera.aspect = window.innerWidth / window.innerHeight;
            camera.updateProjectionMatrix();
            renderer.setSize(window.innerWidth, window.innerHeight);
        };
        window.addEventListener("resize", handleResize);

        // Cleanup
        return () => {
            cancelAnimationFrame(animationId);
            window.removeEventListener("resize", handleResize);
            window.removeEventListener("mousemove", handleMouseMove);

            particles.forEach(particle => {
                scene.remove(particle);
                particle.geometry.dispose();
                particle.material.dispose();
            });

            scene.remove(lines);
            lineGeometry.dispose();
            lineMaterial.dispose();
            renderer.dispose();
        };
    }, []);

    return (
        <div className="main-container">
            <canvas
                ref={canvasRef}
                className="canvas-background"
            />

            <div className="content-overlay">
                <div className="hero-section">
                    <div className="hero-content">
                        <h1 className="hero-title">
                            <span className="title-gradient">Junie AI</span>
                        </h1>
                        <p className="hero-subtitle">
                            Experience the future of artificial intelligence with our cutting-edge neural network technology
                        </p>
                        <div className="hero-buttons">
                            <button
                                className="btn btn-primary"
                                onClick={() => navigate("/login")}
                            >
                                <span>Get Started</span>
                                <div className="btn-effect"></div>
                            </button>
                            <button
                                className="btn btn-secondary"
                                onClick={() => navigate("/register")}
                            >
                                <span>Learn More</span>
                                <div className="btn-effect"></div>
                            </button>
                        </div>
                    </div>
                </div>

                <div className="features-preview">
                    <div className="feature-card">
                        <div className="feature-icon">ðŸ§ </div>
                        <h3>Neural Networks</h3>
                        <p>Advanced AI processing</p>
                    </div>
                    <div className="feature-card">
                        <div className="feature-icon">âš¡</div>
                        <h3>Real-time Analysis</h3>
                        <p>Instant data processing</p>
                    </div>
                    <div className="feature-card">
                        <div className="feature-icon">ðŸš€</div>
                        <h3>Scalable Solutions</h3>
                        <p>Enterprise-ready platform</p>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default MainPage;
