import React, { useRef, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

const ComponentRegistry = {
    Button: {
        template: (props = {}) => `
      <button class="neo-brutalist bg-${props.color || 'yellow-400'} text-${props.textColor || 'black'} text-lg font-bold py-3 px-6 hover:bg-${props.color || 'yellow-400'}-600 transition-none border-4 border-black">
        ${props.text || 'Click Me'}
      </button>
    `,
        init: (element, props) => {
            element.innerHTML = ComponentRegistry.Button.template(props);
        }
    }
};

// React wrapper for the ComponentRegistry.Button
const ComponentRegistryButton = ({ text, color, textColor, onClick }) => {
    const ref = useRef(null);

    useEffect(() => {
        if (ref.current) {
            ComponentRegistry.Button.init(ref.current, { text, color, textColor });
            if (onClick) {
                const btn = ref.current.querySelector('button');
                if (btn) btn.onclick = onClick;
            }
        }
    }, [text, color, textColor, onClick]);

    return <div ref={ref} />;
};

const MainPage = () => {
    const navigate = useNavigate();

    return (
        <div className="main-container text-center p-8 space-y-6">
            <h2 className="text-4xl font-bold">Welcome</h2>
            <div className="flex flex-col md:flex-row justify-center items-center gap-6">
                <ComponentRegistryButton
                    text="Login"
                    color="green-400"
                    textColor="black"
                    onClick={() => navigate('/login')}
                />
                <ComponentRegistryButton
                    text="Register"
                    color="red-400"
                    textColor="black"
                    onClick={() => navigate('/register')}
                />
            </div>
        </div>
    );
};

export default MainPage;
