import React, { useRef, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import * as THREE from "three";
import "./css.css"; // Import the provided CSS file

const MainPage = () => {
    const navigate = useNavigate();
    const canvasRef = useRef(null);

    useEffect(() => {
        const canvas = canvasRef.current;
        if (!canvas) return;

        // Three.js setup
        const renderer = new THREE.WebGLRenderer({ canvas, alpha: true });
        renderer.setSize(window.innerWidth, window.innerHeight);
        renderer.setPixelRatio(window.devicePixelRatio);

        const scene = new THREE.Scene();
        const camera = new THREE.OrthographicCamera(0, 1, 1, 0, 0, 1);

        // Uniforms
        const uniforms = {
            iTime: { value: 0 },
            iResolution: { value: new THREE.Vector2(window.innerWidth, window.innerHeight) },
            iMouse: { value: new THREE.Vector4(0, 0, 0, 0) },
            iClickPositions: { value: [] },
            iClickTimes: { value: [] },
            iClickCount: { value: 0 }
        };

        const maxClicks = 16;
        for (let i = 0; i < maxClicks; i++) {
            uniforms.iClickPositions.value.push(new THREE.Vector2(0, 0));
            uniforms.iClickTimes.value.push(0);
        }

        // Vertex shader
        const vertexShader = `
            void main() {
                gl_Position = vec4(position, 1.0);
            }
        `;

        // Fragment shader (from aliali.js)
        const fragmentShader = `
            precision highp float;
            uniform float iTime;
            uniform vec2 iResolution;
            uniform vec4 iMouse;
            uniform vec2 iClickPositions[16];
            uniform float iClickTimes[16];
            uniform int iClickCount;

            float random(vec2 st) {
                return fract(sin(dot(st.xy, vec2(12.9898, 78.233))) * 43758.5453123);
            }

            float noise(vec2 st) {
                vec2 i = floor(st);
                vec2 f = fract(st);
                vec2 u = f * f * (3.0 - 2.0 * f);
                return mix(
                    mix(random(i + vec2(0.0, 0.0)), random(i + vec2(1.0, 0.0)), u.x),
                    mix(random(i + vec2(0.0, 1.0)), random(i + vec2(1.0, 1.0)), u.x),
                    u.y
                );
            }

            float getBayer8x8(vec2 pos) {
                int x = int(mod(pos.x, 8.0));
                int y = int(mod(pos.y, 8.0));
                float[64] bayer = float[64](
                    0.0, 48.0, 12.0, 60.0, 3.0, 51.0, 15.0, 63.0,
                    32.0, 16.0, 44.0, 28.0, 35.0, 19.0, 47.0, 31.0,
                    8.0, 56.0, 4.0, 52.0, 11.0, 59.0, 7.0, 55.0,
                    40.0, 24.0, 36.0, 20.0, 43.0, 27.0, 39.0, 23.0,
                    2.0, 50.0, 14.0, 62.0, 1.0, 49.0, 13.0, 61.0,
                    34.0, 18.0, 46.0, 30.0, 33.0, 17.0, 45.0, 29.0,
                    10.0, 58.0, 6.0, 54.0, 9.0, 57.0, 5.0, 53.0,
                    42.0, 26.0, 38.0, 22.0, 41.0, 25.0, 37.0, 21.0
                );
                return bayer[y * 8 + x] / 64.0;
            }

            float getNormalizedBayer8x8(vec2 pos) {
                return getBayer8x8(pos);
            }

            void main() {
                vec2 uv = gl_FragCoord.xy / iResolution.xy;
                vec2 centerOffset = vec2(iResolution.x / iResolution.y * 0.5, 0.5);
                float targetHeight = 1080.0;
                float targetRes = iResolution.y / targetHeight;
                vec2 scaledCoord = gl_FragCoord.xy / targetRes;

                float noiseScale = 5.0;
                float baseNoiseValue = noise(uv * noiseScale);
                baseNoiseValue = baseNoiseValue * 0.5 + 0.5;

                float waveDuration = 3.5;
                float waveSpeed = 2.0;
                float waveFrequency = 20.0;
                float waveFalloff = 5.0;
                float waveAmplitude = 0.3;
                float totalWaveEffect = 0.0;

                for (int i = 0; i < iClickCount; ++i) {
                    float clickTime = iClickTimes[i];
                    vec2 clickPos = iClickPositions[i];
                    vec2 clickUV = (clickPos - centerOffset * iResolution.y) / (iResolution.y / targetHeight);
                    clickUV /= targetRes;
                    float timeSinceClick = iTime - clickTime;
                    if (timeSinceClick >= 0.0 && timeSinceClick < waveDuration) {
                        float distToClick = length(uv - clickUV);
                        float wave = sin(distToClick * waveFrequency - timeSinceClick * waveSpeed);
                        wave = wave * 0.5 + 0.5;
                        float temporalFalloff = smoothstep(waveDuration, 0.0, timeSinceClick);
                        float spatialFalloff = exp(-distToClick * waveFalloff);
                        wave *= spatialFalloff * temporalFalloff;
                        wave = pow(wave, 2.5);
                        totalWaveEffect += wave * waveAmplitude;
                    }
                }

                float contrast = 1.0;
                float brightness = 0.0;
                float spread = 0.3;
                float finalNoise = baseNoiseValue + totalWaveEffect;
                finalNoise = (finalNoise - 0.5) * contrast + 0.5 + brightness;
                finalNoise += totalWaveEffect * 0.5;
                finalNoise = clamp(finalNoise, 0.0, 1.0);

                vec3 baseColor = vec3(finalNoise);
                float bayerValue = getNormalizedBayer8x8(scaledCoord);
                float ditherOffset = spread * (bayerValue - 0.5);
                vec3 ditheredColor = baseColor + ditherOffset;
                ditheredColor = clamp(ditheredColor, 0.0, 1.0);

                vec3 colorFirst = vec3(0.0);
                vec3 colorSecond = vec3(0.0, 1.0, 0.0);
                float distSqBlack = dot(ditheredColor, ditheredColor);
                float distSqGreen = dot(ditheredColor - 1.0, ditheredColor - 1.0);
                float ratio = distSqBlack / (distSqBlack + distSqGreen);
                float blendFactor = 1.0 / (1.0 + exp(-(ratio - 0.5) * 9000.0));
                vec3 finalColor = mix(colorFirst, colorSecond, blendFactor);

                gl_FragColor = vec4(finalColor, 1.0);
            }
        `;

        // Create material and mesh
        const material = new THREE.ShaderMaterial({
            vertexShader,
            fragmentShader,
            uniforms
        });
        const geometry = new THREE.PlaneGeometry(1, 1);
        const mesh = new THREE.Mesh(geometry, material);
        scene.add(mesh);

        // Mouse and click handling
        let clickCount = 0;

        const handleMouseMove = (e) => {
            uniforms.iMouse.value.set(e.clientX, window.innerHeight - e.clientY, 0, 0);
        };

        const handleClick = (e) => {
            const currentTime = uniforms.iTime.value;
            let newClickCount = 0;
            const newPositions = [];
            const newTimes = [];

            for (let i = 0; i < clickCount; i++) {
                if (currentTime - uniforms.iClickTimes.value[i] < 3.5) {
                    newPositions.push(uniforms.iClickPositions.value[i].clone());
                    newTimes.push(uniforms.iClickTimes.value[i]);
                    newClickCount++;
                }
            }

            if (newClickCount < maxClicks) {
                newPositions.push(new THREE.Vector2(e.clientX, window.innerHeight - e.clientY));
                newTimes.push(currentTime);
                newClickCount++;
            } else {
                let oldestIndex = 0;
                let oldestTime = newTimes[0];
                for (let i = 1; i < newClickCount; i++) {
                    if (newTimes[i] < oldestTime) {
                        oldestTime = newTimes[i];
                        oldestIndex = i;
                    }
                }
                newPositions[oldestIndex].set(e.clientX, window.innerHeight - e.clientY);
                newTimes[oldestIndex] = currentTime;
            }

            for (let i = 0; i < maxClicks; i++) {
                if (i < newClickCount) {
                    uniforms.iClickPositions.value[i].copy(newPositions[i]);
                    uniforms.iClickTimes.value[i] = newTimes[i];
                } else {
                    uniforms.iClickPositions.value[i].set(0, 0);
                    uniforms.iClickTimes.value[i] = 0;
                }
            }
            clickCount = newClickCount;
            uniforms.iClickCount.value = clickCount;
        };

        const handleResize = () => {
            const width = window.innerWidth;
            const height = window.innerHeight;
            canvas.width = width;
            canvas.height = height;
            renderer.setSize(width, height);
            uniforms.iResolution.value.set(width, height);
        };

        canvas.addEventListener('mousemove', handleMouseMove);
        canvas.addEventListener('click', handleClick);
        window.addEventListener('resize', handleResize);

        // Initial resize to ensure correct canvas size
        handleResize();

        // Animation loop
        let startTime = performance.now();
        function animate(time) {
            uniforms.iTime.value = (time - startTime) / 1000;
            renderer.render(scene, camera);
            requestAnimationFrame(animate);
        }
        requestAnimationFrame(animate);

        // Cleanup
        return () => {
            canvas.removeEventListener('mousemove', handleMouseMove);
            canvas.removeEventListener('click', handleClick);
            window.removeEventListener('resize', handleResize);
        };
    }, []);

    return (
        <div className="junie-hero-section" style={{ position: "relative", width: "100%", height: "100vh", overflow: "hidden" }}>
            <canvas
                ref={canvasRef}
                className="junie-hero-section__animation"
            />
            <div
                style={{ position: "relative", zIndex: 1, color: "white", textAlign: "center", paddingTop: "30vh", userSelect: "none" }}
            >
                <h2 style={{ fontSize: "3rem" }}>Welcome</h2>
                <button
                    onClick={() => navigate("/login")}
                    style={{ margin: "15px", padding: "12px 24px", fontSize: "1.1rem", cursor: "pointer", borderRadius: "6px", border: "none", backgroundColor: "#4caf50", color: "white" }}
                >
                    Login
                </button>
                <button
                    onClick={() => navigate("/register")}
                    style={{ margin: "15px", padding: "12px 24px", fontSize: "1.1rem", cursor: "pointer", borderRadius: "6px", border: "none", backgroundColor: "#2196f3", color: "white" }}
                >
                    Register
                </button>
            </div>
        </div>
    );
};

export default MainPage;
