// src/pages/ProfilePage.js - Main Profile Page
import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import API from './api';
import NeoBrutalistCard from './NeoBrutalist/NeoBrutalistCard';
import NeoBrutalistButton from './NeoBrutalist/NeoBrutalistButton';
import NeoBrutalistModal from './NeoBrutalist/NeoBrutalistModal';
import '../styles/component/profile.css'

const ProfilePage = ({ isModal = false }) => {
    const [profile, setProfile] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');
    const [message, setMessage] = useState('');
    const [activeTab, setActiveTab] = useState('profile'); // 'profile', 'orders', 'notifications', 'security'
    const [editing, setEditing] = useState(false);
    const [showPasswordModal, setShowPasswordModal] = useState(false);
    const [ordersData, setOrdersData] = useState(null);
    const [notificationsData, setNotificationsData] = useState(null);

    const [formData, setFormData] = useState({
        name: '',
        phone: '',
        company_name: ''
    });

    const navigate = useNavigate();

    useEffect(() => {
        fetchProfile();
    }, []);

    useEffect(() => {
        if (activeTab === 'orders' && !ordersData) {
            fetchOrdersSummary();
        } else if (activeTab === 'notifications' && !notificationsData) {
            fetchNotifications();
        }
    }, [activeTab]);

    const fetchProfile = async () => {
        setLoading(true);
        try {
            const response = await API.get('/profile/me/');
            console.log('üë§ Profile fetched:', response.data);
            setProfile(response.data.profile);
            setFormData({
                name: response.data.profile.name,
                phone: response.data.profile.phone || '',
                company_name: response.data.profile.company_name || ''
            });
        } catch (error) {
            console.error('‚ùå Error fetching profile:', error);
            if (error.response?.status === 401) {
                handleLogout();
            } else {
                setError('ÿÆÿ∑ÿß ÿØÿ± ÿ®ÿßÿ±⁄Ø€åÿ±€å ÿßÿ∑ŸÑÿßÿπÿßÿ™ Ÿæÿ±ŸàŸÅÿß€åŸÑ');
            }
        } finally {
            setLoading(false);
        }
    };

    const fetchOrdersSummary = async () => {
        try {
            const response = await API.get('/profile/orders-summary/');
            console.log('üìä Orders summary fetched:', response.data);
            setOrdersData(response.data);
        } catch (error) {
            console.error('‚ùå Error fetching orders summary:', error);
        }
    };

    const fetchNotifications = async () => {
        try {
            const response = await API.get('/profile/notifications-history/');
            console.log('üîî Notifications fetched:', response.data);
            setNotificationsData(response.data);
        } catch (error) {
            console.error('‚ùå Error fetching notifications:', error);
        }
    };

    const handleUpdateProfile = async (e) => {
        e.preventDefault();
        try {
            const response = await API.put('/profile/update/', formData);
            console.log('‚úÖ Profile updated:', response.data);
            setProfile({ ...profile, ...response.data.profile });
            setEditing(false);
            setMessage('Ÿæÿ±ŸàŸÅÿß€åŸÑ ÿ®ÿß ŸÖŸàŸÅŸÇ€åÿ™ ÿ®Ÿá‚Äåÿ±Ÿàÿ≤ÿ±ÿ≥ÿßŸÜ€å ÿ¥ÿØ');
            setTimeout(() => setMessage(''), 3000);
        } catch (error) {
            console.error('‚ùå Error updating profile:', error);
            setError('ÿÆÿ∑ÿß ÿØÿ± ÿ®Ÿá‚Äåÿ±Ÿàÿ≤ÿ±ÿ≥ÿßŸÜ€å Ÿæÿ±ŸàŸÅÿß€åŸÑ');
        }
    };

    const handleLogout = () => {
        localStorage.removeItem('access_token');
        localStorage.removeItem('refresh_token');
        localStorage.removeItem('userData');
        delete API.defaults.headers.common['Authorization'];
        navigate('/');
    };

    const formatPrice = (price) => {
        if (!price || price === 0) return '0';
        return `${parseFloat(price).toLocaleString('fa-IR')} ÿ±€åÿßŸÑ`;
    };

    const getNotificationIcon = (type) => {
        switch (type) {
            case 'email': return 'üìß';
            case 'sms': return 'üì±';
            default: return 'üîî';
        }
    };

    const getNotificationTypeText = (emailType, smsType) => {
        const types = {
            'order_submitted': 'ÿ´ÿ®ÿ™ ÿ≥ŸÅÿßÿ±ÿ¥',
            'pricing_ready': 'ÿ¢ŸÖÿßÿØŸá‚Äåÿ≥ÿßÿ≤€å ŸÇ€åŸÖÿ™',
            'order_confirmed': 'ÿ™ÿ£€å€åÿØ ÿ≥ŸÅÿßÿ±ÿ¥',
            'order_rejected': 'ÿ±ÿØ ÿ≥ŸÅÿßÿ±ÿ¥',
            'order_completed': 'ÿ™⁄©ŸÖ€åŸÑ ÿ≥ŸÅÿßÿ±ÿ¥',
            'dealer_assigned': 'ÿ™ÿÆÿµ€åÿµ ŸÜŸÖÿß€åŸÜÿØŸá',
            'new_arrival_customer': 'ŸÖÿ≠ŸÖŸàŸÑŸá ÿ¨ÿØ€åÿØ',
            'new_arrival_dealer': 'ŸÖÿ≠ŸÖŸàŸÑŸá ÿ¨ÿØ€åÿØ - ŸÜŸÖÿß€åŸÜÿØŸá',
        };
        return types[emailType || smsType] || (emailType || smsType);
    };

    if (loading) {
        return (
            <div className="profile-container">
                <div className="profile-header">
                    <h1>ÿØÿ± ÿ≠ÿßŸÑ ÿ®ÿßÿ±⁄Ø€åÿ±€å...</h1>
                </div>
            </div>
        );
    }

    if (!profile) {
        return (
            <div className="profile-container">
                <div className="profile-header">
                    <h1>ÿÆÿ∑ÿß ÿØÿ± ÿ®ÿßÿ±⁄Ø€åÿ±€å Ÿæÿ±ŸàŸÅÿß€åŸÑ</h1>
                    <NeoBrutalistButton
                        text="ÿ®ÿßÿ≤⁄Øÿ¥ÿ™ ÿ®Ÿá ÿØÿßÿ¥ÿ®Ÿàÿ±ÿØ"
                        color="blue-400"
                        textColor="white"
                        onClick={() => navigate('/dashboard')}
                    />
                </div>
            </div>
        );
    }

    return (
        <div className="profile-container">
            {/* Conditionally render the header */}
            {!isModal && (
            <div className="profile-header">
                <div className="profile-title">
                    <h1>Ÿæÿ±ŸàŸÅÿß€åŸÑ ⁄©ÿßÿ±ÿ®ÿ±€å</h1>
                    <span className="profile-subtitle">{profile.name}</span>
                </div>
                <div className="header-actions">

                    <NeoBrutalistButton
                        text="ÿÆÿ±Ÿàÿ¨"
                        color="red-400"
                        textColor="white"
                        onClick={handleLogout}
                        className="logout-btn"
                    />
                </div>
            </div>
            )}
            {/* Messages */}
            {message && (
                <div className="message-banner success">
                    <span>{message}</span>
                    <NeoBrutalistButton
                        text="√ó"
                        color="white"
                        textColor="black"
                        onClick={() => setMessage('')}
                        className="close-btn"
                    />
                </div>
            )}

            {error && (
                <div className="message-banner error">
                    <span>{error}</span>
                    <NeoBrutalistButton
                        text="√ó"
                        color="white"
                        textColor="black"
                        onClick={() => setError('')}
                        className="close-btn"
                    />
                </div>
            )}

            {/* Tabs */}
            <div className="profile-tabs">
                <NeoBrutalistButton
                    text="ÿßÿ∑ŸÑÿßÿπÿßÿ™ ÿ¥ÿÆÿµ€å"
                    color={activeTab === 'profile' ? 'yellow-400' : 'gray-400'}
                    textColor="black"
                    onClick={() => setActiveTab('profile')}
                    className="tab-btn"
                />
                <NeoBrutalistButton
                    text="ÿÆŸÑÿßÿµŸá ÿ≥ŸÅÿßÿ±ÿ¥ÿßÿ™"
                    color={activeTab === 'orders' ? 'yellow-400' : 'gray-400'}
                    textColor="black"
                    onClick={() => setActiveTab('orders')}
                    className="tab-btn"
                />
                <NeoBrutalistButton
                    text="ÿ™ÿßÿ±€åÿÆ⁄ÜŸá ÿßÿ∑ŸÑÿßÿπ‚Äåÿ±ÿ≥ÿßŸÜ€å"
                    color={activeTab === 'notifications' ? 'yellow-400' : 'gray-400'}
                    textColor="black"
                    onClick={() => setActiveTab('notifications')}
                    className="tab-btn"
                />
                <NeoBrutalistButton
                    text="ÿßŸÖŸÜ€åÿ™"
                    color={activeTab === 'security' ? 'yellow-400' : 'gray-400'}
                    textColor="black"
                    onClick={() => setActiveTab('security')}
                    className="tab-btn"
                />
            </div>

            {/* Tab Content */}
            <div className="profile-content">
                {/* Profile Tab */}
                {activeTab === 'profile' && (
                    <div className="profile-tab">
                        <NeoBrutalistCard className="profile-info-card">
                            <div className="card-header">
                                <h2>ÿßÿ∑ŸÑÿßÿπÿßÿ™ ÿ¥ÿÆÿµ€å</h2>
                                <NeoBrutalistButton
                                    text={editing ? 'ŸÑÿ∫Ÿà' : 'Ÿà€åÿ±ÿß€åÿ¥'}
                                    color={editing ? 'red-400' : 'blue-400'}
                                    textColor="white"
