// Change Password Component
const ChangePasswordForm = ({ onSuccess, onError }) => {
    const [formData, setFormData] = useState({
        current_password: '',
        new_password: '',
        confirm_password: ''
    });
    const [loading, setLoading] = useState(false);

    const handleSubmit = async (e) => {
        e.preventDefault();

        if (formData.new_password !== formData.confirm_password) {
            onError('ÿ±ŸÖÿ≤Ÿáÿß€å ÿπÿ®Ÿàÿ± ÿ¨ÿØ€åÿØ ŸÖÿ∑ÿßÿ®ŸÇÿ™ ŸÜÿØÿßÿ±ŸÜÿØ');
            return;
        }

        if (formData.new_password.length < 8) {
            onError('ÿ±ŸÖÿ≤ ÿπÿ®Ÿàÿ± ÿ®ÿß€åÿØ ÿ≠ÿØÿßŸÇŸÑ 8 ⁄©ÿßÿ±ÿß⁄©ÿ™ÿ± ÿ®ÿßÿ¥ÿØ');
            return;
        }

        setLoading(true);
        try {
            await API.post('/profile/change-password/', formData);
            onSuccess();
        } catch (error) {
            console.error('‚ùå Error changing password:', error);
            if (error.response?.data?.error) {
                onError(error.response.data.error);
            } else {
                onError('ÿÆÿ∑ÿß ÿØÿ± ÿ™ÿ∫€å€åÿ± ÿ±ŸÖÿ≤ ÿπÿ®Ÿàÿ±');
            }
        } finally {
            setLoading(false);
        }
    };

    return (
        <form onSubmit={handleSubmit} className="password-form">
            <div className="form-group">
                <label>ÿ±ŸÖÿ≤ ÿπÿ®Ÿàÿ± ŸÅÿπŸÑ€å:</label>
                <input
                    type="password"
                    value={formData.current_password}
                    onChange={(e) => setFormData({ ...formData, current_password: e.target.value })}
                    required
                    className="form-input"
                />
            </div>

            <div className="form-group">
                <label>ÿ±ŸÖÿ≤ ÿπÿ®Ÿàÿ± ÿ¨ÿØ€åÿØ:</label>
                <input
                    type="password"
                    value={formData.new_password}
                    onChange={(e) => setFormData({ ...formData, new_password: e.target.value })}
                    required
                    minLength={8}
                    className="form-input"
                />
            </div>

            <div className="form-group">
                <label>ÿ™ÿ£€å€åÿØ ÿ±ŸÖÿ≤ ÿπÿ®Ÿàÿ± ÿ¨ÿØ€åÿØ:</label>
                <input
                    type="password"
                    value={formData.confirm_password}
                    onChange={(e) => setFormData({ ...formData, confirm_password: e.target.value })}
                    required
                    minLength={8}
                    className="form-input"
                />
            </div>

            <div className="form-actions">
                <NeoBrutalistButton
                    text={loading ? 'ÿØÿ± ÿ≠ÿßŸÑ ÿ™ÿ∫€å€åÿ±...' : 'ÿ™ÿ∫€å€åÿ± ÿ±ŸÖÿ≤ ÿπÿ®Ÿàÿ±'}
                    color="green-400"
                    textColor="white"
                    type="submit"
                    disabled={loading}
                />
            </div>
        </form>
    );
};

