import React, { useEffect, useRef } from "react";

export default function HomePage() {
    const sceneRef = useRef(null);

    useEffect(() => {
        if (!window.UnicornStudio) {
            const script = document.createElement("script");
            script.src = "https://cdn.jsdelivr.net/gh/hiunicornstudio/unicornstudio.js@v1.4.28/dist/unicornStudio.umd.js";
            script.onload = () => {
                if (!window.UnicornStudio.isInitialized) {
                    window.UnicornStudio.init();
                    window.UnicornStudio.isInitialized = true;
                }
            };
            document.body.appendChild(script);
        } else if (!window.UnicornStudio.isInitialized) {
            window.UnicornStudio.init();
            window.UnicornStudio.isInitialized = true;
        }
    }, []);

    return (
        <div style={{ position: "relative", width: "100%", height: "100vh", overflow: "hidden" }}>
            {/* Unicorn Embed Container */}
            <div
                ref={sceneRef}
                data-us-project="WsbdyvD8R71AQIbQn4A9"
                style={{ width: "100%", height: "100%" }}
            />

            {/* Overlay UI */}
            <div
                style={{
                    position: "absolute",
                    top: 0,
                    left: 0,
                    width: "100%",
                    height: "100%",
                    display: "flex",
                    flexDirection: "column",
                    justifyContent: "center",
                    alignItems: "center",
                    color: "white",
                    zIndex: 10,
                    backdropFilter: "blur(4px)",
                    textAlign: "center",
                }}
            >
                <h1 style={{ fontSize: "3rem", marginBottom: "1rem" }}>Welcome to My App</h1>
                <p style={{ fontSize: "1.2rem", marginBottom: "2rem" }}>
                    Experience the magic of UnicornStudio 3D design!
                </p>
                <div>
                    <button
                        style={{
                            padding: "10px 20px",
                            marginRight: "1rem",
                            fontSize: "1rem",
                            backgroundColor: "#fff",
                            color: "#000",
                            border: "none",
                            borderRadius: "5px",
                            cursor: "pointer",
                        }}
                        onClick={() => alert("Login clicked")}
                    >
                        Login
                    </button>
                    <button
                        style={{
                            padding: "10px 20px",
                            fontSize: "1rem",
                            backgroundColor: "#000",
                            color: "#fff",
                            border: "1px solid #fff",
                            borderRadius: "5px",
                            cursor: "pointer",
                        }}
                        onClick={() => alert("Register clicked")}
                    >
                        Register
                    </button>
                </div>
            </div>
        </div>
    );
}
