import React, { useState, useCallback } from 'react';
import { useDropzone } from 'react-dropzone';
import API from './api';
import NeoBrutalistButton from './NeoBrutalist/NeoBrutalistButton';
import '../styles/component/PaymentReceiptUpload.css';

const PaymentReceiptUpload = ({ orderId, onUploadSuccess }) => {
    const [uploading, setUploading] = useState(false);
    const [error, setError] = useState('');
    const [previewImage, setPreviewImage] = useState(null);

    const onDrop = useCallback((acceptedFiles, rejectedFiles) => {
        setError('');

        if (rejectedFiles.length > 0) {
            const rejection = rejectedFiles[0];
            if (rejection.errors.some(e => e.code === 'file-too-large')) {
                setError('ÿ≠ÿ¨ŸÖ ŸÅÿß€åŸÑ ŸÜÿ®ÿß€åÿØ ÿ®€åÿ¥ÿ™ÿ± ÿßÿ≤ 10MB ÿ®ÿßÿ¥ÿØ');
            } else if (rejection.errors.some(e => e.code === 'file-invalid-type')) {
                setError('ŸÅŸÇÿ∑ ŸÅÿß€åŸÑ‚ÄåŸáÿß€å ÿ™ÿµŸà€åÿ±€å (JPEG, PNG, GIF, WebP) ŸÖÿ¨ÿßÿ≤ Ÿáÿ≥ÿ™ŸÜÿØ');
            } else {
                setError('ŸÅÿß€åŸÑ ÿßŸÜÿ™ÿÆÿßÿ®€å ŸÖÿπÿ™ÿ®ÿ± ŸÜ€åÿ≥ÿ™');
            }
            return;
        }

        if (acceptedFiles.length > 0) {
            const file = acceptedFiles[0];

            // Create preview
            const reader = new FileReader();
            reader.onload = (e) => {
                setPreviewImage({
                    file: file,
                    preview: e.target.result,
                    name: file.name,
                    size: file.size
                });
            };
            reader.readAsDataURL(file);
        }
    }, []);

    const { getRootProps, getInputProps, isDragActive } = useDropzone({
        onDrop,
        accept: {
            'image/*': ['.jpeg', '.jpg', '.png', '.gif', '.webp']
        },
        maxSize: 10 * 1024 * 1024, // 10MB
        multiple: false
    });

    const handleUpload = async () => {
        if (!previewImage) {
            setError('ŸÑÿ∑ŸÅÿßŸã ÿßÿ®ÿ™ÿØÿß ŸÅÿß€åŸÑ ÿ±ÿß ÿßŸÜÿ™ÿÆÿßÿ® ⁄©ŸÜ€åÿØ');
            return;
        }

        setUploading(true);
        setError('');

        try {
            const formData = new FormData();
            formData.append('payment_receipt', previewImage.file);

            const response = await API.post(`/orders/${orderId}/upload-payment-receipt/`, formData, {
                headers: {
                    'Content-Type': 'multipart/form-data',
                },
            });

            // Success
            alert('ÿ±ÿ≥€åÿØ Ÿæÿ±ÿØÿßÿÆÿ™ ÿ®ÿß ŸÖŸàŸÅŸÇ€åÿ™ ÿ¢ŸæŸÑŸàÿØ ÿ¥ÿØ!');

            if (onUploadSuccess) {
                onUploadSuccess();
            }

        } catch (err) {
            console.error('‚ùå Payment receipt upload failed:', err);
            const errorMessage = err.response?.data?.error || 'ÿÆÿ∑ÿß ÿØÿ± ÿ¢ŸæŸÑŸàÿØ ÿ±ÿ≥€åÿØ Ÿæÿ±ÿØÿßÿÆÿ™';
            setError(errorMessage);
        } finally {
            setUploading(false);
        }
    };

    const removePreview = () => {
        setPreviewImage(null);
        setError('');
    };

    const formatFileSize = (bytes) => {
        if (bytes === 0) return '0 Bytes';
        const k = 1024;
        const sizes = ['Bytes', 'KB', 'MB', 'GB'];
        const i = Math.floor(Math.log(bytes) / Math.log(k));
        return parseFloat((bytes / Math.pow(k, i)).toFixed(2)) + ' ' + sizes[i];
    };

    return (
        <div className="payment-receipt-upload" dir="rtl">
            {error && (
                <div className="upload-error">
                    <span className="error-icon">‚ö†Ô∏è</span>
                    <span>{error}</span>
                </div>
            )}

            {!previewImage ? (
                <div
                    {...getRootProps()}
                    className={`dropzone ${isDragActive ? 'dropzone-active' : ''}`}
                >
                    <input {...getInputProps()} />
                    <div className="dropzone-content">
                        <div className="dropzone-icon">üìÑ</div>
                        <div className="dropzone-text">
                            {isDragActive ? (
                                <p>ŸÅÿß€åŸÑ ÿ±ÿß ÿß€åŸÜÿ¨ÿß ÿ±Ÿáÿß ⁄©ŸÜ€åÿØ...</p>
                            ) : (
                                <>
                                    <p>ŸÅÿß€åŸÑ ÿ±ÿ≥€åÿØ Ÿæÿ±ÿØÿßÿÆÿ™ €åÿß ⁄Ü⁄© ÿ±ÿß ÿß€åŸÜÿ¨ÿß ÿ®⁄©ÿ¥€åÿØ</p>
                                    <p>€åÿß ⁄©ŸÑ€å⁄© ⁄©ŸÜ€åÿØ ÿ™ÿß ŸÅÿß€åŸÑ ÿ±ÿß ÿßŸÜÿ™ÿÆÿßÿ® ⁄©ŸÜ€åÿØ</p>
                                </>
                            )}
                        </div>
                        <div className="dropzone-hint">
                            ŸÅÿ±ŸÖÿ™‚ÄåŸáÿß€å ŸÖÿ¨ÿßÿ≤: JPEG, PNG, GIF, WebP (ÿ≠ÿØÿß⁄©ÿ´ÿ± 10MB)
                        </div>
                    </div>
                </div>
            ) : (
                <div className="preview-container">
                    <div className="preview-header">
                        <h3>Ÿæ€åÿ¥‚ÄåŸÜŸÖÿß€åÿ¥ ŸÅÿß€åŸÑ</h3>
                        <button
                            onClick={removePreview}
                            className="remove-preview-btn"
                            type="button"
                        >
                            ‚úï
                        </button>
                    </div>

                    <div className="preview-content">
                        <div className="preview-image-container">
                            <img
                                src={previewImage.preview}
                                alt="Ÿæ€åÿ¥‚ÄåŸÜŸÖÿß€åÿ¥ ÿ±ÿ≥€åÿØ Ÿæÿ±ÿØÿßÿÆÿ™"
                                className="preview-image"
                            />
                        </div>

                        <div className="file-info">
                            <div className="file-info-item">
                                <span className="file-info-label">ŸÜÿßŸÖ ŸÅÿß€åŸÑ:</span>
                                <span className="file-info-value">{previewImage.name}</span>
                            </div>
                            <div className="file-info-item">
                                <span className="file-info-label">ÿ≠ÿ¨ŸÖ ŸÅÿß€åŸÑ:</span>
                                <span className="file-info-value">{formatFileSize(previewImage.size)}</span>
                            </div>
                        </div>
                    </div>

                    <div className="upload-actions">
                        <NeoBrutalistButton
                            text={uploading ? "ÿØÿ± ÿ≠ÿßŸÑ ÿ¢ŸæŸÑŸàÿØ..." : "ÿ¢ŸæŸÑŸàÿØ ÿ±ÿ≥€åÿØ Ÿæÿ±ÿØÿßÿÆÿ™"}
                            color="green-400"
                            textColor="white"
                            onClick={handleUpload}
                            disabled={uploading}
                            className="upload-btn"
                        />
                    </div>
                </div>
            )}
        </div>
    );
};

export default PaymentReceiptUpload;
