import React, { useEffect, useState } from "react";
import API from "../component/api";
import NeoBrutalistButton from "../component/NeoBrutalist/NeoBrutalistButton";
import NeoBrutalistInput from "../component/NeoBrutalist/NeoBrutalistInput";
import NeoBrutalistCard from "../component/NeoBrutalist/NeoBrutalistCard";
import '../styles/OrderDetail.css';

const NeoBrutalistOrderDetailPage = ({ orderId, onOrderUpdated }) => {
    const [order, setOrder] = useState(null);
    const [items, setItems] = useState([]);
    const [editMode, setEditMode] = useState(false);
    const [customerComment, setCustomerComment] = useState("");
    const [approvalDecision, setApprovalDecision] = useState("approve");
    const [rejectionReason, setRejectionReason] = useState("");
    const [loading, setLoading] = useState(true);
    const [invoice, setInvoice] = useState(null);
    const [error, setError] = useState("");

    // Fetch order details
    useEffect(() => {
        setLoading(true);
        API
            .get(`/orders/${orderId}/`)
            .then((res) => {
                setOrder(res.data);
                setCustomerComment(res.data.customer_comment || "");
                setItems(res.data.items || []);
                setInvoice(res.data.invoice || null);
                setLoading(false);
            })
            .catch(() => {
                setError("Failed to load order.");
                setLoading(false);
            });
    }, [orderId]);

    // Edit customer comment
    const handleEditSubmit = (e) => {
        e.preventDefault();
        setLoading(true);
        API
            .patch(`/orders/${orderId}/`, { customer_comment: customerComment })
            .then((res) => {
                setOrder(res.data);
                setEditMode(false);
                setLoading(false);
                if (onOrderUpdated) onOrderUpdated();
            })
            .catch(() => {
                setError("Failed to update comment.");
                setLoading(false);
            });
    };

    // Approve/reject pricing
    const handleApproval = (e) => {
        e.preventDefault();
        setLoading(true);
        API
            .post(`/orders/${orderId}/customer_approval/`, {
                decision: approvalDecision,
                rejection_reason: rejectionReason,
            })
            .then((res) => {
                setOrder(res.data);
                setLoading(false);
                if (onOrderUpdated) onOrderUpdated();
            })
            .catch(() => {
                setError("Failed to submit response.");
                setLoading(false);
            });
    };

    const getUserInfo = () => {
        const userDataString = localStorage.getItem('userData');
        return userDataString ? JSON.parse(userDataString) : null;
    };

    const getStatusColor = (status) => {
        switch (status) {
            case 'pending':
            case 'pending_pricing':
                return 'yellow-400';
            case 'waiting_customer_approval':
                return 'blue-400';
            case 'confirmed':
            case 'completed':
                return 'green-400';
            case 'rejected':
            case 'cancelled':
                return 'red-400';
            default:
                return 'gray-400';
        }
    };

    if (loading) {
        return (
            <div className="neo-order-detail">
                <NeoBrutalistCard className="neo-loading-card">
                    <div className="neo-loading-content">
                        <span className="neo-loading-text">Loading order details...</span>
                    </div>
                </NeoBrutalistCard>
            </div>
        );
    }

    if (error) {
        return (
            <div className="neo-order-detail">
                <NeoBrutalistCard className="neo-error-card">
                    <div className="neo-error-content">
                        <span className="neo-error-icon">âš </span>
                        <span className="neo-error-text">{error}</span>
                    </div>
                </NeoBrutalistCard>
            </div>
        );
    }

    if (!order) {
        return (
            <div className="neo-order-detail">
                <NeoBrutalistCard className="neo-error-card">
                    <div className="neo-error-content">
                        <span className="neo-error-text">Order not found.</span>
                    </div>
                </NeoBrutalistCard>
            </div>
        );
    }

    return (
        <div className="neo-order-detail">
            {/* Order Header */}
            <div className="neo-order-header">
                <h1 className="neo-order-title">Order #{order.id}</h1>
                <NeoBrutalistButton
                    text={order.status.replace('_', ' ').toUpperCase()}
                    color={getStatusColor(order.status)}
                    textColor="black"
                    className="neo-status-badge"
                />
            </div>

            {/* Order Information Card */}
            <NeoBrutalistCard className="neo-order-info-card">
                <div className="neo-card-header">
                    <h2 className="neo-card-title">Order Information</h2>
                </div>
                <div className="neo-order-info-grid">
                    <div className="neo-info-item">
                        <span className="neo-info-label">Customer:</span>
                        <span className="neo-info-value">{getUserInfo()?.name}</span>
                    </div>
                    <div className="neo-info-item">
                        <span className="neo-info-label">Created:</span>
                        <span className="neo-info-value">{new Date(order.created_at).toLocaleString()}</span>
                    </div>
                    <div className="neo-info-item">
                        <span className="neo-info-label">Status:</span>
                        <span className="neo-info-value">{order.status.replace('_', ' ')}</span>
                    </div>
                </div>

                {/* Customer Comment Section */}
                <div className="neo-comment-section">
                    <h3 className="neo-comment-title">Customer Comment</h3>
                    {editMode ? (
                        <div className="neo-edit-form">
                            <NeoBrutalistInput
                                type="textarea"
                                value={customerComment}
                                onChange={(e) => setCustomerComment(e.target.value)}
                                rows={3}
                                placeholder="Add your comments..."
                            />
                            <div className="neo-edit-actions">
                                <NeoBrutalistButton
                                    text={loading ? "Saving..." : "Save"}
                                    color="green-400"
                                    textColor="black"
                                    onClick={handleEditSubmit}
                                    disabled={loading}
                                />
                                <NeoBrutalistButton
                                    text="Cancel"
                                    color="gray-400"
                                    textColor="black"
                                    onClick={() => setEditMode(false)}
                                />
                            </div>
                        </div>
                    ) : (
                        <div className="neo-comment-display">
                            <p className="neo-comment-text">{order.customer_comment || "No comment provided"}</p>
                            {["pending_pricing"].includes(order.status) && (
                                <NeoBrutalistButton
                                    text="Edit Comment"
                                    color="blue-400"
                                    textColor="white"
                                    onClick={() => setEditMode(true)}
                                    className="neo-edit-btn"
                                />
                            )}
                        </div>
                    )}
                </div>
            </NeoBrutalistCard>

            {/* Order Items Card */}
            <NeoBrutalistCard className="neo-items-card">
                <div className="neo-card-header">
                    <h2 className="neo-card-title">Order Items</h2>
                </div>
                <div className="neo-items-table">
                    <div className="neo-table-header">
                        <div className="neo-table-cell">Product</div>
                        <div className="neo-table-cell">Qty</div>
                        <div className="neo-table-cell">Notes</div>
                        <div className="neo-table-cell">Price</div>
                        <div className="neo-table-cell">Final Qty</div>
                        <div className="neo-table-cell">Total</div>
                    </div>
                    {items.map((item) => (
                        <div key={item.id} className="neo-table-row">
                            <div className="neo-table-cell">
                                <strong>{item.product_name || item.product?.name}</strong>
                            </div>
                            <div className="neo-table-cell">{item.requested_quantity}</div>
                            <div className="neo-table-cell">{item.customer_notes || "-"}</div>
                            <div className="neo-table-cell">
                                {item.quoted_unit_price > 0 ? `$${item.quoted_unit_price}` : "-"}
                            </div>
                            <div className="neo-table-cell">{item.final_quantity || "-"}</div>
                            <div className="neo-table-cell">
                                {item.final_quantity && item.quoted_unit_price
                                    ? `$${(item.final_quantity * item.quoted_unit_price).toFixed(2)}`
                                    : "-"}
                            </div>
                        </div>
                    ))}
                </div>
            </NeoBrutalistCard>

            {/* Admin Reply Card */}
            <NeoBrutalistCard className="neo-admin-reply-card">
                <div className="neo-card-header">
                    <h2 className="neo-card-title">Admin Reply</h2>
                </div>
                <div className="neo-admin-info-grid">
                    <div className="neo-info-item">
                        <span className="neo-info-label">Admin Comment:</span>
                        <span className="neo-info-value">{order.admin_comment || "No admin comment yet"}</span>
                    </div>
                    <div className="neo-info-item">
                        <span className="neo-info-label">Quoted Total:</span>
                        <span className="neo-info-value">
                            {order.quoted_total > 0 ? `$${order.quoted_total}` : "Pending"}
                        </span>
                    </div>
                    <div className="neo-info-item">
                        <span className="neo-info-label">Pricing Date:</span>
                        <span className="neo-info-value">
                            {order.pricing_date ? new Date(order.pricing_date).toLocaleString() : "Not priced yet"}
                        </span>
                    </div>
                </div>

                {/* Approval Form */}
                {order.status === "waiting_customer_approval" && (
                    <div className="neo-approval-section">
                        <h3 className="neo-approval-title">Customer Approval Required</h3>
                        <div className="neo-approval-form">
                            <div className="neo-radio-group">
                                <label className="neo-radio-label">
                                    <input
                                        type="radio"
                                        name="decision"
                                        value="approve"
                                        checked={approvalDecision === "approve"}
                                        onChange={() => setApprovalDecision("approve")}
                                        className="neo-radio-input"
                                    />
                                    <span className="neo-radio-text">Accept Pre-Invoice</span>
                                </label>
                                <label className="neo-radio-label">
                                    <input
                                        type="radio"
                                        name="decision"
                                        value="reject"
                                        checked={approvalDecision === "reject"}
                                        onChange={() => setApprovalDecision("reject")}
                                        className="neo-radio-input"
                                    />
                                    <span className="neo-radio-text">Reject</span>
                                </label>
                            </div>
                            {approvalDecision === "reject" && (
                                <div className="neo-rejection-reason">
                                    <NeoBrutalistInput
                                        type="textarea"
                                        placeholder="Please provide reason for rejection..."
                                        value={rejectionReason}
                                        onChange={(e) => setRejectionReason(e.target.value)}
                                        rows={3}
                                        required
                                    />
                                </div>
                            )}
                            <div className="neo-approval-actions">
                                <NeoBrutalistButton
                                    text={loading ? "Submitting..." : "Submit Decision"}
                                    color={approvalDecision === "approve" ? "green-400" : "red-400"}
                                    textColor="black"
                                    onClick={handleApproval}
                                    disabled={loading}
                                    className="neo-submit-decision-btn"
                                />
                            </div>
                        </div>
                    </div>
                )}

                {/* Status Messages */}
                {order.status === "confirmed" && (
                    <div className="neo-status-message neo-success">
                        <span className="neo-status-icon">âœ“</span>
                        <span>Order confirmed! Final invoice will be generated.</span>
                    </div>
                )}

                {order.status === "rejected" && (
                    <div className="neo-status-message neo-error">
                        <span className="neo-status-icon">âœ—</span>
                        <span>Order rejected. Reason: {order.customer_rejection_reason}</span>
                    </div>
                )}
            </NeoBrutalistCard>

            {/* Invoice Card */}
            {invoice && (
                <NeoBrutalistCard className="neo-invoice-card">
                    <div className="neo-card-header">
                        <h2 className="neo-card-title">
                            {invoice.invoice_type === "final_invoice" ? "Final Invoice" : "Pre-Invoice"}
                        </h2>
                    </div>
                    <div className="neo-invoice-info-grid">
                        <div className="neo-info-item">
                            <span className="neo-info-label">Invoice Number:</span>
                            <span className="neo-info-value">{invoice.invoice_number}</span>
                        </div>
                        <div className="neo-info-item">
                            <span className="neo-info-label">Total Amount:</span>
                            <span className="neo-info-value">${invoice.total_amount}</span>
                        </div>
                        <div className="neo-info-item">
                            <span className="neo-info-label">Discount:</span>
                            <span className="neo-info-value">${invoice.discount}</span>
                        </div>
                        <div className="neo-info-item">
                            <span className="neo-info-label">Tax Rate:</span>
                            <span className="neo-info-value">{invoice.tax_rate}%</span>
                        </div>
                        <div className="neo-info-item">
                            <span className="neo-info-label">Tax Amount:</span>
                            <span className="neo-info-value">${invoice.tax_amount}</span>
                        </div>
                        <div className="neo-info-item">
                            <span className="neo-info-label">Payable Amount:</span>
                            <span className="neo-info-value neo-payable-amount">${invoice.payable_amount}</span>
                        </div>
                    </div>
                    {invoice.pdf_file && (
                        <div className="neo-invoice-actions">
                            <NeoBrutalistButton
                                text="Download PDF"
                                color="blue-400"
                                textColor="white"
                                onClick={() => window.open(invoice.pdf_file, '_blank')}
                                className="neo-download-btn"
                            />
                        </div>
                    )}
                </NeoBrutalistCard>
            )}
        </div>
    );
};

export default NeoBrutalistOrderDetailPage;
