import React, { useEffect, useState } from "react";
import API from "../component/api";
import "../styles/CreateOrderModal.css";

const CreateOrderModal = ({ onClose, onOrderCreated }) => {
    const [products, setProducts] = useState([]);
    const [customerComment, setCustomerComment] = useState("");
    const [items, setItems] = useState([
        { product: "", requested_quantity: 1, customer_notes: "" },
    ]);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState("");

    useEffect(() => {
        API.get("products/")
            .then((res) => setProducts(res.data))
            .catch(() => setError("Failed to load products."));
    }, []);

    const handleItemChange = (idx, field, value) => {
        setItems((prev) =>
            prev.map((item, i) =>
                i === idx ? { ...item, [field]: value } : item
            )
        );
    };

    const addItem = () => {
        setItems((prev) => [
            ...prev,
            { product: "", requested_quantity: 1, customer_notes: "" },
        ]);
    };

    const removeItem = (idx) => {
        setItems((prev) => prev.filter((_, i) => i !== idx));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setLoading(true);
        setError("");

        if (!items.length) {
            setError("Order must contain at least one item.");
            setLoading(false);
            return;
        }

        try {
            const payload = {
                customer_comment: customerComment,
                items: items.map((item) => ({
                    product: item.product,
                    requested_quantity: Number(item.requested_quantity),
                    customer_notes: item.customer_notes,
                })),
            };
            const res = await API.post("orders/", payload);

            if (onOrderCreated) onOrderCreated(res.data);

            setCustomerComment("");
            setItems([{ product: "", requested_quantity: 1, customer_notes: "" }]);
            setLoading(false);
            onClose();
        } catch (err) {
            setLoading(false);
            if (err.response)
                setError(`Failed to create order: ${JSON.stringify(err.response.data)}`);
            else
                setError("Failed to create order. Please try again.");
        }
    };

    const getUserInfo = () => {
        const userDataString = localStorage.getItem('userData');
        return userDataString ? JSON.parse(userDataString) : null;
    };

    return (
        <div className="modal-overlay">
            <div className="modal-box">
                <div className="modal-header">
                    <h2>Create New Order</h2>
                    <button onClick={onClose} className="modal-close">X</button>
                </div>
                <form onSubmit={handleSubmit} className="modal-form">
                    <div className="form-group">
                        <label>Customer:</label>
                        <span className="customer-name">{getUserInfo()?.name}</span>
                    </div>
                    <div className="form-group">
                        <label>Customer Comment:</label>
                        <textarea
                            value={customerComment}
                            onChange={(e) => setCustomerComment(e.target.value)}
                            placeholder="Initial request/comments"
                        />
                    </div>
                    <h3>Order Items</h3>
                    {items.map((item, idx) => (
                        <div key={idx} className="item-group">
                            <select
                                value={item.product}
                                onChange={(e) => handleItemChange(idx, "product", e.target.value)}
                                required
                            >
                                <option value="">Select product</option>
                                {products.map((p) => (
                                    <option key={p.id} value={p.id}>{p.name}</option>
                                ))}
                            </select>
                            <input
                                type="number"
                                min={1}
                                value={item.requested_quantity}
                                onChange={(e) => handleItemChange(idx, "requested_quantity", e.target.value)}
                                required
                                placeholder="Quantity"
                            />
                            <input
                                type="text"
                                value={item.customer_notes}
                                onChange={(e) => handleItemChange(idx, "customer_notes", e.target.value)}
                                placeholder="Customer notes"
                            />
                            {items.length > 1 && (
                                <button
                                    type="button"
                                    onClick={() => removeItem(idx)}
                                    className="remove-item"
                                >
                                    Remove
                                </button>
                            )}
                        </div>
                    ))}
                    <button type="button" onClick={addItem} className="add-item">Add Item</button>
                    <div className="form-actions">
                        <button type="submit" disabled={loading} className="submit-order">
                            {loading ? "Submitting..." : "Create Order"}
                        </button>
                    </div>
                    {error && <div className="error-message">{error}</div>}
                </form>
            </div>
        </div>
    );
};

export default CreateOrderModal;
