import React, { useState, useEffect } from 'react';
import API from './api';
import NeoBrutalistDropdown from './NeoBrutalist/NeoBrutalistDropdown';
import NeoBrutalistButton from './NeoBrutalist/NeoBrutalistButton';
import NeoBrutalistInput from './NeoBrutalist/NeoBrutalistInput';
import '../styles/component/DealerAssignmentComponent.css'

const DealerAssignmentComponent = ({ orderId, onDealerAssigned }) => {
    const [wantDealer, setWantDealer] = useState('no');
    const [dealers, setDealers] = useState([]);
    const [selectedDealer, setSelectedDealer] = useState('');
    const [selectedDealerInfo, setSelectedDealerInfo] = useState(null);
    const [notes, setNotes] = useState('');
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState('');

    useEffect(() => {
        if (wantDealer === 'yes') {
            fetchDealers();
        }
    }, [wantDealer]);

    const fetchDealers = async () => {
        try {
            console.log('üîç Fetching dealers for assignment...');
            const response = await API.get('/dealers/list-for-assignment/');
            console.log('‚úÖ Dealers fetched:', response.data);
            setDealers(response.data.dealers || []);
        } catch (err) {
            console.error('‚ùå Error fetching dealers:', err);
            setError('ÿÆÿ∑ÿß ÿØÿ± ÿ®ÿßÿ±⁄Ø€åÿ±€å ŸÜŸÖÿß€åŸÜÿØ⁄ØÿßŸÜ ŸÅÿ±Ÿàÿ¥');
        }
    };

    const handleDealerSelect = (dealerId) => {
        setSelectedDealer(dealerId);
        const dealer = dealers.find(d => d.id.toString() === dealerId);
        setSelectedDealerInfo(dealer || null);
        console.log('üéØ Selected dealer:', dealer);
    };

    const handleAssignDealer = async () => {
        if (!selectedDealer) {
            setError('ŸÑÿ∑ŸÅÿßŸã ŸÜŸÖÿß€åŸÜÿØŸá‚Äåÿß€å ÿ±ÿß ÿßŸÜÿ™ÿÆÿßÿ® ⁄©ŸÜ€åÿØ');
            return;
        }

        if (!selectedDealerInfo || selectedDealerInfo.commission_rate <= 0) {
            setError('ŸÜŸÖÿß€åŸÜÿØŸá ÿßŸÜÿ™ÿÆÿßÿ®€å ŸÜÿ±ÿÆ ⁄©ŸÖ€åÿ≥€åŸàŸÜ ŸÖÿπÿ™ÿ®ÿ±€å ŸÜÿØÿßÿ±ÿØ. ŸÑÿ∑ŸÅÿßŸã ÿßÿ®ÿ™ÿØÿß ŸÜÿ±ÿÆ ⁄©ŸÖ€åÿ≥€åŸàŸÜ ÿ±ÿß ÿ™ŸÜÿ∏€åŸÖ ⁄©ŸÜ€åÿØ.');
            return;
        }

        setLoading(true);
        setError('');

        try {
            console.log('üì§ Assigning dealer to order:', {
                orderId,
                dealer_id: parseInt(selectedDealer),
                dealer_notes: notes,
                commission_rate: selectedDealerInfo.commission_rate
            });

            const response = await API.post(`/orders/${orderId}/assign-dealer/`, {
                dealer_id: parseInt(selectedDealer),
                dealer_notes: notes
            });

            console.log('‚úÖ Dealer assigned successfully:', response.data);

            const successMessage = `ŸÜŸÖÿß€åŸÜÿØŸá ${selectedDealerInfo.name} ÿ®ÿß ŸÖŸàŸÅŸÇ€åÿ™ ÿ™ÿÆÿµ€åÿµ ÿØÿßÿØŸá ÿ¥ÿØ!\nŸÜÿ±ÿÆ ⁄©ŸÖ€åÿ≥€åŸàŸÜ: ${selectedDealerInfo.commission_rate}%`;
            alert(successMessage);

            setWantDealer('no');
            setSelectedDealer('');
            setSelectedDealerInfo(null);
            setNotes('');

            if (onDealerAssigned) {
                onDealerAssigned();
            }

        } catch (err) {
            console.error('‚ùå Error assigning dealer:', err);
            const errorMessage = err.response?.data?.error ||
                err.response?.data?.details ||
                'ÿÆÿ∑ÿß ÿØÿ± ÿ™ÿÆÿµ€åÿµ ŸÜŸÖÿß€åŸÜÿØŸá';
            setError(errorMessage);
        } finally {
            setLoading(false);
        }
    };

    const dealerOptions = dealers.map(dealer => ({
        value: dealer.id.toString(),
        label: `${dealer.name} - ${dealer.email} (${dealer.assigned_orders_count || 0} ÿ≥ŸÅÿßÿ±ÿ¥) - ⁄©ŸÖ€åÿ≥€åŸàŸÜ: ${dealer.commission_rate}%`
    }));

