import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import API from '../component/api';
import NeoBrutalistModal from "../component/NeoBrutalist/NeoBrutalistModal";
import CreateOrderPage from '../component/CreateOrderPage';
import OrderDetailPage from '../component/OrderDetailPage';
import NeoBrutalistCard from "../component/NeoBrutalist/NeoBrutalistCard";
import NeoBrutalistButton from "../component/NeoBrutalist/NeoBrutalistButton";
import '../styles/Main/dashboard.css';

const DashboardPage = () => {
    const [orders, setOrders] = useState([]);
    const [selectedOrder, setSelectedOrder] = useState(null);
    const [showCreateOrder, setShowCreateOrder] = useState(false);
    const [message, setMessage] = useState('');
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');
    const [activeTab, setActiveTab] = useState('active'); // 'active', 'completed', 'rejected'
    const navigate = useNavigate();

    useEffect(() => {
        const checkAuth = () => {
            const token = localStorage.getItem('access_token');
            const userData = localStorage.getItem('userData');

            if (!token || !userData) {
                handleLogout();
                return false;
            }
            return true;
        };

        if (checkAuth()) {
            fetchOrders();
        }
    }, [navigate]);

    const fetchOrders = async () => {
        setLoading(true);
        setError('');

        try {
            const response = await API.get('/orders/');
            console.log('üì¶ Orders fetched:', response.data);
            setOrders(response.data);
        } catch (error) {
            console.error('‚ùå Error fetching orders:', error);
            if (error.response?.status === 401) {
                setError('ÿ¨ŸÑÿ≥Ÿá ÿ¥ŸÖÿß ŸÖŸÜŸÇÿ∂€å ÿ¥ÿØŸá ÿßÿ≥ÿ™. ŸÑÿ∑ŸÅÿßŸã ÿØŸàÿ®ÿßÿ±Ÿá Ÿàÿßÿ±ÿØ ÿ¥Ÿà€åÿØ.');
                setTimeout(() => handleLogout(), 2000);
            } else {
                setError('ÿÆÿ∑ÿß ÿØÿ± ÿ®ÿßÿ±⁄Ø€åÿ±€å ÿ≥ŸÅÿßÿ±ÿ¥ÿßÿ™. ŸÑÿ∑ŸÅÿßŸã ÿØŸàÿ®ÿßÿ±Ÿá ÿ™ŸÑÿßÿ¥ ⁄©ŸÜ€åÿØ.');
            }
        } finally {
            setLoading(false);
        }
    };

    // Filter orders by status
    const getFilteredOrders = () => {
        switch (activeTab) {
            case 'active':
                return orders.filter(order =>
                    ['pending_pricing', 'waiting_customer_approval', 'confirmed'].includes(order.status)
                );
            case 'completed':
                return orders.filter(order => order.status === 'completed');
            case 'rejected':
                return orders.filter(order =>
                    ['rejected', 'cancelled'].includes(order.status)
                );
            default:
                return orders;
        }
    };

    const handleOrderCreated = () => {
        setMessage('ÿ≥ŸÅÿßÿ±ÿ¥ ÿ®ÿß ŸÖŸàŸÅŸÇ€åÿ™ ÿ´ÿ®ÿ™ ÿ¥ÿØ! ÿ®Ÿá ÿ≤ŸàÿØ€å ÿ±ÿ≥€åÿØ⁄Ø€å ŸÖ€åÿ¥ŸàÿØ');
        setShowCreateOrder(false);
        fetchOrders();
    };

    const handleLogout = () => {
        localStorage.removeItem('access_token');
        localStorage.removeItem('refresh_token');
        localStorage.removeItem('userData');
        delete API.defaults.headers.common['Authorization'];
        navigate('/');
    };

    const getUserInfo = () => {
        const userDataString = localStorage.getItem('userData');
        return userDataString ? JSON.parse(userDataString) : null;
    };

    const getStatusColor = (status) => {
        switch (status) {
            case 'pending_pricing':
                return 'yellow-400';
            case 'waiting_customer_approval':
                return 'blue-400';
            case 'confirmed':
                return 'green-400';
            case 'completed':
                return 'green-600';
            case 'rejected':
                return 'red-400';
            case 'cancelled':
                return 'gray-400';
            default:
                return 'gray-400';
        }
    };

    const formatStatus = (status) => {
        const statusMap = {
            'pending_pricing': 'ÿØÿ± ÿßŸÜÿ™ÿ∏ÿßÿ± ŸÇ€åŸÖÿ™‚Äå⁄Øÿ∞ÿßÿ±€å',
            'waiting_customer_approval': 'ÿØÿ± ÿßŸÜÿ™ÿ∏ÿßÿ± ÿ™ÿ£€å€åÿØ',
            'confirmed': 'ÿ™ÿ£€å€åÿØ ÿ¥ÿØŸá',
            'completed': 'ÿ™⁄©ŸÖ€åŸÑ ÿ¥ÿØŸá',
            'rejected': 'ÿ±ÿØ ÿ¥ÿØŸá',
            'cancelled': 'ŸÑÿ∫Ÿà ÿ¥ÿØŸá'
        };
        return statusMap[status] || status;
    };

    // Download PDF function for completed orders
    const handleDownloadPDF = async (order, e) => {
        e.stopPropagation();
        try {
            if (!order.invoice_id) {
                setError('ŸÅÿß⁄©ÿ™Ÿàÿ± ÿ®ÿ±ÿß€å ÿß€åŸÜ ÿ≥ŸÅÿßÿ±ÿ¥ ŸÖŸàÿ¨ŸàÿØ ŸÜ€åÿ≥ÿ™');
                return;
            }

            const response = await API.get(`/invoices/${order.invoice_id}/download-pdf/`, {
                responseType: 'blob'
            });

            const blob = new Blob([response.data], { type: 'application/pdf' });
            const url = window.URL.createObjectURL(blob);
            const link = document.createElement('a');
            link.href = url;
            link.download = `invoice_${order.id}.pdf`;
            document.body.appendChild(link);
            link.click();
            link.remove();
            window.URL.revokeObjectURL(url);
        } catch (error) {
            setError('ÿÆÿ∑ÿß ÿØÿ± ÿØÿßŸÜŸÑŸàÿØ ŸÅÿß⁄©ÿ™Ÿàÿ±');
        }
    };

    const filteredOrders = getFilteredOrders();

    if (loading) {
        return (
            <div className="dashboard-container">
                <div className="dashboard-header">
                    <h1>ÿØÿ± ÿ≠ÿßŸÑ ÿ®ÿßÿ±⁄Ø€åÿ±€å...</h1>
                </div>
            </div>
        );
    }

    return (
        <div className="dashboard-container">
            {/* Header */}
            <div className="dashboard-header">
                <div className="user-info">
                    <h1>ÿØÿßÿ¥ÿ®Ÿàÿ±ÿØ</h1>
                    <span className="welcome-text">{getUserInfo()?.name} ÿÆŸàÿ¥ ÿ¢ŸÖÿØ€å</span>
                </div>
                <div className="header-actions">
                    <NeoBrutalistButton
                        text="ÿß€åÿ¨ÿßÿØ ÿ≥ŸÅÿßÿ±ÿ¥"
                        color="yellow-400"
                        textColor="black"
                        onClick={() => setShowCreateOrder(true)}
                        className="create-order-btn"
                    />
                    <NeoBrutalistButton
                        text="ÿÆÿ±Ÿàÿ¨"
                        color="red-400"
                        textColor="white"
                        onClick={handleLogout}
                        className="logout-btn"
                    />
                </div>
            </div>

            {/* Messages */}
            {message && (
                <div className="message-banner">
                    <span>{message}</span>
                    <NeoBrutalistButton
                        text="√ó"
                        color="white"
                        textColor="black"
                        onClick={() => setMessage('')}
                        className="close-message-btn"
                    />
                </div>
            )}

            {error && (
                <div className="message-banner" style={{ backgroundColor: '#fef2f2', borderColor: '#ef4444', color: '#dc2626' }}>
                    <span>{error}</span>
                    <NeoBrutalistButton
                        text="√ó"
                        color="white"
                        textColor="black"
                        onClick={() => setError('')}
                        className="close-message-btn"
                    />
                </div>
            )}

            {/* Order Tabs */}
            <div className="dashboard-tabs">
                <NeoBrutalistButton
                    text={`ÿ≥ŸÅÿßÿ±ÿ¥ÿßÿ™ ŸÅÿπÿßŸÑ (${orders.filter(o => ['pending_pricing', 'waiting_customer_approval', 'confirmed'].includes(o.status)).length})`}
                    color={activeTab === 'active' ? 'yellow-400' : 'gray-400'}
                    textColor="black"
                    onClick={() => setActiveTab('active')}
                    className="tab-btn"
                />
                <NeoBrutalistButton
                    text={`ÿ™⁄©ŸÖ€åŸÑ ÿ¥ÿØŸá (${orders.filter(o => o.status === 'completed').length})`}
                    color={activeTab === 'completed' ? 'green-400' : 'gray-400'}
                    textColor="black"
                    onClick={() => setActiveTab('completed')}
                    className="tab-btn"
                />
                <NeoBrutalistButton
                    text={`ÿ±ÿØ ÿ¥ÿØŸá (${orders.filter(o => ['rejected', 'cancelled'].includes(o.status)).length})`}
                    color={activeTab === 'rejected' ? 'red-400' : 'gray-400'}
                    textColor="black"
                    onClick={() => setActiveTab('rejected')}
                    className="tab-btn"
                />
            </div>

            {/* Orders Grid */}
            <div className="orders-grid">
                {filteredOrders.map((order) => (
                    <NeoBrutalistCard
                        key={order.id}
                        className="order-card"
                        onClick={() => setSelectedOrder(order)}
                    >
                        <div className="order-card-header">
                            <h3>ÿ≥ŸÅÿßÿ±ÿ¥ #{order.id}</h3>
                            <NeoBrutalistButton
                                text={formatStatus(order.status)}
                                color={getStatusColor(order.status)}
                                textColor="black"
                                className="status-badge"
                            />
                        </div>

                        <div className="order-card-info">
                            <p><strong>ÿ™ÿßÿ±€åÿÆ ÿß€åÿ¨ÿßÿØ:</strong> {new Date(order.created_at).toLocaleDateString('fa-IR')}</p>

                            {/* üéØ NEW: Show who priced the order */}
                            {order.priced_by_name && (
                                <div style={{
                                    backgroundColor: '#e0f2fe',
                                    border: '1px solid #0284c7',
                                    padding: '0.5rem',
                                    marginTop: '0.5rem',
                                    fontSize: '0.9rem',
                                    borderRadius: '4px'
                                }}>
                                    <div style={{ color: '#0369a1', fontWeight: 'bold' }}>
                                        üíº ŸÇ€åŸÖÿ™‚Äå⁄Øÿ∞ÿßÿ±€å ÿ™Ÿàÿ≥ÿ∑: {order.priced_by_name}
                                    </div>
                                    {order.pricing_date && (
                                        <div style={{ fontSize: '0.8rem', color: '#0284c7' }}>
                                            ÿ™ÿßÿ±€åÿÆ: {new Date(order.pricing_date).toLocaleDateString('fa-IR')}
                                        </div>
                                    )}
                                </div>
                            )}

                            {/* üéØ NEW: Show dealer info if assigned */}
                            {order.assigned_dealer_name && (
                                <div style={{
                                    backgroundColor: '#f3e8ff',
                                    border: '1px solid #8b5cf6',
                                    padding: '0.5rem',
                                    marginTop: '0.5rem',
                                    fontSize: '0.9rem',
                                    borderRadius: '4px'
                                }}>
                                    <div style={{ color: '#7c3aed', fontWeight: 'bold' }}>
                                        üë§ ŸÜŸÖÿß€åŸÜÿØŸá ŸÅÿ±Ÿàÿ¥: {order.assigned_dealer_name}
                                    </div>
                                    {order.dealer_assigned_at && (
                                        <div style={{ fontSize: '0.8rem', color: '#6b21a8' }}>
                                            ÿ™ÿßÿ±€åÿÆ ÿ™ÿÆÿµ€åÿµ: {new Date(order.dealer_assigned_at).toLocaleDateString('fa-IR')}
                                        </div>
                                    )}
                                    {order.assigned_dealer_code && (
                                        <div style={{ fontSize: '0.8rem', color: '#6b21a8' }}>
                                            ⁄©ÿØ ŸÜŸÖÿß€åŸÜÿØŸá: {order.assigned_dealer_code}
                                        </div>
                                    )}
                                </div>
                            )}

                            <p style={{ marginTop: '0.5rem' }}>
                                <strong>ÿ¨ŸÖÿπ:</strong> {order.quoted_total ? `${order.quoted_total.toLocaleString('fa-IR')} ÿ±€åÿßŸÑ` : 'ÿØÿ± ÿßŸÜÿ™ÿ∏ÿßÿ± ŸÇ€åŸÖÿ™‚Äå⁄Øÿ∞ÿßÿ±€å'}
                            </p>

                            {order.customer_comment && (
                                <p><strong>ÿ™Ÿàÿ∂€åÿ≠ÿßÿ™:</strong> {order.customer_comment.substring(0, 50)}...</p>
                            )}

                            {/* Show completion date for completed orders */}
                            {order.status === 'completed' && order.completion_date && (
                                <p style={{ color: '#16a34a', fontWeight: 'bold' }}>
                                    <strong>ÿ™ÿßÿ±€åÿÆ ÿ™⁄©ŸÖ€åŸÑ:</strong> {new Date(order.completion_date).toLocaleDateString('fa-IR')}
                                </p>
                            )}

                            {/* üéØ NEW: Show admin comments for customer transparency */}
                            {order.admin_comment && order.status !== 'pending_pricing' && (
                                <div style={{
                                    backgroundColor: '#fff7ed',
                                    border: '1px solid #f97316',
                                    padding: '0.5rem',
                                    marginTop: '0.5rem',
                                    fontSize: '0.85rem',
                                    borderRadius: '4px'
                                }}>
                                    <div style={{ color: '#c2410c', fontWeight: 'bold', marginBottom: '0.25rem' }}>
                                        üìù ŸÜÿ∏ÿ± ŸÖÿØ€åÿ±:
                                    </div>
                                    <div style={{ color: '#9a3412' }}>
                                        {order.admin_comment.length > 60
                                            ? `${order.admin_comment.substring(0, 60)}...`
                                            : order.admin_comment
                                        }
                                    </div>
                                </div>
                            )}
                        </div>

                        <div className="order-card-footer">
                            <NeoBrutalistButton
                                text="ŸÖÿ¥ÿßŸáÿØŸá ÿ¨ÿ≤ÿ¶€åÿßÿ™"
                                color="blue-400"
                                textColor="white"
                                className="view-details-btn"
                                onClick={(e) => {
                                    e.stopPropagation();
                                    setSelectedOrder(order);
                                }}
                            />
                            {/* Download PDF button for completed orders */}
                            {order.status === 'completed' && order.invoice_id && (
                                <NeoBrutalistButton
                                    text="ÿØÿßŸÜŸÑŸàÿØ ŸÅÿß⁄©ÿ™Ÿàÿ±"
                                    color="green-400"
                                    textColor="white"
                                    className="download-btn"
                                    onClick={(e) => handleDownloadPDF(order, e)}
                                />
                            )}
                        </div>
                    </NeoBrutalistCard>
                ))}
            </div>

            {/* Empty State */}
            {filteredOrders.length === 0 && !error && (
                <div className="empty-state">
                    <NeoBrutalistCard className="empty-card">
                        <h3>
                            {activeTab === 'active' && 'Ÿá€å⁄Ü ÿ≥ŸÅÿßÿ±ÿ¥ ŸÅÿπÿßŸÑ€å ŸÜÿØÿßÿ±€åÿØ'}
                            {activeTab === 'completed' && 'Ÿá€å⁄Ü ÿ≥ŸÅÿßÿ±ÿ¥ ÿ™⁄©ŸÖ€åŸÑ ÿ¥ÿØŸá‚Äåÿß€å ŸÜÿØÿßÿ±€åÿØ'}
                            {activeTab === 'rejected' && 'Ÿá€å⁄Ü ÿ≥ŸÅÿßÿ±ÿ¥ ÿ±ÿØ ÿ¥ÿØŸá‚Äåÿß€å ŸÜÿØÿßÿ±€åÿØ'}
                        </h3>
                        <p>
                            {activeTab === 'active' && 'ÿ®ÿ±ÿß€å ÿ¥ÿ±Ÿàÿπ ÿ±Ÿà€å "ÿß€åÿ¨ÿßÿØ ÿ≥ŸÅÿßÿ±ÿ¥" ⁄©ŸÑ€å⁄© ⁄©ŸÜ€åÿØ!'}
                            {activeTab === 'completed' && 'ÿ≥ŸÅÿßÿ±ÿ¥ÿßÿ™ ÿ™⁄©ŸÖ€åŸÑ ÿ¥ÿØŸá ÿ¥ŸÖÿß ÿØÿ± ÿß€åŸÜÿ¨ÿß ŸÜŸÖÿß€åÿ¥ ÿØÿßÿØŸá ŸÖ€å‚Äåÿ¥ŸàÿØ.'}
                            {activeTab === 'rejected' && 'ÿ≥ŸÅÿßÿ±ÿ¥ÿßÿ™ ÿ±ÿØ ÿ¥ÿØŸá €åÿß ŸÑÿ∫Ÿà ÿ¥ÿØŸá ÿ¥ŸÖÿß ÿØÿ± ÿß€åŸÜÿ¨ÿß ŸÜŸÖÿß€åÿ¥ ÿØÿßÿØŸá ŸÖ€å‚Äåÿ¥ŸàÿØ.'}
                        </p>
                        {activeTab === 'active' && (
                            <NeoBrutalistButton
                                text="ÿ´ÿ®ÿ™ ÿßŸàŸÑ€åŸÜ ÿ≥ŸÅÿßÿ±ÿ¥"
                                color="yellow-400"
                                textColor="black"
                                onClick={() => setShowCreateOrder(true)}
                                className="first-order-btn"
                            />
                        )}
                    </NeoBrutalistCard>
                </div>
            )}

            {/* Modals */}
            <NeoBrutalistModal
                isOpen={showCreateOrder}
                onClose={() => setShowCreateOrder(false)}
                title=""
                size="large"
            >
                <CreateOrderPage onOrderCreated={handleOrderCreated} />
            </NeoBrutalistModal>

            <NeoBrutalistModal
                isOpen={!!selectedOrder}
                onClose={() => setSelectedOrder(null)}
                title={selectedOrder ? `ÿ¨ÿ≤ÿ¶€åÿßÿ™ ÿ≥ŸÅÿßÿ±ÿ¥ #${selectedOrder.id}` : ""}
                size="large"
            >
                {selectedOrder && (
                    <OrderDetailPage
                        orderId={selectedOrder.id}
                        onOrderUpdated={handleOrderCreated}
                    />
                )}
            </NeoBrutalistModal>
        </div>
    );
};

export default DashboardPage;
