import React from 'react';
import '../../styles/NeoBrutalistCSS/toggle.css';

const NeoBrutalistToggle = ({
                                checked,
                                onChange,
                                label,
                                className = '',
                                disabled = false,
                                color = 'green',
                                name,
                                ...props
                            }) => {
    const uniqueId = React.useId();

    const toggleClasses = [
        'neo-brutalist-toggle',
        color,
        disabled && 'disabled',
        className
    ].filter(Boolean).join(' ');

    const handleToggleClick = (e) => {
        // COMPLETELY prevent any form behavior
        e.preventDefault();
        e.stopPropagation();
        e.nativeEvent?.stopImmediatePropagation();

        if (!disabled && onChange) {
            // Call onChange with proper event structure
            onChange({
                target: {
                    checked: !checked,
                    name: name || '',
                    type: 'checkbox',
                    value: !checked
                },
                preventDefault: () => {},
                stopPropagation: () => {}
            });
        }

        // Return false to prevent any default behavior
        return false;
    };

    const handleKeyPress = (e) => {
        if (e.key === ' ' || e.key === 'Enter') {
            e.preventDefault();
            e.stopPropagation();
            handleToggleClick(e);
        }
    };

    return (
        <div
            className={toggleClasses}
            onClick={handleToggleClick}
            onKeyDown={handleKeyPress}
            tabIndex={disabled ? -1 : 0}
            role="switch"
            aria-checked={checked}
            aria-label={label}
            style={{ outline: 'none' }}
        >
            {/* Hidden input for form compatibility - but controlled externally */}
            <input
                type="checkbox"
                checked={checked}
                onChange={() => {}} // No-op - controlled by div click
                style={{ display: 'none' }}
                tabIndex={-1}
                name={name}
                {...props}
            />

            <div className="toggle-track">
                <div className="toggle-knob"></div>
            </div>

            {label && (
                <span className="toggle-label">{label}</span>
            )}
        </div>
    );
};

export default NeoBrutalistToggle;
