import React, { useState, useEffect, useRef } from 'react';
import API from './api';
import NeoBrutalistCard from './NeoBrutalist/NeoBrutalistCard';
import NeoBrutalistButton from './NeoBrutalist/NeoBrutalistButton';
import NeoBrutalistInput from './NeoBrutalist/NeoBrutalistInput';
import PaymentReceiptUploadModal from './PaymentReceiptUploadModal';
import CustomerInfoManagement from './CustomerInfoManagement';
import '../styles/component/CustomerComponent/OrderDetail.css';

const AuthenticatedImage = ({ receipt, onError }) => {
    const [imageData, setImageData] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        if (receipt && receipt.file_type === 'image') {
            loadAuthenticatedImage();
        }
    }, [receipt]);

    const loadAuthenticatedImage = async () => {
        try {
            setLoading(true);
            setError(null);

            const token = localStorage.getItem('access_token');
            if (!token) {
                throw new Error('Authentication token not found');
            }

            const imageUrl = receipt.download_url;

            if (!imageUrl) {
                throw new Error('Download URL not available');
            }

            const response = await fetch(imageUrl, {
                method: 'GET',
                headers: {
                    'Authorization': `Bearer ${token}`,
                },
            });

            if (!response.ok) {
                throw new Error(`HTTP ${response.status}: ${response.statusText}`);
            }

            const blob = await response.blob();
            const objectUrl = URL.createObjectURL(blob);
            setImageData(objectUrl);

        } catch (err) {
            console.error('‚ùå Error loading authenticated image:', err);
            setError(err.message);
            if (onError) {
                onError(err);
            }
        } finally {
            setLoading(false);
        }
    };

    useEffect(() => {
        return () => {
            if (imageData) {
                URL.revokeObjectURL(imageData);
            }
        };
    }, [imageData]);

    if (loading) {
        return (
            <div className="neo-image-loading" style={{
                textAlign: 'center',
                padding: '20px',
                border: '2px dashed #ccc',
                borderRadius: '8px',
                backgroundColor: '#f9f9f9'
            }}>
                <div style={{ fontSize: '24px', marginBottom: '8px' }}>üîÑ</div>
                <span>ÿØÿ± ÿ≠ÿßŸÑ ÿ®ÿßÿ±⁄Ø€åÿ±€å ÿ™ÿµŸà€åÿ±...</span>
            </div>
        );
    }

    if (error) {
        return (
            <div className="neo-image-error" style={{
                textAlign: 'center',
                padding: '20px',
                color: '#ef4444',
                border: '2px solid #ef4444',
                borderRadius: '8px',
                backgroundColor: '#fef2f2'
            }}>
                <div style={{ fontSize: '24px', marginBottom: '8px' }}>‚ö†Ô∏è</div>
                <div style={{ marginBottom: '10px' }}>ÿÆÿ∑ÿß ÿØÿ± ÿ®ÿßÿ±⁄Ø€åÿ±€å ÿ™ÿµŸà€åÿ±</div>
                <div style={{ fontSize: '12px', color: '#666', marginBottom: '10px' }}>
                    {error}
                </div>
                <button
                    onClick={loadAuthenticatedImage}
                    style={{
                        padding: '8px 16px',
                        backgroundColor: '#3b82f6',
                        color: 'white',
                        border: 'none',
                        borderRadius: '4px',
                        cursor: 'pointer',
                        fontSize: '14px'
                    }}
                >
                    ÿ™ŸÑÿßÿ¥ ŸÖÿ¨ÿØÿØ
                </button>
            </div>
        );
    }

    if (!imageData) {
        return (
            <div className="neo-image-placeholder" style={{
                textAlign: 'center',
                padding: '20px',
                color: '#6b7280',
                border: '2px dashed #d1d5db',
                borderRadius: '8px',
                backgroundColor: '#f9fafb'
            }}>
                <span>ÿ™ÿµŸà€åÿ± ÿØÿ± ÿØÿ≥ÿ™ÿ±ÿ≥ ŸÜ€åÿ≥ÿ™</span>
            </div>
        );
    }

    return (
        <div className="neo-image-container">
            <img
                src={imageData}
                alt={`ÿ±ÿ≥€åÿØ Ÿæÿ±ÿØÿßÿÆÿ™ ${receipt.file_name}`}
                className="neo-receipt-image"
                onClick={() => window.open(imageData, '_blank')}
                style={{
                    width: '100%',
                    maxWidth: '100%',
                    maxHeight: '400px',
                    objectFit: 'contain',
                    cursor: 'pointer',
                    border: '2px solid #000',
                    borderRadius: '8px',
                    backgroundColor: '#fff'
                }}
                onLoad={() => console.log('‚úÖ Image rendered successfully')}
                onError={(e) => {
                    console.error('‚ùå Image render error:', e);
                    setError('ÿÆÿ∑ÿß ÿØÿ± ŸÜŸÖÿß€åÿ¥ ÿ™ÿµŸà€åÿ±');
                }}
            />
        </div>
    );
};

const OrderDetailPage = ({ orderId, onOrderUpdated }) => {
    const [order, setOrder] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');
    const [approvalDecision, setApprovalDecision] = useState('');
    const [rejectionReason, setRejectionReason] = useState('');
    const [submitting, setSubmitting] = useState(false);
    const [generatingInvoice, setGeneratingInvoice] = useState(false);

    // Payment upload modal state
    const [isPaymentModalOpen, setIsPaymentModalOpen] = useState(false);

    // Customer info management
    const [isCustomerInfoModalOpen, setIsCustomerInfoModalOpen] = useState(false);
    const [customerInfo, setCustomerInfo] = useState(null);
    const [loadingCustomerInfo, setLoadingCustomerInfo] = useState(false);

    // Payment receipts management
    const [paymentReceipts, setPaymentReceipts] = useState([]);
    const [loadingReceipts, setLoadingReceipts] = useState(false);
    const [receiptsError, setReceiptsError] = useState('');

    const tableRef = useRef(null);

    useEffect(() => {
        if (orderId) {
            fetchOrder();
            fetchCustomerInfo();
        }
    }, [orderId]);

    useEffect(() => {
        if (order?.items && tableRef.current) {
            applySmartTextSizing();
        }
    }, [order]);

    useEffect(() => {
        if (order && (order.status === 'payment_uploaded' || order.has_payment_receipts || order.payment_receipts?.length > 0)) {
            fetchPaymentReceipts();
        }
    }, [order]);

    const fetchOrder = async () => {
        setLoading(true);
        setError('');
        try {
            const response = await API.get(`/orders/${orderId}/`);
            setOrder(response.data);
            console.log('‚úÖ Order fetched:', response.data);
        } catch (err) {
            console.error('‚ùå Error fetching order:', err);
            setError('ÿÆÿ∑ÿß ÿØÿ± ÿ®ÿßÿ±⁄Ø€åÿ±€å ÿ¨ÿ≤ÿ¶€åÿßÿ™ ÿ≥ŸÅÿßÿ±ÿ¥');
        } finally {
            setLoading(false);
        }
    };

    // FIXED: Fetch customer info separately
    const fetchCustomerInfo = async () => {
        setLoadingCustomerInfo(true);
        try {
            const response = await API.get('/customers/invoice-info/');
            if (response.status === 200) {
                setCustomerInfo(response.data.customer_info);
                console.log('‚úÖ Customer info fetched:', response.data.customer_info);
            }
        } catch (err) {
            console.error('‚ùå Error fetching customer info:', err);
            // Don't show error for missing customer info
        } finally {
            setLoadingCustomerInfo(false);
        }
    };

    const fetchPaymentReceipts = async () => {
        setLoadingReceipts(true);
        setReceiptsError('');
        try {
            const response = await API.get(`/orders/${orderId}/payment-receipts/`);
            setPaymentReceipts(response.data.receipts || []);
        } catch (err) {
            console.error('‚ùå Error fetching payment receipts:', err);
            setReceiptsError('ÿÆÿ∑ÿß ÿØÿ± ÿ®ÿßÿ±⁄Ø€åÿ±€å ÿ±ÿ≥€åÿØŸáÿß€å Ÿæÿ±ÿØÿßÿÆÿ™');
        } finally {
            setLoadingReceipts(false);
        }
    };

    const deletePaymentReceipt = async (receiptId) => {
        if (!window.confirm('ÿ¢€åÿß ÿßÿ≤ ÿ≠ÿ∞ŸÅ ÿß€åŸÜ ÿ±ÿ≥€åÿØ ÿßÿ∑ŸÖ€åŸÜÿßŸÜ ÿØÿßÿ±€åÿØÿü')) {
            return;
        }

        try {
            await API.delete(`/orders/${orderId}/delete-payment-receipt/${receiptId}/`);
            setPaymentReceipts(prev => prev.filter(receipt => receipt.id !== receiptId));
            fetchOrder();
            alert('ÿ±ÿ≥€åÿØ ÿ®ÿß ŸÖŸàŸÅŸÇ€åÿ™ ÿ≠ÿ∞ŸÅ ÿ¥ÿØ');
        } catch (err) {
            console.error('‚ùå Error deleting payment receipt:', err);
            alert('ÿÆÿ∑ÿß ÿØÿ± ÿ≠ÿ∞ŸÅ ÿ±ÿ≥€åÿØ Ÿæÿ±ÿØÿßÿÆÿ™');
        }
    };

    const handleDownloadReceipt = async (receipt) => {
        try {
            const downloadUrl = receipt.download_url;

            if (!downloadUrl) {
                alert('ŸÑ€åŸÜ⁄© ÿØÿßŸÜŸÑŸàÿØ ÿØÿ± ÿØÿ≥ÿ™ÿ±ÿ≥ ŸÜ€åÿ≥ÿ™');
                return;
            }

            const token = localStorage.getItem('access_token');
            if (!token) {
                alert('ŸÑÿ∑ŸÅÿßŸã ÿØŸàÿ®ÿßÿ±Ÿá Ÿàÿßÿ±ÿØ ÿ¥Ÿà€åÿØ');
                return;
            }

            const response = await fetch(downloadUrl, {
                method: 'GET',
                headers: {
                    'Authorization': `Bearer ${token}`,
                },
            });

            if (!response.ok) {
                throw new Error(`HTTP ${response.status}: ${response.statusText}`);
            }

            const blob = await response.blob();
            const blobUrl = URL.createObjectURL(blob);
            const link = document.createElement('a');
            link.href = blobUrl;
            link.download = receipt.file_name;
            document.body.appendChild(link);
            link.click();
            document.body.removeChild(link);
            URL.revokeObjectURL(blobUrl);

        } catch (error) {
            console.error('‚ùå Error downloading file:', error);
            alert('ÿÆÿ∑ÿß ÿØÿ± ÿØÿßŸÜŸÑŸàÿØ ŸÅÿß€åŸÑ');
        }
    };

    const handleViewPDF = async (receipt) => {
        try {
            const viewUrl = receipt.file_url;

            if (!viewUrl) {
                alert('ŸÅÿß€åŸÑ PDF ÿØÿ± ÿØÿ≥ÿ™ÿ±ÿ≥ ŸÜ€åÿ≥ÿ™');
                return;
            }

            const token = localStorage.getItem('access_token');
            if (!token) {
                alert('ŸÑÿ∑ŸÅÿßŸã ÿØŸàÿ®ÿßÿ±Ÿá Ÿàÿßÿ±ÿØ ÿ¥Ÿà€åÿØ');
                return;
            }

            const response = await fetch(viewUrl, {
                method: 'GET',
                headers: {
                    'Authorization': `Bearer ${token}`,
                },
            });

            if (!response.ok) {
                throw new Error(`HTTP ${response.status}: ${response.statusText}`);
            }

            const blob = await response.blob();
            const blobUrl = URL.createObjectURL(blob);
            const newWindow = window.open(blobUrl, '_blank');

            if (!newWindow) {
                const link = document.createElement('a');
                link.href = blobUrl;
                link.download = receipt.file_name;
                link.click();
                URL.revokeObjectURL(blobUrl);
            } else {
                setTimeout(() => {
                    URL.revokeObjectURL(blobUrl);
                }, 60000);
            }

        } catch (error) {
            console.error('‚ùå Error viewing PDF:', error);
            alert('ÿÆÿ∑ÿß ÿØÿ± ŸÖÿ¥ÿßŸáÿØŸá ŸÅÿß€åŸÑ PDF');
        }
    };

    const renderReceiptPreview = (receipt, index) => {
        if (receipt.file_type === 'image') {
            return (
                <div className="neo-receipt-preview" key={`image-${receipt.id}`}>
                    <AuthenticatedImage
                        receipt={receipt}
                        onError={(err) => {
                            console.error(`Receipt ${receipt.id} image load error:`, err);
                        }}
                    />
                </div>
            );
        } else {
            return (
                <div className="neo-receipt-preview" key={`pdf-${receipt.id}`}>
                    <div className="neo-pdf-preview">
                        <p className="neo-pdf-name">{receipt.file_name}</p>
                        <NeoBrutalistButton
                            text=" ŸÖÿ¥ÿßŸáÿØŸá PDF"
                            color="blue-400"
                            textColor="white"
                            onClick={() => handleViewPDF(receipt)}
                            className="neo-pdf-view-btn"
                        />
                    </div>
                </div>
            );
        }
    };

    const applySmartTextSizing = () => {
        const productCells = tableRef.current?.querySelectorAll('.neo-table-cell:nth-child(1)');
        const notesCells = tableRef.current?.querySelectorAll('.neo-table-cell:nth-child(3)');

        productCells?.forEach(cell => {
            const textLength = cell.textContent.length;
            if (textLength > 30) {
                cell.style.fontSize = '0.7rem';
                cell.classList.add('text-overflow');
            } else if (textLength > 20) {
                cell.style.fontSize = '0.75rem';
            } else if (textLength > 15) {
                cell.style.fontSize = '0.8rem';
            }
        });

        notesCells?.forEach(cell => {
            const textLength = cell.textContent.length;
            if (textLength > 25) {
                cell.style.fontSize = '0.75rem';
                cell.classList.add('text-overflow');
            }
        });
    };

    const handleApprovalSubmit = async () => {
        if (!approvalDecision) {
            setError('ŸÑÿ∑ŸÅÿß ÿ™ÿß€å€åÿØ €åÿß ÿ±ÿØ ÿ±ÿß ÿßŸÜÿ™ÿÆÿßÿ® ⁄©ŸÜ€åÿØ');
            return;
        }

        if (approvalDecision === 'reject' && !rejectionReason.trim()) {
            setError('ŸÑÿ∑ŸÅÿß ÿØŸÑ€åŸÑ ÿ±ÿØ ÿ±ÿß ÿ∞⁄©ÿ± ⁄©ŸÜ€åÿØ');
            return;
        }

        setSubmitting(true);
        setError('');

        try {
            if (approvalDecision === 'approve') {
                await API.post(`/orders/${orderId}/approve/`);
            } else {
                await API.post(`/orders/${orderId}/reject/`, {
                    rejection_reason: rejectionReason
                });
            }

            if (onOrderUpdated) {
                onOrderUpdated();
            } else {
                fetchOrder();
            }
        } catch (err) {
            console.error('‚ùå Error submitting approval:', err);
            setError(err.response?.data?.error || 'ÿÆÿ∑ÿß ÿØÿ± ÿßÿ±ÿ≥ÿßŸÑ ÿ™ÿµŸÖ€åŸÖ');
        } finally {
            setSubmitting(false);
        }
    };

    const downloadInvoice = async () => {
        try {
            if (!order.invoice_id) {
