import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Modal from './Modal'; // You'll need to create this component

const DashboardPage = () => {
    const [orders, setOrders] = useState([]);
    const [selectedOrder, setSelectedOrder] = useState(null);
    const [showCreateOrder, setShowCreateOrder] = useState(false);
    const [message, setMessage] = useState('');

    // Fetch orders when component mounts
    useEffect(() => {
        fetchOrders();
    }, []);

    const fetchOrders = async () => {
        try {
            const response = await axios.get('http://localhost:8000/api/orders/');
            setOrders(response.data);
        } catch (error) {
            setMessage('Error loading orders');
        }
    };

    const handleOrderClick = (order) => {
        setSelectedOrder(order);
    };

    const handleCreateOrder = async (orderData) => {
        try {
            await axios.post('http://localhost:8000/api/orders/', orderData);
            setMessage('Order submitted successfully! We will provide the pricing soon.');
            setShowCreateOrder(false);
            fetchOrders(); // Refresh orders list
        } catch (error) {
            setMessage('Error creating order');
        }
    };

    const closePanel = () => {
        setSelectedOrder(null);
        setShowCreateOrder(false);
    };

    // Helper function to format status for display
    const formatStatus = (status) => {
        return status.split('_').map(word => 
            word.charAt(0).toUpperCase() + word.slice(1)
        ).join(' ');
    };

    return (
        <div className="dashboard-container">
            <div className="dashboard-header">
                <h1>My Orders</h1>
                <button 
                    className="create-order-button"
                    onClick={() => setShowCreateOrder(true)}
                >
                    Create New Order
                </button>
            </div>

            {message && (
                <div className="message-banner">
                    {message}
                    <button onClick={() => setMessage('')}>Ã—</button>
                </div>
            )}

            <div className="orders-list">
                {orders.map((order) => (
                    <div 
                        key={order.id}
                        className="order-card"
                        onClick={() => handleOrderClick(order)}
                    >
                        <div className="order-header">
                            <h3>Order #{order.id}</h3>
                            <span className={`status-badge ${order.status}`}>
                                {formatStatus(order.status)}
                            </span>
                        </div>
                        <div className="order-info">
                            <p>Created: {new Date(order.created_at).toLocaleDateString()}</p>
                            <p>Total: ${order.quoted_total}</p>
                        </div>
                    </div>
                ))}
            </div>

            {selectedOrder && (
                <Modal onClose={closePanel}>
                    <div className="order-details">
                        <h2>Order Details</h2>
                        <p><strong>Order ID:</strong> {selectedOrder.id}</p>
                        <p><strong>Created:</strong> {new Date(selectedOrder.created_at).toLocaleDateString()}</p>
                        <p><strong>Status:</strong> {formatStatus(selectedOrder.status)}</p>
                        <p><strong>Subtotal:</strong> ${selectedOrder.subtotal}</p>
                        <p><strong>Tax:</strong> ${selectedOrder.tax}</p>
                        <p><strong>Quoted Total:</strong> ${selectedOrder.quoted_total}</p>
                        <p><strong>Shipping Address:</strong> {selectedOrder.shipping_address}</p>
                        <p><strong>Customer Notes:</strong> {selectedOrder.customer_notes}</p>
                        {/* Add more details here as needed */}
                        <button onClick={closePanel}>Close</button>
                    </div>
                </Modal>
            )}

            {showCreateOrder && (
                <Modal onClose={closePanel}>
                    <CreateOrderForm 
                        onSubmit={handleCreateOrder} 
                        onCancel={closePanel} 
                    />
                </Modal>
            )}
        </div>
    );
};

const CreateOrderForm = ({ onSubmit, onCancel }) => {
    const [orderData, setOrderData] = useState({
        shipping_address: '',
        customer_notes: '',
        // Add other fields as necessary
    });

    const handleChange = (e) => {
        setOrderData({
            ...orderData,
            [e.target.name]: e.target.value,
        });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        onSubmit(orderData);
    };

    return (
        <div className="create-order-form">
            <h2>Create New Order</h2>
            <form onSubmit={handleSubmit}>
                <div className="form-group">
                    <label htmlFor="shipping_address">Shipping Address:</label>
                    <textarea
                        id="shipping_address"
                        name="shipping_address"
                        value={orderData.shipping_address}
                        onChange={handleChange}
                        required
                    />
                </div>
                <div className="form-group">
                    <label htmlFor="customer_notes">Customer Notes:</label>
                    <textarea
                        id="customer_notes"
                        name="customer_notes"
                        value={orderData.customer_notes}
                        onChange={handleChange}
                    />
                </div>
                {/* Add more fields as necessary */}
                <div className="form-actions">
                    <button type="submit">Submit Order</button>
                    <button type="button" onClick={onCancel}>Cancel</button>
                </div>
            </form>
        </div>
    );
};

export default DashboardPage;
