import React, { useRef, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import * as THREE from "three";

const MainPage = () => {
    const navigate = useNavigate();
    const canvasRef = useRef(null);

    useEffect(() => {
        const canvas = canvasRef.current;
        if (!canvas) return;

        const scene = new THREE.Scene();
        const camera = new THREE.PerspectiveCamera(75, window.innerWidth / window.innerHeight, 1, 1000);
        camera.position.z = 200;

        const renderer = new THREE.WebGLRenderer({ canvas, alpha: true });
        renderer.setSize(window.innerWidth, window.innerHeight);

        const particles = new THREE.Geometry();
        const particleMaterial = new THREE.PointsMaterial({ color: 0xffffff, size: 2 });

        for (let i = 0; i < 1000; i++) {
            const vertex = new THREE.Vector3(
                THREE.MathUtils.randFloatSpread(400),
                THREE.MathUtils.randFloatSpread(400),
                THREE.MathUtils.randFloatSpread(400)
            );
            particles.vertices.push(vertex);
        }

        const particleSystem = new THREE.Points(particles, particleMaterial);
        scene.add(particleSystem);

        let animationId;
        const animate = () => {
            animationId = requestAnimationFrame(animate);
            particleSystem.rotation.y += 0.0005;
            renderer.render(scene, camera);
        };
        animate();

        const handleResize = () => {
            camera.aspect = window.innerWidth / window.innerHeight;
            camera.updateProjectionMatrix();
            renderer.setSize(window.innerWidth, window.innerHeight);
        };
        window.addEventListener("resize", handleResize);

        return () => {
            cancelAnimationFrame(animationId);
            window.removeEventListener("resize", handleResize);
        };
    }, []);

    return (
        <div className="main-container" style={{ position: "relative", width: "100%", height: "100vh" }}>
            <canvas
                ref={canvasRef}
                className="canvas-container"
                style={{ position: "absolute", top: 0, left: 0, width: "100%", height: "100%", zIndex: 0 }}
            ></canvas>
            <div style={{ position: "relative", zIndex: 1 }}>
                <h2 className="main-title" id="mainTitle">Welcome</h2>
                <button
                    className="main-btn login-btn"
                    id="loginButton"
                    onClick={() => navigate("/login")}
                >
                    Login
                </button>
                <button
                    className="main-btn register-btn"
                    id="registerButton"
                    onClick={() => navigate("/register")}
                >
                    Register
                </button>
            </div>
        </div>
    );
};

export default MainPage;
