import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import API from '../../component/api';
import { useAuth } from '../../hooks/useAuth';
import { useCategories } from '../../hooks/useCategories';
import NeoBrutalistCard from '../../component/NeoBrutalist/NeoBrutalistCard';
import NeoBrutalistButton from '../../component/NeoBrutalist/NeoBrutalistButton';
import NeoBrutalistModal from '../../component/NeoBrutalist/NeoBrutalistModal';
import '../../styles/component/NewArrivalsPage.css';

const NewArrivalsPage = () => {
    // ... (keep all existing state declarations)
    const [shipmentAnnouncements, setShipmentAnnouncements] = useState([]);
    const [featuredProducts, setFeaturedProducts] = useState([]);
    const { categories } = useCategories();
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');
    const [selectedAnnouncement, setSelectedAnnouncement] = useState(null);
    const [currentImageIndex, setCurrentImageIndex] = useState(0);

    const navigate = useNavigate();
    const { isDealer } = useAuth(); // <-- Use the hook to check the role

    // ... (keep all existing functions like useEffect, fetchShipmentAnnouncements, etc.)
    useEffect(() => {
        fetchShipmentAnnouncements();
        fetchFeaturedProducts();
    }, []);

    const fetchShipmentAnnouncements = async () => {
        try {
            const response = await API.get('/shipment-announcements/');
            setShipmentAnnouncements(response.data);
        } catch (err) {
            if (err.response?.status === 401) {
                setError('نشست شما منقضی شده است. در حال انتقال به صفحه ورود...');
                setTimeout(() => handleLogout(), 2000);
            } else {
                setError('خطا در بارگیری اطلاعیه‌های محموله');
            }
        }
    };
    const fetchFeaturedProducts = async () => {
        try {
            const response = await API.get('/products/new-arrivals/');
            setFeaturedProducts(response.data.slice(0, 6));
        } catch (err) { /* Silent fail */ } finally {
            setLoading(false);
        }
    };
    const handleLogout = () => {
        localStorage.removeItem('access_token');
        localStorage.removeItem('refresh_token');
        localStorage.removeItem('userData');
        delete API.defaults.headers.common['Authorization'];
        navigate('/');
    };
    const getDaysAgo = (dateString) => {
        const date = new Date(dateString);
        const now = new Date();
        const diffTime = Math.abs(now - date);
        const diffDays = Math.ceil(diffTime / (1000 * 60 * 60 * 24));
        if (diffDays === 1) return 'دیروز';
        if (diffDays < 7) return `${diffDays} روز پیش`;
        if (diffDays < 30) return `${Math.floor(diffDays / 7)} هفته پیش`;
        return 'بیش از یک ماه پیش';
    };
    const handleOrderInquiry = (productName, announcementTitle) => {
        navigate('/orders/create', { state: { inquiryMode: true, productInquiry: productName, announcementReference: announcementTitle } });
    };
    const openAnnouncementModal = (announcement) => {
        setSelectedAnnouncement(announcement);
        setCurrentImageIndex(0);
    };
    const nextImage = () => { /* ... remains same */ };
    const prevImage = () => { /* ... remains same */ };


    if (loading) {
        // ... (loading state remains the same)
        return <div className="new-arrivals-page"><div className="page-header"><div className="loading-container"><div className="loading-spinner"></div><h1>در حال بارگیری اطلاعیه‌های جدید...</h1></div></div></div>;
    }

    return (
        <div className="new-arrivals-page" dir="rtl">
            {/* Header */}
            <div className="page-header">
                <div className="header-content">
                    <div className="title-section">
                        <h1 className="page-title">🚢 محموله‌های جدید</h1>
                        <p className="page-subtitle"> آخرین اطلاعیه‌ها و محموله‌های وارد شده {shipmentAnnouncements.length > 0 && ` - ${shipmentAnnouncements.length} محموله`}</p>
                    </div>
                    <div className="header-actions">
                        <NeoBrutalistButton text="کاتالوگ" color="blue-400" textColor="white" onClick={() => navigate('/product')} className="catalog-btn"/>


                        {!isDealer && (
                            <NeoBrutalistButton text="ثبت سفارش" color="green-400" textColor="black" onClick={() => navigate('/orders/create')} className="order-btn" />
                        )}
                        {!isDealer && (
                            <NeoBrutalistButton text="داشبورد" color="yellow-400" textColor="black" onClick={() => navigate('/dashboard')} className="dashboard-btn" />
                        )}
                        {isDealer && (<NeoBrutalistButton text="داشبورد" color="yellow-400" textColor="black" onClick={() => navigate('/dealer')} className="dashboard-btn" />
                        )}
                        <NeoBrutalistButton text="خروج" color="red-400" textColor="white" onClick={handleLogout} className="logout-btn"/>
                    </div>
                </div>
            </div>

            {/* ... (error banner remains the same) */}
            {error && <div className="error-banner"> {/* ... */} </div>}

            {/* Latest Shipment Announcements */}
            {shipmentAnnouncements.length > 0 && (
                <div className="shipments-section">
                    <h2 className="section-title">📢 آخرین محموله‌ها</h2>
                    <div className="shipments-grid">
                        {shipmentAnnouncements.map((announcement) => (
                            <NeoBrutalistCard
                                key={announcement.id}
                                className={`shipment-card ${announcement.is_featured ? 'featured' : ''}`}
                                onClick={() => openAnnouncementModal(announcement)}
                            >
                                {/* ... (card header and images remain the same) */}
                                <div className="shipment-header"> {/* ... */} </div>
                                <div className="shipment-images-container"> {/* ... */} </div>
                                <div className="shipment-content"> {/* ... */} </div>

                                <div className="shipment-actions">
                                    <NeoBrutalistButton text="مشاهده جزئیات" color="blue-400" textColor="white" onClick={(e) => { e.stopPropagation(); openAnnouncementModal(announcement); }} className="details-btn"/>

                                    {/* CHANGE: Hide "Price Inquiry" for dealers */}
                                    {!isDealer && (
                                        <NeoBrutalistButton
                                            text="استعلام قیمت"
                                            color="green-400"
                                            textColor="black"
                                            onClick={(e) => {
                                                e.stopPropagation();
                                                handleOrderInquiry('محصولات محموله', announcement.title);
                                            }}
                                            className="inquiry-btn"
                                        />
                                    )}
                                </div>
                            </NeoBrutalistCard>
                        ))}
                    </div>
                </div>
            )}

            {/* ... (Empty state remains the same) */}
            {shipmentAnnouncements.length === 0 && featuredProducts.length === 0 && !loading && <div className="empty-state"> {/* ... */} </div>}

            {/* Quick Actions */}
            <div className="quick-actions-section">
                <NeoBrutalistCard className="quick-actions-card">
                    <h3 className="actions-title">خدمات سریع</h3>
                    <div className="actions-grid">
                        {/* CHANGE: Hide "Create Order" for dealers */}
                        {!isDealer && (
                            <NeoBrutalistButton text="📋 ثبت سفارش جدید" color="green-400" textColor="black" onClick={() => navigate('/orders/create')} className="quick-action-btn"/>
                        )}

                        <NeoBrutalistButton text="📦 کاتالوگ کامل" color="blue-400" textColor="white" onClick={() => navigate('/product')} className="quick-action-btn"/>
                        <NeoBrutalistButton text="📞 تماس برای استعلام" color="yellow-400" textColor="black" onClick={() => { window.open('tel:+989123456789', '_self'); }} className="quick-action-btn"/>

                        {/* CHANGE: Update "My Orders" button for dealers */}
                        <NeoBrutalistButton
                            text={isDealer ? "📊 سفارشات تخصیص یافته" : "📨 سفارشات من"}
                            color="purple-400"
                            textColor="white"
                            onClick={() => navigate(isDealer ? '/dashboard' : '/orders')}
                            className="quick-action-btn"
                        />
                    </div>
                </NeoBrutalistCard>
            </div>

            {/* Enhanced Modal with Image Gallery */}
            <NeoBrutalistModal
                isOpen={!!selectedAnnouncement}
                onClose={() => setSelectedAnnouncement(null)}
                title={selectedAnnouncement ? selectedAnnouncement.title : ""}
                size="large"
            >
                {selectedAnnouncement && (
                    <div className="announcement-detail-modal" dir="rtl">
                        {/* ... (Modal images and info remain the same) */}
                        {selectedAnnouncement.images && selectedAnnouncement.images.length > 0 && <div className="modal-image-gallery"> {/* ... */} </div>}
                        <div className="modal-announcement-info"> {/* ... */} </div>

                        {/* Related Products Full List */}
                        {selectedAnnouncement.related_products_info && selectedAnnouncement.related_products_info.length > 0 && (
                            <div className="modal-related-products">
                                <h4>محصولات این محموله:</h4>
                                <div className="modal-products-list">
                                    {selectedAnnouncement.related_products_info.map((product, idx) => (
                                        <div key={idx} className="modal-product-item">
                                            <div className="product-item-info">
                                                <span className="product-item-name">{product.name}</span>
                                                {product.stock_status && ( <span className={`product-item-status ${product.stock_status}`}> {product.stock_status === 'in_stock' ? 'موجود' : product.stock_status === 'out_of_stock' ? 'ناموجود' : 'نامشخص'} </span> )}
                                            </div>

                                            {/* CHANGE: Hide inquiry button in modal for dealers */}
                                            {!isDealer && (
                                                <NeoBrutalistButton
                                                    text="استعلام"
                                                    color="blue-400"
                                                    textColor="white"
                                                    onClick={() => {
                                                        setSelectedAnnouncement(null);
                                                        handleOrderInquiry(product.name, selectedAnnouncement.title);
                                                    }}
                                                    className="product-inquiry-btn"
                                                />
                                            )}
                                        </div>
                                    ))}
                                </div>
                            </div>
                        )}
                    </div>
                )}
            </NeoBrutalistModal>
        </div>
    );
};

export default NewArrivalsPage;
