import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import NeoBrutalistInput from "../component/NeoBrutalist/NeoBrutalistInput";
import NeoBrutalistButton from "../component/NeoBrutalist/NeoBrutalistButton";
import "../styles/register.css";

const RegisterPage = () => {
    const [email, setEmail] = useState("");
    const [name, setName] = useState("");
    const [password, setPassword] = useState("");
    const [companyName, setCompanyName] = useState("");
    const [phone, setPhone] = useState("");
    const [error, setError] = useState("");
    const [loading, setLoading] = useState(false);
    const navigate = useNavigate();

    const handleRegister = async (e) => {
        e.preventDefault();
        setLoading(true);
        setError("");

        console.log('🚀 Starting registration process...');

        try {
            const registrationData = {
                email,
                name,
                password,
                phone,
                company_name: companyName,
            };

            console.log('📤 Sending registration data:', { ...registrationData, password: '[HIDDEN]' });

            const response = await axios.post(
                `${process.env.REACT_APP_API_URL || 'http://127.0.0.1:8000/api/'}auth/register/`,
                registrationData,
                {
                    headers: {
                        'Content-Type': 'application/json',
                    },
                    withCredentials: true
                }
            );

            console.log('✅ Registration response:', response.data);

            if (response.status === 201) {
                const { user, tokens } = response.data;

                console.log('🔑 Storing authentication data:', {
                    user: user,
                    hasTokens: !!tokens,
                    accessToken: tokens?.access ? `${tokens.access.substring(0, 20)}...` : null,
                    refreshToken: tokens?.refresh ? `${tokens.refresh.substring(0, 20)}...` : null
                });

                // Store user data
                localStorage.setItem('userData', JSON.stringify({
                    id: user.id,
                    email: user.email,
                    name: user.name,
                    is_staff: user.is_staff,
                    company_name: user.company_name
                }));

                // Store tokens
                localStorage.setItem('access_token', tokens.access);
                localStorage.setItem('refresh_token', tokens.refresh);

                // CRITICAL: Set the authorization header for future requests
                axios.defaults.headers.common['Authorization'] = `Bearer ${tokens.access}`;

                console.log('✅ Authentication data stored successfully');
                console.log('🔍 Verification - localStorage check:', {
                    userData: !!localStorage.getItem('userData'),
                    accessToken: !!localStorage.getItem('access_token'),
                    refreshToken: !!localStorage.getItem('refresh_token'),
                    axiosHeader: axios.defaults.headers.common['Authorization']
                });

                // Small delay to ensure storage is complete
                setTimeout(() => {
                    console.log('🔄 Navigating to dashboard...');
                    navigate("/dashboard");
                }, 100);
            }

        } catch (err) {
            console.error("❌ Registration error:", err);

            if (err.response?.data?.error) {
                setError(err.response.data.error);
            } else if (err.response?.data?.details) {
                // Handle validation errors
                const details = err.response.data.details;
                const errorMessages = Object.values(details).flat();
                setError(errorMessages.join(', '));
            } else if (err.response?.status === 400) {
                setError("خطا در اطلاعات وارد شده. لطفاً دوباره بررسی کنید.");
            } else {
                setError("خطا در ثبت نام. لطفاً دوباره تلاش کنید.");
            }

            // Clear any partially stored data on error
            localStorage.removeItem('userData');
            localStorage.removeItem('access_token');
            localStorage.removeItem('refresh_token');
            delete axios.defaults.headers.common['Authorization'];
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="register-container">
            <div className="register-card">
                <h2>سامانه ثبت سفارش</h2>
                {error && <p className="error">{error}</p>}

                <form onSubmit={handleRegister}>
                    <NeoBrutalistInput
                        type="email"
                        placeholder="آدرس ایمیل"
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                        required
                        disabled={loading}
                    />
                    <NeoBrutalistInput
                        type="text"
                        placeholder="نام و نام خانوادگی"
                        value={name}
                        onChange={(e) => setName(e.target.value)}
                        required
                        disabled={loading}
                    />
                    <NeoBrutalistInput
                        type="password"
                        placeholder="پسورد"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                        required
                        minLength={8}
                        disabled={loading}
                    />
                    <NeoBrutalistInput
                        type="tel"
                        placeholder="شماره تلفن همراه"
                        value={phone}
                        minLength={10}
                        maxLength={13}
                        onChange={(e) => {
                            const value = e.target.value;
                            // Allow only digits
                            if (/^\d*$/.test(value)) {
                                setPhone(value);
                            }
                        }}
                        required
                        disabled={loading}
                    />
                    <NeoBrutalistInput
                        type="text"
                        placeholder="نام شرکت (اختیاری)"
                        value={companyName}
                        onChange={(e) => setCompanyName(e.target.value)}
                        disabled={loading}
                    />
                    <NeoBrutalistButton
                        text={loading ? "در حال ثبت نام..." : "ثبت نام"}
                        type="submit"
                        color="yellow-400"
                        textColor="black"
                        className="register-submit-btn"
                        disabled={loading}
                    />
                </form>

                <div className="register-footer">
                    <NeoBrutalistButton
                        text="قبلاً ثبت نام کرده‌اید؟ ورود"
                        color="yellow-400"
                        textColor="white"
                        onClick={() => navigate("/login")}
                        disabled={loading}
                    />
                    <NeoBrutalistButton
                        text="برگشت به صفحه اصلی"
                        color="yellow-400"
                        textColor="black"
                        onClick={() => navigate("/")}
                        disabled={loading}
                    />
                </div>
            </div>
        </div>
    );
};

export default RegisterPage;
