import React, { useState, useEffect, useCallback } from 'react';
import { useNavigate } from 'react-router-dom';
import {
    Package, Search, Filter, Eye, Edit, ShoppingCart, TrendingUp,
    AlertTriangle, CheckCircle, XCircle, Plus, Download, Star,
    BarChart3, DollarSign, Archive, RefreshCw
} from 'lucide-react';
import NeoBrutalistButton from '../../component/NeoBrutalist/NeoBrutalistButton';
import NeoBrutalistCard from '../../component/NeoBrutalist/NeoBrutalistCard';
import NeoBrutalistModal from '../../component/NeoBrutalist/NeoBrutalistModal';
import NeoBrutalistInput from '../../component/NeoBrutalist/NeoBrutalistInput';
import NeoBrutalistDropdown from '../../component/NeoBrutalist/NeoBrutalistDropdown';
import NeoBrutalistToggle from '../../component/NeoBrutalist/NeoBrutalistToggle';
import API from '../../component/api';
import '../../styles/Admin/AdminProducts.css';

const AdminProductsPage = () => {
    const [products, setProducts] = useState([]);
    const [filteredProducts, setFilteredProducts] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');

    // Modal State
    const [isModalOpen, setIsModalOpen] = useState(false);
    const [editingProduct, setEditingProduct] = useState(null);
    const [isImageModalOpen, setIsImageModalOpen] = useState(false);
    const [selectedImage, setSelectedImage] = useState('');

    // Form State
    const [productFormData, setProductFormData] = useState({
        name: '',
        description: '',
        category: null,
        origin: '',
        base_price: 0,
        stock: 0,
        is_active: true,
        is_featured: false,
        weight: 0,
    });
    const [imageFile, setImageFile] = useState(null);
    const [imagePreview, setImagePreview] = useState('');

    // Filtering and Sorting State
    const [searchTerm, setSearchTerm] = useState('');
    const [statusFilter, setStatusFilter] = useState('all');
    const [stockFilter, setStockFilter] = useState('all');
    const [categoryFilter, setCategoryFilter] = useState('all');
    const [sortBy, setSortBy] = useState('newest');

    // Bulk Actions
    const [selectedProducts, setSelectedProducts] = useState([]);
    const [showBulkActions, setShowBulkActions] = useState(false);

    // Stats
    const [productStats, setProductStats] = useState({
        total: 0,
        active: 0,
        inactive: 0,
        lowStock: 0,
        outOfStock: 0,
        featured: 0,
        totalValue: 0
    });

    // Categories for filter
    const [categories, setCategories] = useState([]);

    const navigate = useNavigate();

    const fetchProducts = useCallback(async () => {
        setLoading(true);
        try {
            const response = await API.get('/admin/products/');
            console.log('üì¶ Admin products fetched:', response.data);
            setProducts(response.data);
            calculateStats(response.data);
            //extractCategories(response.data);
            setError('');
        } catch (err) {
            console.error('‚ùå Error fetching products:', err);
            if (err.response?.status === 401) {
                setError('ŸÜÿ¥ÿ≥ÿ™ ÿ¥ŸÖÿß ŸÖŸÜŸÇÿ∂€å ÿ¥ÿØŸá ÿßÿ≥ÿ™. ÿØÿ± ÿ≠ÿßŸÑ ÿßŸÜÿ™ŸÇÿßŸÑ ÿ®Ÿá ÿµŸÅÿ≠Ÿá Ÿàÿ±ŸàÿØ...');
                setTimeout(() => handleLogout(), 2000);
            } else {
                setError('ÿÆÿ∑ÿß ÿØÿ± ÿ®ÿßÿ±⁄Ø€åÿ±€å ŸÑ€åÿ≥ÿ™ ŸÖÿ≠ÿµŸàŸÑÿßÿ™');
            }
        } finally {
            setLoading(false);
        }
    }, []);

    useEffect(() => {
        fetchProducts();
        fetchCategories();
    }, [fetchProducts]);

    const calculateStats = (productsList) => {
        console.log('üìä Calculating stats for products:', productsList);

        const totalValue = productsList.reduce((sum, p) => {
            // Convert to numbers and handle null/undefined values
            const price = parseFloat(p.base_price) || 0;
            const stock = parseInt(p.stock) || 0;
            const productValue = price * stock;

            // Debug individual product values
            if (productValue > 0) {
                console.log(`üí∞ ${p.name}: ${price} √ó ${stock} = ${productValue}`);
            }

            return sum + productValue;
        }, 0);

        console.log('üíµ Total inventory value calculated:', totalValue);

        const stats = {
            total: productsList.length,
            active: productsList.filter(p => p.is_active).length,
            inactive: productsList.filter(p => !p.is_active).length,
            lowStock: productsList.filter(p => p.stock > 0 && p.stock <= 10).length,
            outOfStock: productsList.filter(p => p.stock === 0).length,
            featured: productsList.filter(p => p.is_featured).length,
            totalValue: totalValue
        };

        console.log('üìà Final stats:', stats);
        setProductStats(stats);
    };

// Also update the display logic to handle smaller values better
// In your JSX, replace the totalValue display with:

    const formatTotalValue = (value) => {
        if (value >= 1000000000) {
            return `${(value / 1000000000).toFixed(1)}B`;
        } else if (value >= 1000000) {
            return `${(value / 1000000).toFixed(1)}M`;
        } else if (value >= 1000) {
            return `${(value / 1000).toFixed(1)}K`;
        } else {
            return value.toLocaleString('fa-IR');
        }
    };

    const fetchCategories = async () => {
        try {
            const response = await API.get('/admin/products/categories/');
            console.log('üìÇ Categories fetched:', response.data);
            setCategories(response.data);
        } catch (err) {
            console.error('‚ùå Error fetching categories:', err);
            setCategories([
                { id: 1, name: 'Coffee Related', display_name: 'ŸÖÿ≠ÿµŸàŸÑÿßÿ™ ŸÇŸáŸàŸá' },
                { id: 2, name: 'Seeds', display_name: 'ÿØÿßŸÜŸá‚ÄåŸáÿß' },
                { id: 3, name: 'Spices', display_name: 'ÿßÿØŸà€åŸá‚Äåÿ¨ÿßÿ™' },
                { id: 4, name: 'Nuts', display_name: 'ÿ¢ÿ¨€åŸÑ' },
                { id:5, name:'Confectionery products', display_name: 'ŸÖÿ≠ÿµŸàŸÑÿßÿ™ ŸÇŸÜÿßÿØ€å' }
            ]);
        }
    };

    useEffect(() => {
        let filtered = [...products];

        // Search filter
        if (searchTerm) {
            filtered = filtered.filter(p =>
                p.name?.toLowerCase().includes(searchTerm.toLowerCase()) ||
                p.description?.toLowerCase().includes(searchTerm.toLowerCase()) ||
                p.category?.toLowerCase().includes(searchTerm.toLowerCase())
            );
        }

        // Status filter
        if (statusFilter !== 'all') {
            filtered = filtered.filter(p => p.is_active === (statusFilter === 'active'));
        }

        // Stock filter
        if (stockFilter !== 'all') {
            filtered = filtered.filter(p => {
                if (stockFilter === 'out_of_stock') return p.stock === 0;
                if (stockFilter === 'low_stock') return p.stock > 0 && p.stock <= 10;
                if (stockFilter === 'in_stock') return p.stock > 10;
                return true;
            });
        }

        // Category filter
        if (categoryFilter !== 'all' && categoryFilter !== '') {
            filtered = filtered.filter(p => {
                // Handle both cases: category as ID or as object
                const productCategoryId = typeof p.category === 'object' ? p.category?.id : p.category;
                return productCategoryId === parseInt(categoryFilter);
            });
        }


        // Sorting
        filtered.sort((a, b) => {
            switch (sortBy) {
                case 'newest':
                    return new Date(b.created_at || 0) - new Date(a.created_at || 0);
                case 'oldest':
                    return new Date(a.created_at || 0) - new Date(b.created_at || 0);
                case 'name':
                    return a.name.localeCompare(b.name, 'fa');
                case 'price':
                    return b.base_price - a.base_price;
                case 'stock':
                    return b.stock - a.stock;
                default:
                    return 0;
            }
        });

        setFilteredProducts(filtered);
    }, [products, searchTerm, statusFilter, stockFilter, categoryFilter, sortBy]);

    const handleLogout = () => {
        localStorage.removeItem('access_token');
        localStorage.removeItem('refresh_token');
        localStorage.removeItem('userData');
        delete API.defaults.headers.common['Authorization'];
        navigate('/');
    };

    const handleOpenModal = (product = null) => {
        setEditingProduct(product);
        if (product) {
            setProductFormData({ ...product });
            setImagePreview(product.image_url || '');
        } else {
            setProductFormData({
                name: '',
                description: '',
                category: null,
                origin: '',
                base_price: 0,
                stock: 0,
                is_active: true,
                is_featured: false,
                weight: 0,
            });
            setImagePreview('');
        }
        setImageFile(null);
        setIsModalOpen(true);
    };

    const handleCloseModal = () => {
        setIsModalOpen(false);
        setEditingProduct(null);
        setProductFormData({});
        setImageFile(null);
        setImagePreview('');
    };

    const handleFormChange = (e) => {
        const { name, value, type, checked } = e.target;
        setProductFormData(prev => ({
            ...prev,
            [name]: type === 'checkbox' ? checked :
                name === 'category' ? (value ? parseInt(value, 10) : null) :
                    value
        }));
    };

    const handleFileChange = (e) => {
        const file = e.target.files[0];
        if (file) {
            setImageFile(file);
            setImagePreview(URL.createObjectURL(file));
        }
    };

    const handleFormSubmit = async (e) => {
        e.preventDefault();
        const formData = new FormData();

