import React, { useEffect, useState } from "react";
import API from "../component/api";

const OrderDetailPage = ({ orderId, onOrderUpdated }) => {
    const [order, setOrder] = useState(null);
    const [items, setItems] = useState([]);
    const [editMode, setEditMode] = useState(false);
    const [customerComment, setCustomerComment] = useState("");
    const [approvalDecision, setApprovalDecision] = useState("approve");
    const [rejectionReason, setRejectionReason] = useState("");
    const [loading, setLoading] = useState(true);
    const [invoice, setInvoice] = useState(null);
    const [error, setError] = useState("");

    // Fetch order details
    useEffect(() => {
        setLoading(true);
        API
            .get(`/orders/${orderId}/`)
            .then((res) => {
                setOrder(res.data);
                setCustomerComment(res.data.customer_comment || "");
                setItems(res.data.items || []);
                setInvoice(res.data.invoice || null);
                setLoading(false);
            })
            .catch(() => {
                setError("Failed to load order.");
                setLoading(false);
            });
    }, [orderId]);

    // Edit customer comment
    const handleEditSubmit = (e) => {
        e.preventDefault();
        setLoading(true);
        API
            .patch(`/orders/${orderId}/`, { customer_comment: customerComment })
            .then((res) => {
                setOrder(res.data);
                setEditMode(false);
                setLoading(false);
                if (onOrderUpdated) onOrderUpdated();
            })
            .catch(() => {
                setError("Failed to update comment.");
                setLoading(false);
            });
    };

    // Approve/reject pricing
    const handleApproval = (e) => {
        e.preventDefault();
        setLoading(true);
        API
            .post(`/orders/${orderId}/customer_approval/`, {
                decision: approvalDecision,
                rejection_reason: rejectionReason,
            })
            .then((res) => {
                setOrder(res.data);
                setLoading(false);
                if (onOrderUpdated) onOrderUpdated();
            })
            .catch(() => {
                setError("Failed to submit response.");
                setLoading(false);
            });
    };

    const getUserInfo = () => {
        const userDataString = localStorage.getItem('userData');
        return userDataString ? JSON.parse(userDataString) : null;
    };

    if (loading) return <div>Loading...</div>;
    if (error) return <div style={{ color: "red" }}>{error}</div>;
    if (!order) return <div>Order not found.</div>;

    return (
        <div>
            <h2>Order #{order.id}</h2>
            <div style={{ border: "1px solid #ccc", padding: 16, marginBottom: 24 }}>
                <h3>Your Order</h3>
                <div>
                    <strong>Customer:</strong> {getUserInfo()?.name}
                </div>
                <div>
                    <strong>Created:</strong> {new Date(order.created_at).toLocaleString()}
                </div>
                <div>
                    <strong>Status:</strong> {order.status}
                </div>
                <div>
                    <strong>Comment:</strong>
                    {editMode ? (
                        <form onSubmit={handleEditSubmit}>
                            <textarea
                                value={customerComment}
                                onChange={(e) => setCustomerComment(e.target.value)}
                                rows={3}
                                style={{ width: "100%" }}
                            />
                            <button type="submit" disabled={loading}>Save</button>
                            <button type="button" onClick={() => setEditMode(false)}>Cancel</button>
                        </form>
                    ) : (
                        <>
                            <span style={{ marginLeft: 8 }}>{order.customer_comment}</span>
                            {["pending_pricing"].includes(order.status) && (
                                <button style={{ marginLeft: 16 }} onClick={() => setEditMode(true)}>
                                    Edit
                                </button>
                            )}
                        </>
                    )}
                </div>
                <h4>Items</h4>
                <table border="1" cellPadding={4} style={{ width: "100%", marginBottom: 8 }}>
                    <thead>
                        <tr>
                            <th>Product</th>
                            <th>Requested Qty</th>
                            <th>Customer Notes</th>
                            <th>Admin Price</th>
                            <th>Final Qty</th>
                            <th>Admin Notes</th>
                            <th>Total Price</th>
                        </tr>
                    </thead>
                    <tbody>
                        {items.map((item) => (
                            <tr key={item.id}>
                                <td>{item.product_name || item.product?.name}</td>
                                <td>{item.requested_quantity}</td>
                                <td>{item.customer_notes}</td>
                                <td>
                                    {item.quoted_unit_price > 0
                                        ? `${item.quoted_unit_price} (per unit)`
                                        : "-"}
                                </td>
                                <td>{item.final_quantity || "-"}</td>
                                <td>{item.admin_notes || "-"}</td>
                                <td>
                                    {item.final_quantity && item.quoted_unit_price
                                        ? (item.final_quantity * item.quoted_unit_price).toFixed(2)
                                        : "-"}
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>

            {/* Admin reply and approval */}
            <div style={{ border: "1px solid #ccc", padding: 16, marginBottom: 24 }}>
                <h3>Admin Reply</h3>
                <div>
                    <strong>Admin Comment:</strong> {order.admin_comment || "-"}
                </div>
                <div>
                    <strong>Quoted Total:</strong>{" "}
                    {order.quoted_total > 0 ? order.quoted_total : "-"}
                </div>
                <div>
                    <strong>Pricing Date:</strong>{" "}
                    {order.pricing_date
                        ? new Date(order.pricing_date).toLocaleString()
                        : "-"}
                </div>
                {/* Approval form */}
                {order.status === "waiting_customer_approval" && (
                    <form onSubmit={handleApproval} style={{ marginTop: 16 }}>
                        <label>
                            <input
                                type="radio"
                                name="decision"
                                value="approve"
                                checked={approvalDecision === "approve"}
                                onChange={() => setApprovalDecision("approve")}
                            />
                            Accept Pre-Invoice
                        </label>
                        <label style={{ marginLeft: 16 }}>
                            <input
                                type="radio"
                                name="decision"
                                value="reject"
                                checked={approvalDecision === "reject"}
                                onChange={() => setApprovalDecision("reject")}
                            />
                            Reject
                        </label>
                        {approvalDecision === "reject" && (
                            <div>
                                <textarea
                                    placeholder="Reason for rejection"
                                    value={rejectionReason}
                                    onChange={(e) => setRejectionReason(e.target.value)}
                                    required
                                    style={{ width: "100%", marginTop: 8 }}
                                />
                            </div>
                        )}
                        <button type="submit" disabled={loading} style={{ marginTop: 8 }}>
                            Submit
                        </button>
                    </form>
                )}
                {order.status === "confirmed" && (
                    <div style={{ color: "green", marginTop: 16 }}>
                        Order confirmed. Final invoice will be generated.
                    </div>
                )}
                {order.status === "rejected" && (
                    <div style={{ color: "red", marginTop: 16 }}>
                        Order rejected. Reason: {order.customer_rejection_reason}
                    </div>
                )}
            </div>

            {/* Invoice section */}
            {invoice && (
                <div style={{ border: "1px solid #ccc", padding: 16 }}>
                    <h3>
                        {invoice.invoice_type === "final_invoice"
                            ? "Final Invoice"
                            : "Pre-Invoice"}
                    </h3>
                    <div>
                        <strong>Invoice Number:</strong> {invoice.invoice_number}
                    </div>
                    <div>
                        <strong>Total Amount:</strong> {invoice.total_amount}
                    </div>
                    <div>
                        <strong>Discount:</strong> {invoice.discount}
                    </div>
                    <div>
                        <strong>Tax Rate:</strong> {invoice.tax_rate}%
                    </div>
                    <div>
                        <strong>Tax Amount:</strong> {invoice.tax_amount}
                    </div>
                    <div>
                        <strong>Payable Amount:</strong> {invoice.payable_amount}
                    </div>
                    {invoice.pdf_file && (
                        <div>
                            <a href={invoice.pdf_file} target="_blank" rel="noopener noreferrer">
                                Download PDF
                            </a>
                        </div>
                    )}
                </div>
            )}
        </div>
    );
};

export default OrderDetailPage;
