import React, { useState, useEffect } from "react";
import API from "../component/api";

const currentCustomerId = localStorage.getItem("customer_id");

const CreateOrderPage = ({ onOrderCreated }) => {
    const [products, setProducts] = useState([]);
    const [customerComment, setCustomerComment] = useState("");
    const [items, setItems] = useState([
        { product: "", requested_quantity: 1, customer_notes: "" },
    ]);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState("");

    useEffect(() => {
        console.log("Fetching products from API...");
        API.get("products/")
            .then((res) => {
                console.log("Products loaded:", res.data);
                setProducts(res.data);
            })
            .catch((err) => {
                console.error("Failed to load products:", err);
                setError("Failed to load products.");
            });
    }, []);

    // Update a specific field for an item at index idx
    const handleItemChange = (idx, field, value) => {
        console.log(`Changing item ${idx} field '${field}' to`, value);
        setItems((prev) =>
            prev.map((item, i) =>
                i === idx ? { ...item, [field]: value } : item
            )
        );
    };

    // Add a new empty item row
    const addItem = () => {
        console.log("Adding new item");
        setItems((prev) => [
            ...prev,
            { product: "", requested_quantity: 1, customer_notes: "" },
        ]);
    };

    // Remove item at index idx
    const removeItem = (idx) => {
        console.log("Removing item at index:", idx);
        setItems((prev) => prev.filter((_, i) => i !== idx));
    };

    // Handle form submission to create order
    const handleSubmit = async (e) => {
    e.preventDefault();
    console.log("handleSubmit called");
    setLoading(true);
    setError("");

    if (!Array.isArray(items) || items.length === 0) {
        setError("Order must contain at least one item.");
        setLoading(false);
        return;
    }

    console.log("Current items state before submission:", items);
    console.log("Current customer comment:", customerComment);

    try {
        const payload = {
            customer_comment: customerComment,
            items: items.map((item) => ({
                product: item.product,
                requested_quantity: Number(item.requested_quantity),
                customer_notes: item.customer_notes,
            })),
        };
        console.log("Submitting payload:", JSON.stringify(payload, null, 2));

        const res = await API.post("orders/", payload);

        console.log("Order created successfully, response:", res.data);

        setLoading(false);
        if (onOrderCreated) onOrderCreated(res.data.id);

        setCustomerComment("");
        setItems([{ product: "", requested_quantity: 1, customer_notes: "" }]);
    } catch (err) {
        if (err.response) {
            console.error("API response error:", err.response.data);
            setError(`Failed to create order: ${JSON.stringify(err.response.data)}`);
        } else {
            console.error("Error creating order:", err.message);
            setError("Failed to create order. Please check your input.");
        }
        setLoading(false);
    }
};





    return (
        <div>
            <h2>Create New Order</h2>
            <form onSubmit={handleSubmit}>
                <div>
                    <label>Customer:</label>
                    <span style={{ marginLeft: 8, fontWeight: "bold" }}>
                        {currentCustomerId || "Unknown"}
                    </span>
                </div>
                <div>
                    <label>Customer Comment:</label>
                    <textarea
                        value={customerComment}
                        onChange={(e) => setCustomerComment(e.target.value)}
                        placeholder="Initial request/comments"
                    />
                </div>
                <h3>Order Items</h3>
                {items.map((item, idx) => (
                    <div key={idx} style={{ border: "1px solid #ccc", margin: 8, padding: 8 }}>
                        <select
                            value={item.product}
                            onChange={(e) => handleItemChange(idx, "product", e.target.value)}
                            required
                        >
                            <option value="">Select product</option>
                            {products.map((p) => (
                                <option key={p.id} value={p.id}>
                                    {p.name}
                                </option>
                            ))}
                        </select>
                        <input
                            type="number"
                            min={1}
                            value={item.requested_quantity}
                            onChange={(e) =>
                                handleItemChange(idx, "requested_quantity", e.target.value)
                            }
                            required
                            placeholder="Quantity"
                            style={{ width: 80, marginLeft: 8 }}
                        />
                        <input
                            type="text"
                            value={item.customer_notes}
                            onChange={(e) =>
                                handleItemChange(idx, "customer_notes", e.target.value)
                            }
                            placeholder="Customer notes"
                            style={{ marginLeft: 8 }}
                        />
                        {items.length > 1 && (
                            <button
                                type="button"
                                onClick={() => removeItem(idx)}
                                style={{ marginLeft: 8 }}
                            >
                                Remove
                            </button>
                        )}
                    </div>
                ))}
                <button type="button" onClick={addItem}>
                    Add Item
                </button>
                <div style={{ marginTop: 16 }}>
                    <button type="submit" disabled={loading}>
                        {loading ? "Submitting..." : "Create Order"}
                    </button>
                </div>
                {error && <div style={{ color: "red" }}>{error}</div>}
            </form>
        </div>
    );
};

export default CreateOrderPage;
